<body>
    <!-- Main container -->
    <div id="demo-container">

        <!-- Control Panel Section -->
        <div id="control-panel">

            <!-- Heat Transfer Coefficient Slider -->
            <div>
                <label for="slider-ua">heat transfer coefficient (cal/(dm² K s))</label>
                <input type="range" id="slider-ua" min="0" max="20" step="0.1" value="0">
                <span id="ua-value">0.0</span>
            </div>

            <!-- Reverse Reaction Pre-exponential Factor Slider -->
            <div>
                <label for="slider-kr0">reverse reaction pre-exponential factor (1/s)</label>
                <input type="range" id="slider-kr0" min="0" max="10" step="0.1" value="0">
                <span id="kr0-value">0.0 × 10¹²</span>
            </div>

            <!-- Feed Temperature Slider -->
            <div>
                <label for="slider-tf">feed temperature (K)</label>
                <input type="range" id="slider-tf" min="250" max="350" step="1" value="265">
                <span id="tf-value">265</span>
            </div>

            <!-- Residence Time Slider -->
            <div>
                <label for="slider-tau">residence time (s)</label>
                <input type="range" id="slider-tau" min="100" max="1000" step="10" value="400">
                <span id="tau-value">400</span>
            </div>

        </div>

        <!-- Visualization Section -->
        <div id="plot-container"></div>
    </div>

    <!-- CDN for Plotly.js -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>

    <script>
        // Mathematical constants
        const C_A0 = 10; // mmol/dm³
        const R = 1.987; // cal/mol K
        const E_f = 25000; // cal/mol
        const ΔH_rxn = -200000; // cal/mol
        const E_r = E_f - ΔH_rxn; // 225000 cal/mol
        const k_f0 = 1e15; // 1/s
        const rhoCp = 20000; // cal/(dm³ K)

        // Get slider elements
        const sliderUA = document.getElementById('slider-ua');
        const sliderKr0 = document.getElementById('slider-kr0');
        const sliderTf = document.getElementById('slider-tf');
        const sliderTau = document.getElementById('slider-tau');

        // Get value display elements
        const uaValue = document.getElementById('ua-value');
        const kr0Value = document.getElementById('kr0-value');
        const tfValue = document.getElementById('tf-value');
        const tauValue = document.getElementById('tau-value');

        // Function to update plot
        function updatePlot() {
            // Read slider values
            const UA = parseFloat(sliderUA.value);
            const k_r0_slider = parseFloat(sliderKr0.value);
            const T_f = parseFloat(sliderTf.value);
            const tau = parseFloat(sliderTau.value);

            // Update value displays
            uaValue.textContent = UA.toFixed(1);
            kr0Value.textContent = k_r0_slider.toFixed(1) + ' × 10¹²';
            tfValue.textContent = T_f;
            tauValue.textContent = tau;

            // Calculate curves
            const temperatures = [];
            const massBalance = [];
            const energyBalance = [];

            for (let T = 250; T <= 400; T += 1) {
                temperatures.push(T);

                // Mass balance calculation
                const k_f = k_f0 * Math.exp(-E_f / (R * T));
                const k_r = k_r0_slider * 1e12 * Math.exp(-E_r / (R * T));
                const C_B_mass = (tau * k_f * C_A0) / (1 + tau * (k_f + k_r));
                massBalance.push(C_B_mass);

                // Energy balance calculation
                const C_B_energy = (rhoCp * (T - T_f) + UA * tau * (T - T_f)) / (-ΔH_rxn);
                energyBalance.push(C_B_energy);
            }

            // Create traces
            const trace1 = {
                x: temperatures,
                y: massBalance,
                type: 'scatter',
                mode: 'lines',
                name: 'mass balance',
                line: {
                    color: '#008000',
                    width: 2
                },
                showlegend: false
            };

            const trace2 = {
                x: temperatures,
                y: energyBalance,
                type: 'scatter',
                mode: 'lines',
                name: 'energy balance',
                line: {
                    color: '#0000FF',
                    width: 2
                },
                showlegend: false
            };

            // Find appropriate positions for annotations
            const massBalanceAnnotationIndex = Math.floor(massBalance.length * 0.73);
            const energyBalanceAnnotationIndex = Math.floor(energyBalance.length * 0.2);

            // Layout
            const layout = {
                xaxis: {
                    title: 'temperature (K)',
                    range: [250, 400],
                    gridcolor: '#e0e0e0',
                    gridwidth: 1,
                    zeroline: false
                },
                yaxis: {
                    title: 'product concentration (mmol/dm³)',
                    range: [0, 12],
                    gridcolor: '#e0e0e0',
                    gridwidth: 1,
                    zeroline: false
                },
                annotations: [
                    {
                        x: temperatures[massBalanceAnnotationIndex],
                        y: massBalance[massBalanceAnnotationIndex],
                        text: 'mass balance',
                        showarrow: false,
                        font: {
                            color: '#008000',
                            size: 14
                        },
                        xshift: 10,
                        yshift: 10
                    },
                    {
                        x: temperatures[energyBalanceAnnotationIndex],
                        y: energyBalance[energyBalanceAnnotationIndex],
                        text: 'energy balance',
                        showarrow: false,
                        font: {
                            color: '#0000FF',
                            size: 14
                        },
                        xshift: 10,
                        yshift: -10
                    }
                ],
                margin: {
                    l: 60,
                    r: 20,
                    t: 20,
                    b: 60
                },
                paper_bgcolor: '#ffffff',
                plot_bgcolor: '#ffffff',
                font: {
                    family: 'sans-serif'
                }
            };

            // Plot
            const data = [trace1, trace2];
            Plotly.react('plot-container', data, layout, {displayModeBar: false});
        }

        // Add event listeners
        sliderUA.addEventListener('input', updatePlot);
        sliderKr0.addEventListener('input', updatePlot);
        sliderTf.addEventListener('input', updatePlot);
        sliderTau.addEventListener('input', updatePlot);

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
