</head>
<body>
    <!-- Main container for the demo -->
    <div id="demo-container">

        <!-- Title and Description -->
        <h1>Wankel Rotary Engine: Epitrochoidal Envelopes</h1>
        <p>This Demonstration gives an animation of an epitrochoid and associated planetary-motion envelope curve. The configuration shown has applications in the internal combustion engines invented by Felix Wankel and popularized by Mazda in RX-7 and RX-8 cars. The "eccentricity ratio" changes the shapes of the curves. The "reference frame" determines what is held stationary in the animation: either the epitrochoid (blue), the envelope curve (purple), or the centers of rotation of both curves. The "inner" envelope is the triangular rotor shape used in place of a piston in a Wankel rotary engine, whereas the "outer" envelope is the continuation of the envelope curve along the opposite extreme of motion.</p>

        <!-- Control Panel -->
        <div id="control-panel">
            <div>
                <label for="slider-eccentricity">eccentricity ratio</label>
                <input type="range" id="slider-eccentricity" min="0" max="0.3" step="0.0001" value="0.14">
                <span id="label-eccentricity">0.14</span>
            </div>
            <div>
                <span>reference frame</span>
                <input type="radio" name="ref-frame" id="radio-epitrochoid" value="epitrochoid" checked>
                <label for="radio-epitrochoid">epitrochoid</label>
                <input type="radio" name="ref-frame" id="radio-envelope" value="envelope">
                <label for="radio-envelope">envelope</label>
                <input type="radio" name="ref-frame" id="radio-fixed-centers" value="fixed centers">
                <label for="radio-fixed-centers">fixed centers</label>
            </div>
            <div>
                <span>envelope to show</span>
                <input type="radio" name="envelope-type" id="radio-inner" value="inner" checked>
                <label for="radio-inner">inner</label>
                <input type="radio" name="envelope-type" id="radio-outer" value="outer">
                <label for="radio-outer">outer</label>
                <input type="radio" name="envelope-type" id="radio-both" value="both">
                <label for="radio-both">both</label>
            </div>
            <div>
                <label for="slider-rotation">rotation angle</label>
                <input type="range" id="slider-rotation" min="0" max="6.28318" step="0.0001" value="0">
                <span id="label-rotation">0.00</span>
            </div>
        </div>

        <!-- Visualization Canvas -->
        <div id="canvas-container">
            <!-- p5.js canvas will be inserted here -->
        </div>

    </div>

    <!-- CDN Libraries -->
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>

    <script>
        let k = 0.14; // eccentricity ratio
        let phi = 0; // rotation angle
        let referenceFrame = 'epitrochoid';
        let envelopeType = 'inner';

        // Get DOM elements
        const sliderEccentricity = document.getElementById('slider-eccentricity');
        const labelEccentricity = document.getElementById('label-eccentricity');
        const sliderRotation = document.getElementById('slider-rotation');
        const labelRotation = document.getElementById('label-rotation');

        // Event listeners for sliders
        sliderEccentricity.addEventListener('input', function() {
            k = parseFloat(this.value);
            labelEccentricity.textContent = k.toFixed(4);
        });

        sliderRotation.addEventListener('input', function() {
            phi = parseFloat(this.value);
            labelRotation.textContent = phi.toFixed(2);
        });

        // Event listeners for radio buttons
        document.querySelectorAll('input[name="ref-frame"]').forEach(radio => {
            radio.addEventListener('change', function() {
                referenceFrame = this.value;
            });
        });

        document.querySelectorAll('input[name="envelope-type"]').forEach(radio => {
            radio.addEventListener('change', function() {
                envelopeType = this.value;
            });
        });

        function setup() {
            let canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');
        }

        function draw() {
            background(255);
            translate(width/2, height/2);

            // Calculate scale based on curves
            let scale = 150;
            let maxExtent = 1 + k * 1.5;
            if (maxExtent > 1.2) {
                scale = 250 / maxExtent;
            }

            // Draw coordinate system
            stroke(150);
            strokeWeight(1);
            line(-width/2, 0, width/2, 0);
            line(0, -height/2, 0, height/2);

            // Draw tick marks and labels
            textAlign(CENTER, TOP);
            fill(150);
            noStroke();
            for (let i = -2; i <= 2; i++) {
                if (i !== 0) {
                    stroke(150);
                    line(i * scale, -5, i * scale, 5);
                    noStroke();
                    text(i, i * scale, 10);
                }
            }
            textAlign(RIGHT, CENTER);
            for (let i = -2; i <= 2; i++) {
                if (i !== 0) {
                    stroke(150);
                    line(-5, -i * scale, 5, -i * scale);
                    noStroke();
                    text(i, -10, -i * scale);
                }
            }

            // Calculate motion parameters
            let phi_shaft = 2 * phi;
            let theta = -phi_shaft / 3;
            let cx = k * cos(phi_shaft);
            let cy = k * sin(phi_shaft);

            // Draw based on reference frame
            push();

            if (referenceFrame === 'epitrochoid') {
                // Draw epitrochoid at origin
                drawEpitrochoid(0, 0, scale);
                // Transform and draw envelope
                translate(cx * scale, cy * scale);
                rotate(theta);
                drawEnvelope(0, 0, scale);
            } else if (referenceFrame === 'envelope') {
                // Draw envelope at origin
                drawEnvelope(0, 0, scale);
                // Transform and draw epitrochoid
                rotate(-theta);
                translate(-cx * scale, -cy * scale);
                drawEpitrochoid(0, 0, scale);
            } else if (referenceFrame === 'fixed centers') {
                // Draw epitrochoid at origin
                drawEpitrochoid(0, 0, scale);
                // Draw envelope with transformation
                push();
                translate(cx * scale, cy * scale);
                rotate(theta);
                drawEnvelope(0, 0, scale);
                pop();
            }

            pop();
        }

        function drawEpitrochoid(x, y, scale) {
            stroke(50, 100, 200);
            strokeWeight(2);
            noFill();
            beginShape();
            for (let t = 0; t <= TWO_PI; t += TWO_PI/200) {
                let px = cos(t) - k * cos(3 * t);
                let py = sin(t) - k * sin(3 * t);
                vertex(x + px * scale, y + py * scale);
            }
            endShape(CLOSE);
        }

        function drawEnvelope(x, y, scale) {
            stroke(230, 150, 0);
            strokeWeight(2);
            noFill();

            let R = 1;
            let e = k;

            if (envelopeType === 'inner' || envelopeType === 'both') {
                beginShape();
                for (let t = 0; t <= TWO_PI; t += TWO_PI/200) {
                    let px = (2/3) * R * cos(t) + e * cos(2 * t);
                    let py = (2/3) * R * sin(t) - e * sin(2 * t);
                    vertex(x + px * scale, y + py * scale);
                }
                endShape(CLOSE);
            }

            if (envelopeType === 'outer' || envelopeType === 'both') {
                beginShape();
                for (let t = 0; t <= TWO_PI; t += TWO_PI/200) {
                    let px = (2/3) * R * cos(t) - e * cos(2 * t);
                    let py = (2/3) * R * sin(t) + e * sin(2 * t);
                    vertex(x + px * scale, y + py * scale);
                }
                endShape(CLOSE);
            }
        }
    </script>
</body>
