            color: #555;
        }

        input[type="range"] {
            width: 100%;
            margin-bottom: 5px;
        }

        .value-display {
            font-weight: bold;
            color: #333;
        }

        hr {
            margin: 20px 0;
            border: 0;
            border-top: 1px solid #ddd;
        }

        #info-display p {
            margin: 5px 0;
        }
    </style>
</head>
<body>
    <div id="container">
        <div id="control-panel">
            <h4>stability of multilayered emulsions</h4>

            <div class="slider-group">
                <label for="slider-phi">\(\phi\)</label>
                <input type="range" id="slider-phi" min="0.0001" max="0.05" step="0.0001" value="0.005">
                <span id="phi-value-display" class="value-display">0.005</span>
            </div>

            <hr>

            <div class="slider-group">
                <label for="slider-gamma-sat">\(\Gamma_{sat}\) (mg/m²)</label>
                <input type="range" id="slider-gamma-sat" min="0.1" max="10" step="0.1" value="1.5">
                <span id="gamma-sat-value-display" class="value-display">1.5</span>
            </div>

            <div class="slider-group">
                <label for="slider-r">r (nm)</label>
                <input type="range" id="slider-r" min="50" max="1000" step="10" value="300">
                <span id="r-value-display" class="value-display">300</span>
            </div>

            <div class="slider-group">
                <label for="slider-rpe">r<sub>pe</sub> (nm)</label>
                <input type="range" id="slider-rpe" min="5" max="50" step="1" value="20">
                <span id="rpe-value-display" class="value-display">20</span>
            </div>

            <div class="slider-group">
                <label for="slider-mw">M<sub>W</sub> (kDa)</label>
                <input type="range" id="slider-mw" min="10" max="1000" step="1" value="166">
                <span id="mw-value-display" class="value-display">166</span>
            </div>

            <hr>

            <div class="slider-group">
                <label for="slider-phi-axis">\(\phi\) axis max.</label>
                <input type="range" id="slider-phi-axis" min="0.01" max="0.5" step="0.01" value="0.05">
                <span id="phi-axis-value-display" class="value-display">0.05</span>
            </div>

            <div class="slider-group">
                <label for="slider-c-axis">C axis max.</label>
                <input type="range" id="slider-c-axis" min="1.0" max="5.0" step="0.1" value="2.0">
                <span id="c-axis-value-display" class="value-display">2.0</span>
            </div>
        </div>

        <div id="vis-area">
            <div id="info-display">
                <p id="formula-display"></p>
                <p id="phimax-display"></p>
                <p id="range-display"></p>
            </div>
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        // Constants
        const C_UNIT_CONVERSION = 1000;
        const K_DEPLETION = 0.137;

        // Get all slider elements
        const sliders = {
            phi: document.getElementById('slider-phi'),
            gammaSat: document.getElementById('slider-gamma-sat'),
            r: document.getElementById('slider-r'),
            rpe: document.getElementById('slider-rpe'),
            mw: document.getElementById('slider-mw'),
            phiAxis: document.getElementById('slider-phi-axis'),
            cAxis: document.getElementById('slider-c-axis')
        };

        // Get all display elements
        const displays = {
            phi: document.getElementById('phi-value-display'),
            gammaSat: document.getElementById('gamma-sat-value-display'),
            r: document.getElementById('r-value-display'),
            rpe: document.getElementById('rpe-value-display'),
            mw: document.getElementById('mw-value-display'),
            phiAxis: document.getElementById('phi-axis-value-display'),
            cAxis: document.getElementById('c-axis-value-display'),
            formula: document.getElementById('formula-display'),
            phiMax: document.getElementById('phimax-display'),
            range: document.getElementById('range-display')
        };

        // Function definitions
        function y1(phi, gammaSat, r) {
            return (3 * gammaSat / r) * C_UNIT_CONVERSION * phi;
        }

        function y2(phi, gammaSat, r) {
            return y1(phi, gammaSat, r) / (1 - phi);
        }

        function y3(phi, gammaSat, r, mw, rpe) {
            return ((3 * gammaSat / r) * C_UNIT_CONVERSION) * phi + 
                   (K_DEPLETION * mw / Math.pow(rpe, 3)) / phi;
        }

        function calculatePhiMax() {
            const gammaSat = parseFloat(sliders.gammaSat.value);
            const r = parseFloat(sliders.r.value);
            const mw = parseFloat(sliders.mw.value);
            const rpe = parseFloat(sliders.rpe.value);

            const A = (3 * gammaSat / r) * C_UNIT_CONVERSION;
            const B = K_DEPLETION * mw / Math.pow(rpe, 3);

            return Math.sqrt(B / A);
        }

        function generateCurveData() {
            const gammaSat = parseFloat(sliders.gammaSat.value);
            const r = parseFloat(sliders.r.value);
            const mw = parseFloat(sliders.mw.value);
            const rpe = parseFloat(sliders.rpe.value);
            const phiMax = calculatePhiMax();
            const phiAxisMax = parseFloat(sliders.phiAxis.value);

            const phiValues = [];
            const y1Values = [];
            const y2Values = [];
            const y3Values = [];

            // Generate data points
            const numPoints = 500;
            for (let i = 0; i <= numPoints; i++) {
                const phi = (i / numPoints) * phiAxisMax;
                if (phi > 0) {
                    phiValues.push(phi);
                    y1Values.push(y1(phi, gammaSat, r));
                    y2Values.push(y2(phi, gammaSat, r));
                    y3Values.push(y3(phi, gammaSat, r, mw, rpe));
                }
            }

            // Generate shaded region data (up to phiMax)
            const shadedPhiValues = [];
            const shadedY2Values = [];
            const shadedY3Values = [];

            for (let i = 0; i < phiValues.length; i++) {
                if (phiValues[i] <= phiMax) {
                    shadedPhiValues.push(phiValues[i]);
                    shadedY2Values.push(y2Values[i]);
                    shadedY3Values.push(y3Values[i]);
                }
            }

            return {
                phiValues,
                y1Values,
                y2Values,
                y3Values,
                shadedPhiValues,
                shadedY2Values,
                shadedY3Values,
                phiMax
            };
        }

        function updatePlot() {
            const data = generateCurveData();
            const currentPhi = parseFloat(sliders.phi.value);
            const gammaSat = parseFloat(sliders.gammaSat.value);
            const r = parseFloat(sliders.r.value);
            const mw = parseFloat(sliders.mw.value);
            const rpe = parseFloat(sliders.rpe.value);
            const cAxisMax = parseFloat(sliders.cAxis.value);
            const phiAxisMax = parseFloat(sliders.phiAxis.value);

            // Calculate marker positions
            const y1Val = y1(currentPhi, gammaSat, r);
            const y2Val = y2(currentPhi, gammaSat, r);
            const y3Val = y3(currentPhi, gammaSat, r, mw, rpe);

            const traces = [
                // y1 curve
                {
                    x: data.phiValues,
                    y: data.y1Values,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue' },
                    showlegend: false
                },
                // y2 curve
                {
                    x: data.phiValues,
                    y: data.y2Values,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'red' },
                    showlegend: false
                },
                // y3 curve
                {
                    x: data.phiValues,
                    y: data.y3Values,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'green' },
                    showlegend: false
                },
                // Shaded region lower bound
                {
                    x: data.shadedPhiValues,
                    y: data.shadedY2Values,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'rgba(173, 216, 230, 0)' },
                    showlegend: false,
                    hoverinfo: 'skip'
                },
                // Shaded region upper bound
                {
                    x: data.shadedPhiValues,
                    y: data.shadedY3Values,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'rgba(173, 216, 230, 0)' },
                    fill: 'tonexty',
                    fillcolor: 'rgba(173, 216, 230, 0.5)',
                    showlegend: false,
                    hoverinfo: 'skip'
                },
                // Markers
                {
                    x: [currentPhi, currentPhi, currentPhi],
                    y: [y1Val, y2Val, y3Val],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        size: 10,
                        color: ['blue', 'red', 'green']
                    },
                    showlegend: false
                }
            ];

            const layout = {
                xaxis: {
                    title: 'ϕ (volume fraction)',
                    range: [0, phiAxisMax]
                },
                yaxis: {
                    title: 'C (kg/m³)',
                    range: [0, cAxisMax]
                },
                shapes: [
                    {
                        type: 'line',
                        x0: currentPhi,
                        x1: currentPhi,
                        y0: 0,
                        y1: cAxisMax,
                        line: {
                            color: 'grey',
                            dash: 'dash'
                        }
                    }
                ],
                showlegend: false,
                margin: {
                    l: 60,
                    r: 30,
                    t: 30,
                    b: 60
                }
            };

            Plotly.react('plot-container', traces, layout);
        }

        function updateDisplays() {
            // Update value displays
            displays.phi.textContent = parseFloat(sliders.phi.value).toFixed(4);
            displays.gammaSat.textContent = sliders.gammaSat.value;
            displays.r.textContent = sliders.r.value;
            displays.rpe.textContent = sliders.rpe.value;
            displays.mw.textContent = sliders.mw.value;
            displays.phiAxis.textContent = sliders.phiAxis.value;
            displays.cAxis.textContent = sliders.cAxis.value;

            // Update formula display
            displays.formula.innerHTML = '<span style="color:blue;">y₁(ϕ)</span>, <span style="color:red;">y₂(ϕ)</span>, and <span style="color:green;">y₃(ϕ)</span>';

            // Update phi_max display
            const phiMax = calculatePhiMax();
            displays.phiMax.innerHTML = `\\(\\phi_{max} = ${phiMax.toFixed(4)}\\)`;

            // Update range display
            const currentPhi = parseFloat(sliders.phi.value);
            const gammaSat = parseFloat(sliders.gammaSat.value);
            const r = parseFloat(sliders.r.value);
            const mw = parseFloat(sliders.mw.value);
            const rpe = parseFloat(sliders.rpe.value);

            const lowerBound = y2(currentPhi, gammaSat, r);
            const upperBound = y3(currentPhi, gammaSat, r, mw, rpe);

            displays.range.innerHTML = `Stability range at \\(\\phi = ${currentPhi.toFixed(4)}\\): [${lowerBound.toFixed(2)}, ${upperBound.toFixed(2)}] kg/m³`;

            // Re-render math
            MathJax.typesetPromise([displays.phiMax, displays.range]);
        }

        function updatePhiSliderMax() {
            const phiAxisMax = parseFloat(sliders.phiAxis.value);
            sliders.phi.max = phiAxisMax;

            // Ensure current phi value doesn't exceed new max
            if (parseFloat(sliders.phi.value) > phiAxisMax) {
                sliders.phi.value = phiAxisMax;
            }
        }

        // Event listeners
        Object.values(sliders).forEach(slider => {
            slider.addEventListener('input', function() {
                if (this === sliders.phiAxis) {
                    updatePhiSliderMax();
                }
                updateDisplays();
                updatePlot();
            });
        });

        // Initialize
        updateDisplays();
        updatePlot();

        // Initial MathJax rendering
        MathJax.typesetPromise();
    </script>
</body>
</html>
