<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>2D Elastic Collision Simulator</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.7.0/p5.min.js"></script>
    <style>
        body {
            margin: 0;
            padding: 0;
            font-family: Arial, sans-serif;
            overflow: hidden;
        }

        #main-container {
            display: flex;
            height: 100vh;
        }

        #control-panel {
            width: 300px;
            padding: 20px;
            background-color: #f0f0f0;
            overflow-y: auto;
            box-sizing: border-box;
        }

        #control-panel h4 {
            margin: 20px 0 10px 0;
            color: #333;
        }

        #control-panel label {
            display: block;
            margin-top: 10px;
            margin-bottom: 5px;
            font-size: 14px;
            color: #555;
        }

        #control-panel input[type="range"] {
            width: 100%;
            margin-bottom: 5px;
        }

        #control-panel span {
            display: block;
            font-size: 12px;
            color: #666;
            margin-bottom: 10px;
        }

        #joystick-container {
            width: 200px;
            height: 200px;
            background-color: #ddd;
            border: 2px solid #999;
            border-radius: 10px;
            position: relative;
            margin: 10px auto;
            cursor: pointer;
        }

        #joystick-knob {
            width: 30px;
            height: 30px;
            background-color: #333;
            border-radius: 50%;
            position: absolute;
            transform: translate(-50%, -50%);
            pointer-events: none;
        }

        #canvas-container {
            flex: 1;
            position: relative;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-time">time relative to collision</label>
            <input type="range" id="slider-time" min="-30" max="30" step="0.1" value="-10">
            <span id="label-time">-10.0 s</span>

            <h4>object 1</h4>
            <label for="slider-m1">mass</label>
            <input type="range" id="slider-m1" min="1" max="10" step="0.01" value="5">
            <span id="label-m1">5.00 kg</span>

            <label for="slider-v1">speed</label>
            <input type="range" id="slider-v1" min="1" max="20" step="0.1" value="10">
            <span id="label-v1">10.0 m/s</span>

            <h4>object 2</h4>
            <label for="slider-m2">mass</label>
            <input type="range" id="slider-m2" min="1" max="10" step="0.01" value="3">
            <span id="label-m2">3.00 kg</span>

            <label for="slider-v2">speed</label>
            <input type="range" id="slider-v2" min="1" max="20" step="0.1" value="5">
            <span id="label-v2">5.0 m/s</span>

            <label>observer velocity (m/s)</label>
            <div id="joystick-container">
                <div id="joystick-knob"></div>
            </div>
            <span id="label-v0x">vx: 1.10 m/s</span>
            <span id="label-v0y">vy: 1.10 m/s</span>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script>
        // State variables
        let time = -10;
        let m1 = 5, m2 = 3;
        let s1_cm = 10, s2_cm;
        let v0x = 1.1, v0y = 1.1;

        // Physics variables
        let u1_cm, u2_cm, v1_cm, v2_cm;
        let u1_obs, u2_obs, v1_obs, v2_obs;

        // Joystick drag state
        let isDragging = false;

        // Get DOM elements
        const sliderTime = document.getElementById('slider-time');
        const labelTime = document.getElementById('label-time');
        const sliderM1 = document.getElementById('slider-m1');
        const labelM1 = document.getElementById('label-m1');
        const sliderV1 = document.getElementById('slider-v1');
        const labelV1 = document.getElementById('label-v1');
        const sliderM2 = document.getElementById('slider-m2');
        const labelM2 = document.getElementById('label-m2');
        const sliderV2 = document.getElementById('slider-v2');
        const labelV2 = document.getElementById('label-v2');
        const joystickContainer = document.getElementById('joystick-container');
        const joystickKnob = document.getElementById('joystick-knob');
        const labelV0x = document.getElementById('label-v0x');
        const labelV0y = document.getElementById('label-v0y');

        // Update physics
        function updatePhysics() {
            // CM frame setup
            s2_cm = (m1 / m2) * s1_cm;

            // Initial velocities (t < 0)
            u1_cm = createVector(s1_cm, 0);
            u2_cm = createVector(-s2_cm, 0);

            // Final velocities (t > 0) - 45 degree rotation
            const angle = -PI/4;
            v1_cm = u1_cm.copy().rotate(angle);
            v2_cm = u2_cm.copy().rotate(angle);

            // Observer frame transformation
            const v0 = createVector(v0x, v0y);
            u1_obs = p5.Vector.add(u1_cm, v0);
            u2_obs = p5.Vector.add(u2_cm, v0);
            v1_obs = p5.Vector.add(v1_cm, v0);
            v2_obs = p5.Vector.add(v2_cm, v0);
        }

        // Update display values
        function updateDisplay() {
            labelTime.textContent = time.toFixed(1) + ' s';
            labelM1.textContent = m1.toFixed(2) + ' kg';
            labelV1.textContent = s1_cm.toFixed(1) + ' m/s';
            labelM2.textContent = m2.toFixed(2) + ' kg';
            labelV2.textContent = s2_cm.toFixed(1) + ' m/s';
            labelV0x.textContent = 'vx: ' + v0x.toFixed(2) + ' m/s';
            labelV0y.textContent = 'vy: ' + v0y.toFixed(2) + ' m/s';
        }

        // Update joystick position
        function updateJoystickPosition() {
            const x = ((v0x + 5) / 10) * 200;
            const y = ((5 - v0y) / 10) * 200;
            joystickKnob.style.left = x + 'px';
            joystickKnob.style.top = y + 'px';
        }

        // Event listeners
        sliderTime.addEventListener('input', (e) => {
            time = parseFloat(e.target.value);
            updateDisplay();
        });

        sliderM1.addEventListener('input', (e) => {
            m1 = parseFloat(e.target.value);
            updatePhysics();
            updateDisplay();
        });

        sliderV1.addEventListener('input', (e) => {
            s1_cm = parseFloat(e.target.value);
            updatePhysics();
            updateDisplay();
        });

        sliderM2.addEventListener('input', (e) => {
            m2 = parseFloat(e.target.value);
            updatePhysics();
            updateDisplay();
        });

        sliderV2.addEventListener('input', (e) => {
            // Note: v2 slider is for display only
            updateDisplay();
        });

        // Joystick controls
        joystickContainer.addEventListener('mousedown', (e) => {
            isDragging = true;
            updateJoystick(e);
        });

        document.addEventListener('mousemove', (e) => {
            if (isDragging) {
                updateJoystick(e);
            }
        });

        document.addEventListener('mouseup', () => {
            isDragging = false;
        });

        function updateJoystick(e) {
            const rect = joystickContainer.getBoundingClientRect();
            let x = e.clientX - rect.left;
            let y = e.clientY - rect.top;

            // Constrain to container
            x = Math.max(0, Math.min(200, x));
            y = Math.max(0, Math.min(200, y));

            // Convert to velocity
            v0x = (x / 200) * 10 - 5;
            v0y = 5 - (y / 200) * 10;

            updateJoystickPosition();
            updatePhysics();
            updateDisplay();
        }

        // p5.js sketch
        function setup() {
            const canvas = createCanvas(windowWidth - 300, windowHeight);
            canvas.parent('canvas-container');
            updatePhysics();
            updateDisplay();
            updateJoystickPosition();
        }

        function draw() {
            background(255);
            translate(width/2, height/2);
            scale(1, -1); // Flip y-axis for standard coordinates

            const scale = 10; // pixels per meter

            // Calculate particle positions
            let pos1, pos2;
            const v0 = createVector(v0x, v0y);

            if (time < 0) {
                // Pre-collision
                pos1 = p5.Vector.add(p5.Vector.mult(u1_cm, time), p5.Vector.mult(v0, time));
                pos2 = p5.Vector.add(p5.Vector.mult(u2_cm, time), p5.Vector.mult(v0, time));
            } else {
                // Post-collision
                pos1 = p5.Vector.add(p5.Vector.mult(v1_cm, time), p5.Vector.mult(v0, time));
                pos2 = p5.Vector.add(p5.Vector.mult(v2_cm, time), p5.Vector.mult(v0, time));
            }

            // Draw trajectories
            push();
            drawingContext.setLineDash([5, 5]);
            strokeWeight(2);

            // Particle 1 trajectory
            stroke(255, 0, 0);
            const p1_start = p5.Vector.add(p5.Vector.mult(u1_cm, -50), p5.Vector.mult(v0, -50));
            const p1_collision = createVector(0, 0);
            const p1_end = p5.Vector.add(p5.Vector.mult(v1_cm, 50), p5.Vector.mult(v0, 50));
            line(p1_start.x * scale, p1_start.y * scale, p1_collision.x * scale, p1_collision.y * scale);
            line(p1_collision.x * scale, p1_collision.y * scale, p1_end.x * scale, p1_end.y * scale);

            // Particle 2 trajectory
            stroke(0, 0, 255);
            const p2_start = p5.Vector.add(p5.Vector.mult(u2_cm, -50), p5.Vector.mult(v0, -50));
            const p2_collision = createVector(0, 0);
            const p2_end = p5.Vector.add(p5.Vector.mult(v2_cm, 50), p5.Vector.mult(v0, 50));
            line(p2_start.x * scale, p2_start.y * scale, p2_collision.x * scale, p2_collision.y * scale);
            line(p2_collision.x * scale, p2_collision.y * scale, p2_end.x * scale, p2_end.y * scale);
            pop();

            // Draw particles
            noStroke();
            fill(255, 0, 0);
            const r1 = 5 * sqrt(m1);
            circle(pos1.x * scale, pos1.y * scale, r1 * 2);

            fill(0, 0, 255);
            const r2 = 5 * sqrt(m2);
            circle(pos2.x * scale, pos2.y * scale, r2 * 2);

            // Draw observer velocity vector
            stroke(0);
            strokeWeight(3);
            const arrowStart = createVector(-width/3, height/3);
            const arrowEnd = p5.Vector.add(arrowStart, p5.Vector.mult(v0, -scale * 2));
            line(arrowStart.x, arrowStart.y, arrowEnd.x, arrowEnd.y);

            // Arrow head
            push();
            translate(arrowEnd.x, arrowEnd.y);
            const angle = atan2(arrowStart.y - arrowEnd.y, arrowStart.x - arrowEnd.x);
            rotate(angle);
            fill(0);
            noStroke();
            triangle(0, 0, -10, 5, -10, -5);
            pop();

            // Display metrics
            push();
            scale(1, -1); // Flip back for text
            fill(0);
            noStroke();
            textAlign(LEFT);
            textSize(16);
            const textX = -width/2 + 20;
            const textY = -height/2 + 30;

            const v0_mag = sqrt(v0x * v0x + v0y * v0y);
            const v1_mag = u1_obs.mag();
            const v2_mag = u2_obs.mag();
            const energy = 0.5 * m1 * v1_mag * v1_mag + 0.5 * m2 * v2_mag * v2_mag;

            text('Observer speed: v₀ = ' + v0_mag.toFixed(5) + ' m/s', textX, textY);
            text('Particle 1 speed: v₁ = ' + v1_mag.toFixed(5) + ' m/s', textX, textY + 25);
            text('Particle 2 speed: v₂ = ' + v2_mag.toFixed(5) + ' m/s', textX, textY + 50);
            text('Total kinetic energy: ' + energy.toFixed(2) + ' J', textX, textY + 75);
            pop();
        }

        function windowResized() {
            resizeCanvas(windowWidth - 300, windowHeight);
        }
    </script>
</body>
</html>
