    <div class="main-container">
        <div class="control-panel">
            <div class="control-group">
                <label for="shift-slider">shift</label>
                <input type="range" id="shift-slider" min="0" max="25" step="1" value="12">
                <span id="shift-value">12</span>
            </div>
