            <div class="alphabet-container">
                <h3>Alphabet Mapping</h3>
                <div id="plaintext-alphabet" class="alphabet-row"></div>
                <div id="ciphertext-alphabet" class="alphabet-row"></div>
            </div>

            <div class="key-container">
                <h3>Keys</h3>
                <p id="encryption-key-display">encryption key: 12</p>
                <p id="decryption-key-display">decryption key: 14</p>
            </div>

            <div class="text-output">
                <h3>Text Output</h3>
                <p id="plaintext-output"><span class="plaintext-label">plaintext :</span> abcd</p>
                <p id="ciphertext-output"><span class="ciphertext-label">ciphertext:</span> MNOP</p>
            </div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // Initialize the demo
            initializeAlphabets();
            updateCipher();

            // Set up event listeners
            document.getElementById('shift-slider').addEventListener('input', function() {
                document.getElementById('shift-value').textContent = this.value;
                updateCipher();
            });

            document.getElementById('text-input').addEventListener('input', updateCipher);
        });

        function initializeAlphabets() {
            const plaintextAlphabet = document.getElementById('plaintext-alphabet');
            const ciphertextAlphabet = document.getElementById('ciphertext-alphabet');

            // Clear any existing content
            plaintextAlphabet.innerHTML = '';
            ciphertextAlphabet.innerHTML = '';

            // Create plaintext alphabet (a-z)
            for (let i = 0; i < 26; i++) {
                const letter = String.fromCharCode(97 + i);
                const span = document.createElement('span');
                span.textContent = letter;
                plaintextAlphabet.appendChild(span);
            }

            // Initial ciphertext alphabet will be created in updateCipher
        }

        function updateCipher() {
            const shiftValue = parseInt(document.getElementById('shift-slider').value);
            const plaintext = document.getElementById('text-input').value;

            // Update alphabet mapping
            updateAlphabetMapping(shiftValue);

            // Update key displays
            document.getElementById('encryption-key-display').textContent = `encryption key: ${shiftValue}`;
            const decryptionKey = (26 - shiftValue) % 26;
            document.getElementById('decryption-key-display').textContent = `decryption key: ${decryptionKey}`;

            // Update text output
            document.getElementById('plaintext-output').innerHTML = 
                `<span class="plaintext-label">plaintext :</span> ${plaintext}`;

            const ciphertext = encrypt(plaintext, shiftValue);
            document.getElementById('ciphertext-output').innerHTML = 
                `<span class="ciphertext-label">ciphertext:</span> ${ciphertext}`;
        }

        function updateAlphabetMapping(shiftValue) {
            const ciphertextAlphabet = document.getElementById('ciphertext-alphabet');

            // Clear existing content
            ciphertextAlphabet.innerHTML = '';

            // Create shifted ciphertext alphabet (A-Z)
            for (let i = 0; i < 26; i++) {
                const originalIndex = (i + shiftValue) % 26;
                const letter = String.fromCharCode(65 + originalIndex);
                const span = document.createElement('span');
                span.textContent = letter;
                ciphertextAlphabet.appendChild(span);
            }
        }

        function encrypt(text, shift) {
            let result = '';

            for (let i = 0; i < text.length; i++) {
                const char = text[i];

                if (/[a-z]/.test(char)) {
                    // Lowercase letter
                    const index = char.charCodeAt(0) - 97;
                    const shiftedIndex = (index + shift) % 26;
                    result += String.fromCharCode(65 + shiftedIndex);
                } else if (/[A-Z]/.test(char)) {
                    // Uppercase letter (treat same as lowercase but keep uppercase)
                    const index = char.charCodeAt(0) - 65;
                    const shiftedIndex = (index + shift) % 26;
                    result += String.fromCharCode(65 + shiftedIndex);
                } else {
                    // Non-alphabetic character
                    result += char;
                }
            }

            return result;
        }
    </script>
</body>
</html>
