        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="button-group">
                <label>coordination</label>
                <button id="btn-threefold" class="active">threefold</button>
                <button id="btn-fourfold">fourfold</button>
                <button id="btn-sixfold">sixfold</button>
                <button id="btn-eightfold">eightfold</button>
            </div>
            <div class="slider-group">
                <label for="slider-anion-radius">relative anion radius</label>
                <input type="range" id="slider-anion-radius" min="0.1" max="1.0" step="0.001" value="0.1">
                <span id="value-anion-radius">0.100</span>
            </div>
            <div class="slider-group">
                <label for="slider-cation-radius">relative cation radius</label>
                <input type="range" id="slider-cation-radius" min="0.1" max="1.0" step="0.001" value="0.1">
                <span id="value-cation-radius">0.100</span>
            </div>
            <div class="slider-group">
                <label for="slider-opacity">opacity of anions</label>
                <input type="range" id="slider-opacity" min="0.0" max="1.0" step="0.01" value="0.8">
            </div>
        </div>
        <div id="display-container">
            <div id="canvas-container"></div>
            <p id="ratio-text">The ratio of the cation radius to the anion radius is 1.00.</p>
        </div>
    </div>

    <script>
        // Create matcap texture for cation
        function createMatcapTexture() {
            const canvas = document.createElement('canvas');
            canvas.width = 128;
            canvas.height = 128;
            const ctx = canvas.getContext('2d');

            const gradient = ctx.createRadialGradient(64, 64, 0, 64, 64, 64);
            gradient.addColorStop(0, '#1a73e8');
            gradient.addColorStop(0.5, '#34a853');
            gradient.addColorStop(1, '#ea4335');

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, 128, 128);

            return new THREE.CanvasTexture(canvas);
        }

        // Initialize Three.js scene
        let scene, camera, renderer, controls;
        let cation, anions = [], boundingBox = null;
        let currentCoordination = 'threefold';
        const matcapTexture = createMatcapTexture();

        function initScene() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera setup
            camera = new THREE.PerspectiveCamera(75, document.getElementById('canvas-container').clientWidth / document.getElementById('canvas-container').clientHeight, 0.1, 1000);
            camera.position.z = 5;

            // Renderer setup
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(document.getElementById('canvas-container').clientWidth, document.getElementById('canvas-container').clientHeight);
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            // Controls setup
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(1, 1, 1);
            scene.add(directionalLight);

            // Create initial objects
            createCation();
            createAnions(currentCoordination);

            // Handle window resize
            window.addEventListener('resize', onWindowResize);

            // Start animation loop
            animate();
        }

        function onWindowResize() {
            camera.aspect = document.getElementById('canvas-container').clientWidth / document.getElementById('canvas-container').clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(document.getElementById('canvas-container').clientWidth, document.getElementById('canvas-container').clientHeight);
        }

        function createCation() {
            if (cation) scene.remove(cation);

            const radius = parseFloat(document.getElementById('slider-cation-radius').value);
            const geometry = new THREE.SphereGeometry(radius, 32, 32);
            const material = new THREE.MeshPhongMaterial({ 
                color: 0xffffff,
                matcap: matcapTexture,
                shininess: 100
            });

            cation = new THREE.Mesh(geometry, material);
            scene.add(cation);
        }

        function createAnions(coordinationType) {
            // Remove existing anions and bounding box
            anions.forEach(anion => scene.remove(anion));
            anions = [];

            if (boundingBox) {
                scene.remove(boundingBox);
                boundingBox = null;
            }

            const radius = parseFloat(document.getElementById('slider-anion-radius').value);
            const opacity = parseFloat(document.getElementById('slider-opacity').value);
            const geometry = new THREE.SphereGeometry(radius, 32, 32);
            const material = new THREE.MeshPhongMaterial({ 
                color: 0xe0d8e0,
                transparent: true,
                opacity: opacity
            });

            let positions = [];
            const distance = 1.5;

            switch (coordinationType) {
                case 'threefold':
                    // Equilateral triangle in XY plane
                    positions = [
                        new THREE.Vector3(distance, 0, 0),
                        new THREE.Vector3(-distance/2, distance * Math.sqrt(3)/2, 0),
                        new THREE.Vector3(-distance/2, -distance * Math.sqrt(3)/2, 0)
                    ];
                    break;

                case 'fourfold':
                    // Tetrahedron
                    positions = [
                        new THREE.Vector3(1, 1, 1).normalize().multiplyScalar(distance),
                        new THREE.Vector3(-1, -1, 1).normalize().multiplyScalar(distance),
                        new THREE.Vector3(-1, 1, -1).normalize().multiplyScalar(distance),
                        new THREE.Vector3(1, -1, -1).normalize().multiplyScalar(distance)
                    ];

                    // Add bounding box
                    const boxSize = distance * 1.2;
                    const boxGeometry = new THREE.BoxGeometry(boxSize, boxSize, boxSize);
                    const edges = new THREE.EdgesGeometry(boxGeometry);
                    const lineMaterial = new THREE.LineBasicMaterial({ color: 0x888888 });
                    boundingBox = new THREE.LineSegments(edges, lineMaterial);
                    scene.add(boundingBox);
                    break;

                case 'sixfold':
                    // Octahedron (along axes)
                    positions = [
                        new THREE.Vector3(distance, 0, 0),
                        new THREE.Vector3(-distance, 0, 0),
                        new THREE.Vector3(0, distance, 0),
                        new THREE.Vector3(0, -distance, 0),
                        new THREE.Vector3(0, 0, distance),
                        new THREE.Vector3(0, 0, -distance)
                    ];

                    // Add bounding box
                    const boxSize6 = distance * 1.2;
                    const boxGeometry6 = new THREE.BoxGeometry(boxSize6, boxSize6, boxSize6);
                    const edges6 = new THREE.EdgesGeometry(boxGeometry6);
                    const lineMaterial6 = new THREE.LineBasicMaterial({ color: 0x888888 });
                    boundingBox = new THREE.LineSegments(edges6, lineMaterial6);
                    scene.add(boundingBox);
                    break;

                case 'eightfold':
                    // Cube vertices
                    const cubeDist = distance / Math.sqrt(3);
                    positions = [
                        new THREE.Vector3(cubeDist, cubeDist, cubeDist),
                        new THREE.Vector3(-cubeDist, cubeDist, cubeDist),
                        new THREE.Vector3(-cubeDist, -cubeDist, cubeDist),
                        new THREE.Vector3(cubeDist, -cubeDist, cubeDist),
                        new THREE.Vector3(cubeDist, cubeDist, -cubeDist),
                        new THREE.Vector3(-cubeDist, cubeDist, -cubeDist),
                        new THREE.Vector3(-cubeDist, -cubeDist, -cubeDist),
                        new THREE.Vector3(cubeDist, -cubeDist, -cubeDist)
                    ];

                    // Add bounding box
                    const boxSize8 = cubeDist * 2 * 1.2;
                    const boxGeometry8 = new THREE.BoxGeometry(boxSize8, boxSize8, boxSize8);
                    const edges8 = new THREE.EdgesGeometry(boxGeometry8);
                    const lineMaterial8 = new THREE.LineBasicMaterial({ color: 0x888888 });
                    boundingBox = new THREE.LineSegments(edges8, lineMaterial8);
                    scene.add(boundingBox);
                    break;
            }

            // Create anion spheres
            positions.forEach(pos => {
                const anion = new THREE.Mesh(geometry, material.clone());
                anion.position.copy(pos);
                scene.add(anion);
                anions.push(anion);
            });

            // Update ratio text
            updateRatioText();
        }

        function updateRatioText() {
            const cationRadius = parseFloat(document.getElementById('slider-cation-radius').value);
            const anionRadius = parseFloat(document.getElementById('slider-anion-radius').value);
            const ratio = cationRadius / anionRadius;
            document.getElementById('ratio-text').textContent = 
                `The ratio of the cation radius to the anion radius is ${ratio.toFixed(2)}.`;
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize UI event listeners
        function initUI() {
            // Coordination buttons
            const coordinationButtons = [
                document.getElementById('btn-threefold'),
                document.getElementById('btn-fourfold'),
                document.getElementById('btn-sixfold'),
                document.getElementById('btn-eightfold')
            ];

            coordinationButtons.forEach(button => {
                button.addEventListener('click', function() {
                    coordinationButtons.forEach(btn => btn.classList.remove('active'));
                    this.classList.add('active');
                    currentCoordination = this.id.replace('btn-', '');
                    createAnions(currentCoordination);

                    // Reset camera position
                    camera.position.set(0, 0, 5);
                    controls.update();
                });
            });

            // Anion radius slider
            const anionSlider = document.getElementById('slider-anion-radius');
            const anionValue = document.getElementById('value-anion-radius');
            anionSlider.addEventListener('input', function() {
                const value = parseFloat(this.value);
                anionValue.textContent = value.toFixed(3);

                anions.forEach(anion => {
                    anion.geometry.dispose();
                    anion.geometry = new THREE.SphereGeometry(value, 32, 32);
                });

                updateRatioText();
            });

            // Cation radius slider
            const cationSlider = document.getElementById('slider-cation-radius');
            const cationValue = document.getElementById('value-cation-radius');
            cationSlider.addEventListener('input', function() {
                const value = parseFloat(this.value);
                cationValue.textContent = value.toFixed(3);

                cation.geometry.dispose();
                cation.geometry = new THREE.SphereGeometry(value, 32, 32);

                updateRatioText();
            });

            // Opacity slider
            const opacitySlider = document.getElementById('slider-opacity');
            opacitySlider.addEventListener('input', function() {
                const value = parseFloat(this.value);
                anions.forEach(anion => {
                    anion.material.opacity = value;
                });
            });
        }

        // Initialize everything when the page loads
        window.onload = function() {
            initScene();
            initUI();
        };
    </script>
</body>
</html>
