</head>
<body>
    <div id="control-panel">
        <div id="slider-container">
            <label for="slider-angle">slope θ</label>
            <input type="range" id="slider-angle" min="10" max="80" value="30" step="1">
        </div>

        <div id="animation-controls">
            <p>release block</p>
            <button id="btn-play">▶</button>
            <button id="btn-pause">||</button>
            <button id="btn-reset">|<</button>
        </div>
    </div>

    <div id="visualization-area">
        <p id="info-display">slope = \( \theta \) = 30°; acceleration = \( g \sin(\theta) \) = 4.905 m/s²</p>
        <canvas id="p5-canvas"></canvas>
    </div>

    <script>
        // Physics constants
        const g = 9.81; // m/s²
        let θ = 30; // degrees
        let angleRad = θ * Math.PI / 180;

        // Animation state
        let isPlaying = false;
        let startTime = 0;
        let currentTime = 0;
        let blockPosition = 0;
        let animationId = null;

        // Canvas dimensions
        let canvasWidth, canvasHeight;
        let originX, originY;
        let rampLength = 400;
        let blockSize = 40;

        // DOM elements
        const sliderAngle = document.getElementById('slider-angle');
        const btnPlay = document.getElementById('btn-play');
        const btnPause = document.getElementById('btn-pause');
        const btnReset = document.getElementById('btn-reset');
        const infoDisplay = document.getElementById('info-display');

        // p5.js sketch
        let sketch = function(p) {
            p.setup = function() {
                const canvas = p.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('p5-canvas');
                p.angleMode(p.DEGREES);

                // Set origin point (bottom left of ramp)
                originX = p.width * 0.2;
                originY = p.height * 0.7;

                drawScene();
            };

            p.draw = function() {
                if (isPlaying) {
                    currentTime = p.millis() - startTime;
                    blockPosition = 0.5 * (g * Math.sin(angleRad)) * Math.pow(currentTime / 1000, 2);

                    // Check if block reached bottom
                    if (blockPosition * 100 > rampLength - blockSize/2) {
                        isPlaying = false;
                        blockPosition = (rampLength - blockSize/2) / 100;
                    }
                }

                drawScene();
            };

            function drawScene() {
                p.background(255);
                p.translate(originX, originY);

                // Draw inclined plane (ramp)
                p.fill(173, 216, 230); // lightblue
                p.stroke(255, 255, 0); // yellow
                p.strokeWeight(2);
                p.triangle(
                    0, 0,
                    rampLength * Math.cos(angleRad), -rampLength * Math.sin(angleRad),
                    0, -rampLength * Math.sin(angleRad)
                );

                // Draw angle marker (θ)
                p.noFill();
                p.stroke(0);
                p.strokeWeight(1);
                let arcRadius = 30;
                p.arc(0, 0, arcRadius, arcRadius, -θ, 0);
                p.textSize(16);
                p.text("θ", arcRadius/2 * Math.cos(-θ/2 * Math.PI/180), -arcRadius/2 * Math.sin(-θ/2 * Math.PI/180));

                // Calculate block position
                let blockX = blockPosition * 100 * Math.cos(angleRad);
                let blockY = -blockPosition * 100 * Math.sin(angleRad);

                // Draw block
                p.fill(169, 169, 169); // darkgrey
                p.stroke(0);
                p.strokeWeight(1);
                p.push();
                p.translate(blockX, blockY);
                p.rotate(-θ);
                p.rect(-blockSize/2, -blockSize/2, blockSize, blockSize);
                p.pop();

                // Draw coordinate axes
                p.stroke(0, 0, 255); // blue
                p.strokeWeight(1);
                drawArrow(p, 0, 0, 100 * Math.cos(angleRad), -100 * Math.sin(angleRad), 'blue', 'x');
                drawArrow(p, 0, 0, 100 * Math.sin(angleRad), 100 * Math.cos(angleRad), 'blue', 'y');

                // Calculate forces
                let fGrav = 100; // arbitrary length for visualization
                let fNormal = fGrav * Math.cos(angleRad);
                let fNet = fGrav * Math.sin(angleRad);

                // Draw gravity force (F_grav = mg)
                drawArrow(p, blockX, blockY, blockX, blockY + fGrav, 'red', 'F_{grav}=mg');

                // Draw normal force
                let normalAngle = angleRad + Math.PI/2;
                drawArrow(
                    p, blockX, blockY,
                    blockX + fNormal * Math.sin(angleRad),
                    blockY + fNormal * Math.cos(angleRad),
                    'deepskyblue', 'normal force'
                );

                // Draw net force (F_net = mg sinθ)
                drawArrow(
                    p, blockX, blockY,
                    blockX + fNet * Math.cos(angleRad),
                    blockY - fNet * Math.sin(angleRad),
                    'purple', 'F_{net}=mg\\sin\\theta'
                );

                // Draw force decomposition (components of gravity)
                p.stroke(0);
                p.strokeWeight(1);
                p.drawingContext.setLineDash([5, 3]);

                // Parallel component (mg sinθ)
                p.line(
                    blockX, blockY + fGrav,
                    blockX + fNet * Math.cos(angleRad), blockY + fGrav - fNet * Math.sin(angleRad)
                );

                // Perpendicular component (mg cosθ)
                p.line(
                    blockX + fNet * Math.cos(angleRad), blockY + fGrav - fNet * Math.sin(angleRad),
                    blockX + fNet * Math.cos(angleRad) + fNormal * Math.sin(angleRad),
                    blockY + fGrav - fNet * Math.sin(angleRad) + fNormal * Math.cos(angleRad)
                );

                // Label for perpendicular component
                p.drawingContext.setLineDash([]);
                p.textSize(12);
                p.text(
                    'mg\\cos\\theta',
                    blockX + fNet * Math.cos(angleRad) + fNormal/2 * Math.sin(angleRad),
                    blockY + fGrav - fNet * Math.sin(angleRad) + fNormal/2 * Math.cos(angleRad)
                );

                // Angle marker for force decomposition
                p.noFill();
                p.stroke(0);
                p.strokeWeight(1);
                let forceArcRadius = 20;
                p.arc(
                    blockX, blockY + fGrav,
                    forceArcRadius, forceArcRadius,
                    Math.PI/2 - angleRad, Math.PI/2
                );
                p.text(
                    'θ',
                    blockX + forceArcRadius/2 * Math.cos(Math.PI/2 - angleRad/2),
                    blockY + fGrav - forceArcRadius/2 * Math.sin(Math.PI/2 - angleRad/2)
                );
            }

            function drawArrow(p, x1, y1, x2, y2, color, label) {
                p.push();
                p.stroke(color);
                p.strokeWeight(2);
                p.line(x1, y1, x2, y2);

                // Arrowhead
                let angle = Math.atan2(y2 - y1, x2 - x1);
                let headSize = 10;
                p.translate(x2, y2);
                p.rotate(angle);
                p.line(0, 0, -headSize, -headSize/2);
                p.line(0, 0, -headSize, headSize/2);

                // Label
                if (label) {
                    p.noStroke();
                    p.fill(0);
                    p.textSize(14);
                    p.text(label, -headSize - p.textWidth(label)/2, -headSize);
                }
                p.pop();
            }
        };

        // Initialize p5 sketch when window loads
        window.onload = function() {
            // Get canvas dimensions from container
            const canvasContainer = document.getElementById('p5-canvas');
            canvasWidth = canvasContainer.clientWidth;
            canvasHeight = canvasContainer.clientHeight;

            // Create p5 instance
            new p5(sketch);

            // Update info display with initial values
            updateInfoDisplay();
        };

        // Event listeners
        sliderAngle.addEventListener('input', function() {
            θ = parseInt(this.value);
            angleRad = θ * Math.PI / 180;
            isPlaying = false;
            blockPosition = 0;
            startTime = 0;
            currentTime = 0;
            updateInfoDisplay();
        });

        btnPlay.addEventListener('click', function() {
            if (!isPlaying) {
                isPlaying = true;
                startTime = Date.now() - currentTime;
            }
        });

        btnPause.addEventListener('click', function() {
            isPlaying = false;
        });

        btnReset.addEventListener('click', function() {
            isPlaying = false;
            blockPosition = 0;
            startTime = 0;
            currentTime = 0;
        });

        function updateInfoDisplay() {
            const acceleration = g * Math.sin(angleRad);
            infoDisplay.innerHTML = `slope = \\( \\theta \\) = ${θ}°; acceleration = \\( g \\sin(\\theta) \\) = ${acceleration.toFixed(3)} m/s²`;

            // Trigger MathJax to re-render
            if (typeof MathJax !== 'undefined') {
                MathJax.typesetPromise();
            }
        }

        // Handle window resize
        window.addEventListener('resize', function() {
            const canvasContainer = document.getElementById('p5-canvas');
            canvasWidth = canvasContainer.clientWidth;
            canvasHeight = canvasContainer.clientHeight;

            // Need to recreate p5 instance to handle resize properly
            document.getElementById('p5-canvas').innerHTML = '';
            new p5(sketch);
        });
    </script>
</body>
</html>
