                const charCode = str.charCodeAt(i);
                binary += charCode.toString(2).padStart(8, '0');
            }
            return binary;
        }

        function binaryToHex(binary) {
            let hex = '';
            for (let i = 0; i < binary.length; i += 4) {
                const nibble = binary.substr(i, 4);
                hex += parseInt(nibble, 2).toString(16).toUpperCase();
            }
            return hex;
        }

        function permute(input, table) {
            let output = '';
            for (let i = 0; i < table.length; i++) {
                output += input[table[i] - 1];
            }
            return output;
        }

        function leftShift(input, shift) {
            return input.substr(shift) + input.substr(0, shift);
        }

        function xor(a, b) {
            let result = '';
            for (let i = 0; i < a.length; i++) {
                result += (a[i] === b[i]) ? '0' : '1';
            }
            return result;
        }

        function sBoxSubstitution(input) {
            let output = '';
            for (let i = 0; i < 8; i++) {
                const block = input.substr(i * 6, 6);
                const row = parseInt(block[0] + block[5], 2);
                const col = parseInt(block.substr(1, 4), 2);
                const val = S[i][row][col];
                output += val.toString(2).padStart(4, '0');
            }
            return output;
        }

        function generateSubKeys(key) {
            // Permuted Choice 1 (PC-1)
            let permutedKey = permute(key, PC1);

            // Split into left and right halves
            let left = permutedKey.substr(0, 28);
            let right = permutedKey.substr(28);

            const subKeys = [];

            for (let i = 0; i < 16; i++) {
                // Left shift both halves
                left = leftShift(left, SHIFTS[i]);
                right = leftShift(right, SHIFTS[i]);

                // Combine and apply PC-2 to get subkey
                const combined = left + right;
                const subKey = permute(combined, PC2);
                subKeys.push(subKey);
            }

            return subKeys;
        }

        function desEncryptBlock(block, subKeys, rounds) {
            // Initial Permutation (IP)
            let permutedBlock = permute(block, IP);

            // Split into left and right halves
            let left = permutedBlock.substr(0, 32);
            let right = permutedBlock.substr(32);

            // Feistel network
            for (let i = 0; i < rounds; i++) {
                const previousLeft = left;
                left = right;

                // Expansion (E)
                const expandedRight = permute(right, E);

                // XOR with subkey
                const xored = xor(expandedRight, subKeys[i]);

                // S-box substitution
                const substituted = sBoxSubstitution(xored);

                // Permutation (P)
                const permuted = permute(substituted, P);

                // XOR with left half
                right = xor(previousLeft, permuted);
            }

            // Final swap and permutation
            const combined = right + left;
            const ciphertext = permute(combined, FP);

            return ciphertext;
        }

        function desEncrypt(message, key, steps) {
            // Convert inputs to binary
            let messageBinary = stringToBinary(message);
            let keyBinary = stringToBinary(key);

            // Pad message to multiple of 64 bits
            const paddingLength = (64 - (messageBinary.length % 64)) % 64;
            messageBinary += '0'.repeat(paddingLength);

            // Pad key to exactly 64 bits (DES uses 56 bits, but we'll take first 56)
            keyBinary = keyBinary.padEnd(64, '0').substr(0, 64);

            // Generate subkeys
            const subKeys = generateSubKeys(keyBinary);

            // Split message into 64-bit blocks
            const blocks = [];
            for (let i = 0; i < messageBinary.length; i += 64) {
                blocks.push(messageBinary.substr(i, 64));
            }

            // Process each step (even numbers up to selected steps)
            const results = [];
            for (let s = 2; s <= steps; s += 2) {
                let ciphertext = '';

                // Encrypt each block with s rounds
                for (const block of blocks) {
                    const encryptedBlock = desEncryptBlock(block, subKeys, s);
                    ciphertext += binaryToHex(encryptedBlock);
                }

                results.push({
                    step: s,
                    ciphertext: ciphertext
                });
            }

            return results;
        }

        function updateDisplay() {
            // Get input values
            const message = document.getElementById('input-message').value;
            const key = document.getElementById('input-key').value;
            const steps = parseInt(document.querySelector('input[name="steps"]:checked').value);

            // Perform encryption
            const results = desEncrypt(message, key, steps);

            // Format output
            let output = '';
            for (const result of results) {
                output += `${result.step.toString().padStart(2)}      ${result.ciphertext}\n`;
            }

            // Update display
            document.getElementById('output-results').textContent = output;
        }

        // Initialize event listeners
        document.getElementById('input-message').addEventListener('input', updateDisplay);
        document.getElementById('input-key').addEventListener('input', updateDisplay);
        document.querySelectorAll('input[name="steps"]').forEach(radio => {
            radio.addEventListener('change', updateDisplay);
        });

        // Initial display
        window.addEventListener('load', updateDisplay);
    </script>
</body>
