            <button id="btn-bogosort" class="sort-btn">bogosort</button>
            <button id="btn-randomize">randomize</button>
        </div>
    </div>

    <div id="visualization-area">
        <canvas id="bar-chart-canvas"></canvas>
    </div>

    <script>
        // Application state
        const state = {
            currentAlgorithm: 'shellsort',
            numElements: 10,
            sortHistory: [],
            currentStep: 1,
            isPlaying: false,
            animationInterval: null,
            maxYValue: 20
        };

        // p5.js sketch
        let sketch = function(p) {
            let canvas;
            let margin = 50;

            p.setup = function() {
                canvas = p.createCanvas(800, 400);
                canvas.parent('bar-chart-canvas');
                p.colorMode(p.HSB, 360, 100, 100);
                p.noLoop();

                // Initialize with random data
                randomizeData();
            };

            p.draw = function() {
                p.background(255);
                drawYAxis();
                drawBars();
            };

            function drawYAxis() {
                p.stroke(0);
                p.strokeWeight(1);
                p.line(margin, margin, margin, p.height - margin);

                // Draw ticks and labels
                const tickCount = 5;
                for (let i = 0; i <= tickCount; i++) {
                    const y = p.map(i, 0, tickCount, p.height - margin, margin);
                    const value = p.map(i, 0, tickCount, 0, state.maxYValue);

                    // Tick
                    p.line(margin - 5, y, margin, y);

                    // Label
                    p.noStroke();
                    p.textAlign(p.RIGHT, p.CENTER);
                    p.text(p.floor(value), margin - 10, y);
                }
            }

            function drawBars() {
                if (state.sortHistory.length === 0) return;

                const currentState = state.sortHistory[state.currentStep - 1];
                const barWidth = (p.width - margin * 2) / currentState.length;

                for (let i = 0; i < currentState.length; i++) {
                    const value = currentState[i];
                    const x = margin + i * barWidth;
                    const barHeight = p.map(value, 0, state.maxYValue, 0, p.height - margin * 2);
                    const y = p.height - margin - barHeight;

                    // Calculate color based on value (rainbow effect)
                    const hue = p.map(value, 1, currentState.length, 240, 0);
                    p.fill(hue, 80, 90);
                    p.stroke(0);
                    p.strokeWeight(1);
                    p.rect(x, y, barWidth, barHeight);
                }
            }
        };

        new p5(sketch);

        // DOM elements
        const sliderElements = document.getElementById('slider-elements');
        const elementsValue = document.getElementById('elements-value');
        const sliderStep = document.getElementById('slider-step');
        const stepValue = document.getElementById('step-value');
        const btnPlayPause = document.getElementById('btn-play-pause');
        const btnStepBackward = document.getElementById('btn-step-backward');
        const btnStepForward = document.getElementById('btn-step-forward');
        const btnResetStep = document.getElementById('btn-reset-step');
        const btnShellsort = document.getElementById('btn-shellsort');
        const btnBubblesort = document.getElementById('btn-bubblesort');
        const btnInsertionsort = document.getElementById('btn-insertionsort');
        const btnCocktail = document.getElementById('btn-cocktail');
        const btnBogosort = document.getElementById('btn-bogosort');
        const btnRandomize = document.getElementById('btn-randomize');

        // Event listeners
        sliderElements.addEventListener('input', updateElementsValue);
        sliderStep.addEventListener('input', updateStepValue);
        btnPlayPause.addEventListener('click', togglePlayPause);
        btnStepBackward.addEventListener('click', stepBackward);
        btnStepForward.addEventListener('click', stepForward);
        btnResetStep.addEventListener('click', resetStep);
        btnShellsort.addEventListener('click', () => setAlgorithm('shellsort'));
        btnBubblesort.addEventListener('click', () => setAlgorithm('bubblesort'));
        btnInsertionsort.addEventListener('click', () => setAlgorithm('insertionsort'));
        btnCocktail.addEventListener('click', () => setAlgorithm('cocktail'));
        btnBogosort.addEventListener('click', () => setAlgorithm('bogosort'));
        btnRandomize.addEventListener('click', randomizeData);

        // Functions
        function updateElementsValue() {
            elementsValue.textContent = sliderElements.value;
        }

        function updateStepValue() {
            state.currentStep = parseInt(sliderStep.value);
            stepValue.textContent = state.currentStep;
            redrawCanvas();
        }

        function togglePlayPause() {
            if (state.isPlaying) {
                pauseAnimation();
            } else {
                playAnimation();
            }
        }

        function playAnimation() {
            if (state.currentStep >= state.sortHistory.length) {
                resetStep();
            }

            state.isPlaying = true;
            btnPlayPause.textContent = '❚❚';

            state.animationInterval = setInterval(() => {
                if (state.currentStep < state.sortHistory.length) {
                    state.currentStep++;
                    sliderStep.value = state.currentStep;
                    stepValue.textContent = state.currentStep;
                    redrawCanvas();
                } else {
                    pauseAnimation();
                }
            }, 200);
        }

        function pauseAnimation() {
            state.isPlaying = false;
            clearInterval(state.animationInterval);
            btnPlayPause.textContent = '▶';
        }

        function stepBackward() {
            if (state.currentStep > 1) {
                state.currentStep--;
                sliderStep.value = state.currentStep;
                stepValue.textContent = state.currentStep;
                redrawCanvas();
            }
        }

        function stepForward() {
            if (state.currentStep < state.sortHistory.length) {
                state.currentStep++;
                sliderStep.value = state.currentStep;
                stepValue.textContent = state.currentStep;
                redrawCanvas();
            }
        }

        function resetStep() {
            pauseAnimation();
            state.currentStep = 1;
            sliderStep.value = 1;
            stepValue.textContent = 1;
            redrawCanvas();
        }

        function setAlgorithm(algorithm) {
            // Update active button
            document.querySelectorAll('.sort-btn').forEach(btn => {
                btn.classList.remove('active');
            });
            document.getElementById(`btn-${algorithm}`).classList.add('active');

            // Handle bogosort special case
            if (algorithm === 'bogosort') {
                sliderElements.disabled = true;
                sliderElements.value = 7;
                elementsValue.textContent = '7';
            } else {
                sliderElements.disabled = false;
            }

            // Update state and regenerate data
            state.currentAlgorithm = algorithm;
            randomizeData();
        }

        function randomizeData() {
            pauseAnimation();

            // Get number of elements
            state.numElements = parseInt(sliderElements.value);

            // Create and shuffle array
            let array = [];
            for (let i = 1; i <= state.numElements; i++) {
                array.push(i);
            }
            shuffleArray(array);

            // Set max Y value (for visualization)
            state.maxYValue = Math.max(state.numElements, 20);

            // Generate sort history
            generateSortHistory(array);

            // Update step controls
            sliderStep.max = state.sortHistory.length;
            sliderStep.value = 1;
            stepValue.textContent = '1';
            state.currentStep = 1;

            // Redraw
            redrawCanvas();
        }

        function shuffleArray(array) {
            for (let i = array.length - 1; i > 0; i--) {
                const j = Math.floor(Math.random() * (i + 1));
                [array[i], array[j]] = [array[j], array[i]];
            }
        }

        function generateSortHistory(initialArray) {
            state.sortHistory = [];

            // Make a copy of the array
            let array = [...initialArray];
            state.sortHistory.push([...array]);

            // Apply the selected algorithm
            switch (state.currentAlgorithm) {
                case 'shellsort':
                    shellSort(array);
                    break;
                case 'bubblesort':
                    bubbleSort(array);
                    break;
                case 'insertionsort':
                    insertionSort(array);
                    break;
                case 'cocktail':
                    cocktailSort(array);
                    break;
                case 'bogosort':
                    bogoSort(array);
                    break;
            }
        }

        // Sorting algorithms (each records steps)
        function shellSort(array) {
            let n = array.length;

            for (let gap = Math.floor(n/2); gap > 0; gap = Math.floor(gap/2)) {
                for (let i = gap; i < n; i++) {
                    let temp = array[i];
                    let j;
                    for (j = i; j >= gap && array[j - gap] > temp; j -= gap) {
                        array[j] = array[j - gap];
                        state.sortHistory.push([...array]);
                    }
                    array[j] = temp;
                    state.sortHistory.push([...array]);
                }
            }
        }

        function bubbleSort(array) {
            let n = array.length;
            for (let i = 0; i < n-1; i++) {
                for (let j = 0; j < n-i-1; j++) {
                    if (array[j] > array[j+1]) {
                        [array[j], array[j+1]] = [array[j+1], array[j]];
                        state.sortHistory.push([...array]);
                    }
                }
            }
        }

        function insertionSort(array) {
            let n = array.length;
            for (let i = 1; i < n; i++) {
                let key = array[i];
                let j = i - 1;

                while (j >= 0 && array[j] > key) {
                    array[j + 1] = array[j];
                    state.sortHistory.push([...array]);
                    j = j - 1;
                }
                array[j + 1] = key;
                state.sortHistory.push([...array]);
            }
        }

        function cocktailSort(array) {
            let swapped = true;
            let start = 0;
            let end = array.length;

            while (swapped) {
                swapped = false;

                for (let i = start; i < end - 1; i++) {
                    if (array[i] > array[i + 1]) {
                        [array[i], array[i + 1]] = [array[i + 1], array[i]];
                        swapped = true;
                        state.sortHistory.push([...array]);
                    }
                }

                if (!swapped) break;

                swapped = false;
                end--;

                for (let i = end - 1; i >= start; i--) {
                    if (array[i] > array[i + 1]) {
                        [array[i], array[i + 1]] = [array[i + 1], array[i]];
                        swapped = true;
                        state.sortHistory.push([...array]);
                    }
                }

                start++;
            }
        }

        function bogoSort(array) {
            // Bogo sort is very inefficient, so we limit it to 7 elements
            while (!isSorted(array)) {
                shuffleArray(array);
                state.sortHistory.push([...array]);
            }
        }

        function isSorted(array) {
            for (let i = 1; i < array.length; i++) {
                if (array[i-1] > array[i]) {
                    return false;
                }
            }
            return true;
        }

        function redrawCanvas() {
            // Trigger p5.js redraw
            if (sketch && sketch.loop) {
                sketch.loop();
                sketch.redraw();
            }
        }

        // Initialize
        updateElementsValue();
    </script>
</body>
</html>
