    <script>
        // Initialize default values
        const defaultValues = {
            x1: 500,
            v1: -10,
            x2: 100,
            v2: 5
        };

        // Get DOM elements
        const sliderX1 = document.getElementById('slider-x1');
        const sliderV1 = document.getElementById('slider-v1');
        const sliderX2 = document.getElementById('slider-x2');
        const sliderV2 = document.getElementById('slider-v2');

        const valueX1 = document.getElementById('value-x1');
        const valueV1 = document.getElementById('value-v1');
        const valueX2 = document.getElementById('value-x2');
        const valueV2 = document.getElementById('value-v2');

        const resultText = document.getElementById('result-text');
        const plotContainer = document.getElementById('plot-container');
        const resetButton = document.getElementById('btn-reset');

        // Set slider properties
        sliderX1.min = -1000;
        sliderX1.max = 1000;
        sliderX1.step = 1;
        sliderX1.value = defaultValues.x1;

        sliderV1.min = -15;
        sliderV1.max = 15;
        sliderV1.step = 0.01;
        sliderV1.value = defaultValues.v1;

        sliderX2.min = -1000;
        sliderX2.max = 1000;
        sliderX2.step = 1;
        sliderX2.value = defaultValues.x2;

        sliderV2.min = -15;
        sliderV2.max = 15;
        sliderV2.step = 0.01;
        sliderV2.value = defaultValues.v2;

        // Initialize the plot
        let plot = null;
        initPlot();

        // Update display values and plot on slider change
        function updateAll() {
            // Update displayed values
            valueX1.textContent = sliderX1.value;
            valueV1.textContent = parseFloat(sliderV1.value).toFixed(2);
            valueX2.textContent = sliderX2.value;
            valueV2.textContent = parseFloat(sliderV2.value).toFixed(2);

            // Calculate intersection
            const x1 = parseFloat(sliderX1.value);
            const v1 = parseFloat(sliderV1.value);
            const x2 = parseFloat(sliderX2.value);
            const v2 = parseFloat(sliderV2.value);

            let T = null;
            let X = null;
            let intersect = false;

            if (v1 !== v2) {
                T = (x1 - x2) / (v2 - v1);
                X = x1 + v1 * T;

                if (T >= 0 && T <= 1000) {
                    intersect = true;
                }
            }

            // Update result text
            if (v1 === v2) {
                resultText.textContent = "The runners' paths never intersect (parallel lines)";
            } else if (!intersect) {
                resultText.textContent = "The runners' paths never intersect within the time frame";
            } else {
                resultText.textContent = `T = ${T.toFixed(2)} s | X = ${X.toFixed(2)} m`;
            }

            // Update plot
            updatePlot(x1, v1, x2, v2, intersect ? {T, X} : null);
        }

        // Initialize the plot
        function initPlot() {
            const x1 = parseFloat(sliderX1.value);
            const v1 = parseFloat(sliderV1.value);
            const x2 = parseFloat(sliderX2.value);
            const v2 = parseFloat(sliderV2.value);

            const time = [0, 1000];
            const pos1 = [x1, x1 + v1 * 1000];
            const pos2 = [x2, x2 + v2 * 1000];

            const traces = [
                {
                    x: time,
                    y: pos1,
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: 'rgb(70, 130, 180)',
                        width: 2
                    },
                    name: 'Runner 1'
                },
                {
                    x: time,
                    y: pos2,
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: 'rgb(255, 165, 0)',
                        width: 2
                    },
                    name: 'Runner 2'
                }
            ];

            const layout = {
                xaxis: {
                    title: 't',
                    range: [0, 1000],
                    showgrid: true,
                    gridcolor: 'lightgray',
                    zeroline: true,
                    zerolinecolor: 'gray'
                },
                yaxis: {
                    title: 'distance',
                    autorange: true,
                    showgrid: true,
                    gridcolor: 'lightgray',
                    zeroline: true,
                    zerolinecolor: 'gray'
                },
                showlegend: false,
                margin: { t: 20, r: 20, b: 40, l: 60 }
            };

            plot = Plotly.newPlot(plotContainer, traces, layout);
        }

        // Update the plot with new data
        function updatePlot(x1, v1, x2, v2, intersection) {
            const time = [0, 1000];
            const pos1 = [x1, x1 + v1 * 1000];
            const pos2 = [x2, x2 + v2 * 1000];

            const traces = [
                {
                    x: time,
                    y: pos1,
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: 'rgb(70, 130, 180)',
                        width: 2
                    }
                },
                {
                    x: time,
                    y: pos2,
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: 'rgb(255, 165, 0)',
                        width: 2
                    }
                }
            ];

            if (intersection) {
                traces.push({
                    x: [intersection.T],
                    y: [intersection.X],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'rgb(0, 128, 0)',
                        size: 10,
                        line: {
                            color: 'rgb(0, 80, 0)',
                            width: 1
                        }
                    }
                });
            }

            Plotly.react(plotContainer, traces, {
                xaxis: {
                    title: 't',
                    range: [0, 1000],
                    showgrid: true,
                    gridcolor: 'lightgray',
                    zeroline: true,
                    zerolinecolor: 'gray'
                },
                yaxis: {
                    title: 'distance',
                    autorange: true,
                    showgrid: true,
                    gridcolor: 'lightgray',
                    zeroline: true,
                    zerolinecolor: 'gray'
                },
                showlegend: false,
                margin: { t: 20, r: 20, b: 40, l: 60 }
            });
        }

        // Event listeners
        sliderX1.addEventListener('input', updateAll);
        sliderV1.addEventListener('input', updateAll);
        sliderX2.addEventListener('input', updateAll);
        sliderV2.addEventListener('input', updateAll);

        resetButton.addEventListener('click', () => {
            sliderX1.value = defaultValues.x1;
            sliderV1.value = defaultValues.v1;
            sliderX2.value = defaultValues.x2;
            sliderV2.value = defaultValues.v2;
            updateAll();
        });

        // Initial update
        updateAll();
    </script>
