            { name: "Decomposition", x: 300, y: 400, width: 120, height: 30, tooltip: "Bacteria break down dead organic matter, returning phosphate to the soil and water." },
            { name: "Geologic uplift", x: 150, y: 300, width: 120, height: 30, tooltip: "Over millions of years, ocean sediments containing phosphate are lifted up to form new land." }
        ];

        function setup() {
            canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');
            noLoop();

            // Initialize DOM elements
            sliderPhase = document.getElementById('slider-phase');
            sliderValue = document.getElementById('slider-value');
            btnReset = document.getElementById('btn-reset');
            tooltip = document.getElementById('tooltip');

            // Set up event listeners
            sliderPhase.addEventListener('input', function() {
                sliderValue.textContent = this.value;
                redraw();
            });

            btnReset.addEventListener('click', function() {
                sliderPhase.value = 0;
                sliderValue.textContent = '0';
                redraw();
            });

            // Mouse movement for tooltips
            canvas.elt.addEventListener('mousemove', handleMouseMove);
            canvas.elt.addEventListener('mouseout', function() {
                tooltip.style.display = 'none';
            });
        }

        function handleMouseMove(event) {
            const rect = canvas.elt.getBoundingClientRect();
            const mouseX = event.clientX - rect.left;
            const mouseY = event.clientY - rect.top;

            const currentValue = parseInt(sliderPhase.value);
            let found = false;

            // Check static labels first
            for (const label of labels) {
                if (label.name === "Clouds" || label.name === "Rocks" || 
                    label.name === "Animals" || label.name === "Plants" || 
                    label.name === "Water") {
                    if (mouseX >= label.x && mouseX <= label.x + label.width &&
                        mouseY >= label.y && mouseY <= label.y + label.height) {
                        showTooltip(event, label.tooltip);
                        found = true;
                        break;
                    }
                }
            }

            // Check dynamic labels based on slider value
            if (!found) {
                if (currentValue >= 1 && labels[5].name === "Erosion") {
                    if (mouseX >= labels[5].x && mouseX <= labels[5].x + labels[5].width &&
                        mouseY >= labels[5].y && mouseY <= labels[5].y + labels[5].height) {
                        showTooltip(event, labels[5].tooltip);
                        found = true;
                    }
                }
                if (!found && currentValue >= 2 && labels[6].name === "Runoff") {
                    if (mouseX >= labels[6].x && mouseX <= labels[6].x + labels[6].width &&
                        mouseY >= labels[6].y && mouseY <= labels[6].y + labels[6].height) {
                        showTooltip(event, labels[6].tooltip);
                        found = true;
                    }
                }
                if (!found && currentValue >= 3 && labels[7].name === "Soil") {
                    if (mouseX >= labels[7].x && mouseX <= labels[7].x + labels[7].width &&
                        mouseY >= labels[7].y && mouseY <= labels[7].y + labels[7].height) {
                        showTooltip(event, labels[7].tooltip);
                        found = true;
                    }
                }
                if (!found && currentValue >= 4 && labels[8].name === "Leaching") {
                    if (mouseX >= labels[8].x && mouseX <= labels[8].x + labels[8].width &&
                        mouseY >= labels[8].y && mouseY <= labels[8].y + labels[8].height) {
                        showTooltip(event, labels[8].tooltip);
                        found = true;
                    }
                }
                if (!found && currentValue >= 5 && labels[9].name === "Plant uptake") {
                    if (mouseX >= labels[9].x && mouseX <= labels[9].x + labels[9].width &&
                        mouseY >= labels[9].y && mouseY <= labels[9].y + labels[9].height) {
                        showTooltip(event, labels[9].tooltip);
                        found = true;
                    }
                }
                if (!found && currentValue >= 6 && labels[10].name === "Animals eat plants") {
                    if (mouseX >= labels[10].x && mouseX <= labels[10].x + labels[10].width &&
                        mouseY >= labels[10].y && mouseY <= labels[10].y + labels[10].height) {
                        showTooltip(event, labels[10].tooltip);
                        found = true;
                    }
                }
                if (!found && currentValue >= 7 && labels[11].name === "Decomposition") {
                    if (mouseX >= labels[11].x && mouseX <= labels[11].x + labels[11].width &&
                        mouseY >= labels[11].y && mouseY <= labels[11].y + labels[11].height) {
                        showTooltip(event, labels[11].tooltip);
                        found = true;
                    }
                }
                if (!found && currentValue >= 8 && labels[12].name === "Geologic uplift") {
                    if (mouseX >= labels[12].x && mouseX <= labels[12].x + labels[12].width &&
                        mouseY >= labels[12].y && mouseY <= labels[12].y + labels[12].height) {
                        showTooltip(event, labels[12].tooltip);
                        found = true;
                    }
                }
            }

            if (!found) {
                tooltip.style.display = 'none';
            }
        }

        function showTooltip(event, text) {
            tooltip.textContent = text;
            tooltip.style.display = 'block';
            tooltip.style.left = (event.clientX + 10) + 'px';
            tooltip.style.top = (event.clientY + 10) + 'px';
        }

        function draw() {
            background(255);
            const currentValue = parseInt(sliderPhase.value);

            // Draw sky
            fill('#CAE9FF');
            noStroke();
            rect(0, 0, width, height/2);

            // Draw ground
            fill('#9B7653');
            rect(0, height/2, width, height/2);

            // Draw sun
            fill('#FFDD00');
            noStroke();
            ellipse(width - 50, 50, 80, 80);

            // Draw sun rays
            stroke('#FFDD00');
            strokeWeight(2);
            for (let i = 0; i < 12; i++) {
                const angle = TWO_PI * i / 12;
                const x1 = width - 50 + cos(angle) * 45;
                const y1 = 50 + sin(angle) * 45;
                const x2 = width - 50 + cos(angle) * 65;
                const y2 = 50 + sin(angle) * 65;
                line(x1, y1, x2, y2);
            }

            // Draw clouds
            fill('#E0E0E0');
            noStroke();
            ellipse(80, 60, 70, 40);
            ellipse(120, 60, 80, 50);
            ellipse(100, 40, 60, 40);

            // Draw rain
            stroke('#0000FF');
            strokeWeight(1);
            for (let i = 0; i < 10; i++) {
                const x = random(70, 130);
                const y = random(70, 100);
                line(x, y, x + 5, y + 15);
            }

            // Draw rocks
            fill('#5A5A5A');
            noStroke();
            beginShape();
            vertex(120, 300);
            vertex(140, 280);
            vertex(160, 290);
            vertex(180, 270);
            vertex(200, 290);
            vertex(180, 310);
            vertex(160, 320);
            vertex(140, 310);
            endShape(CLOSE);

            // Draw water
            fill(0, 0, 255, 150);
            noStroke();
            ellipse(150, 450, 200, 80);
            ellipse(100, 470, 150, 60);

            // Draw plants
            fill('#90EE90');
            noStroke();
            triangle(380, 300, 390, 250, 400, 300);
            triangle(400, 300, 410, 240, 420, 300);
            triangle(420, 300, 430, 260, 440, 300);

            // Draw animal (simplified cow)
            fill(255);
            stroke(0);
            strokeWeight(1);
            // Body
            ellipse(300, 250, 100, 60);
            // Head
            ellipse(350, 240, 40, 30);
            // Legs
            rect(280, 280, 10, 40);
            rect(310, 280, 10, 40);
            // Eye
            fill(0, 0, 255);
            noStroke();
            ellipse(360, 235, 5, 5);
            // Spots
            fill(0);
            ellipse(290, 240, 15, 10);
            ellipse(310, 260, 20, 15);

            // Draw static labels
            textSize(14);
            fill('#505050');
            noStroke();
            text("Clouds", 100, 80);

            fill(0);
            text("Rocks", 150, 350);
            text("Animals", 300, 350);

            fill('#006400');
            text("Plants", 400, 350);

            fill('#00008B');
            text("Water", 100, 450);

            // Draw dynamic elements based on slider value
            if (currentValue >= 1) {
                drawArrow(120, 150, 150, 290, "Erosion");
            }
            if (currentValue >= 2) {
                drawArrow(180, 320, 150, 430, "Runoff");
            }
            if (currentValue >= 3) {
                drawArrow(150, 430, 250, 400, "Soil");
            }
            if (currentValue >= 4) {
                drawArrow(180, 380, 150, 430, "Leaching");
            }
            if (currentValue >= 5) {
                drawArrow(300, 380, 400, 300, "Plant uptake");
            }
            if (currentValue >= 6) {
                drawArrow(400, 280, 350, 250, "Animals eat plants");
            }
            if (currentValue >= 7) {
                drawArrow(300, 280, 250, 380, "Decomposition");
            }
            if (currentValue >= 8) {
                drawCurvedArrow(150, 470, 150, 320, "Geologic uplift");
            }
        }

        function drawArrow(x1, y1, x2, y2, label) {
            // Draw arrow line
            stroke(0);
            strokeWeight(2);
            line(x1, y1, x2, y2);

            // Draw arrow head
            const angle = atan2(y2 - y1, x2 - x1);
            push();
            translate(x2, y2);
            rotate(angle);
            fill(0);
            noStroke();
            triangle(0, 0, -15, -5, -15, 5);
            pop();

            // Draw label
            textSize(14);
            fill(0);
            noStroke();
            const midX = (x1 + x2) / 2;
            const midY = (y1 + y2) / 2;
            text(label, midX, midY);

            // Update label position for tooltip
            const labelIndex = labels.findIndex(l => l.name === label);
            if (labelIndex !== -1) {
                labels[labelIndex].x = midX;
                labels[labelIndex].y = midY;
                labels[labelIndex].width = textWidth(label) + 10;
                labels[labelIndex].height = 20;
            }
        }

        function drawCurvedArrow(x1, y1, x2, y2, label) {
            // Draw curved arrow (simplified as a quadratic curve)
            noFill();
            stroke(0);
            strokeWeight(2);
            const cpX = (x1 + x2) / 2 - 50;
            const cpY = (y1 + y2) / 2 - 100;
            beginShape();
            vertex(x1, y1);
            quadraticVertex(cpX, cpY, x2, y2);
            endShape();

            // Draw arrow head
            const angle = atan2(y2 - cpY, x2 - cpX);
            push();
            translate(x2, y2);
            rotate(angle);
            fill(0);
            noStroke();
            triangle(0, 0, -15, -5, -15, 5);
            pop();

            // Draw label
            textSize(14);
            fill(0);
            noStroke();
            const midX = cpX;
            const midY = cpY;
            text(label, midX, midY);

            // Update label position for tooltip
            const labelIndex = labels.findIndex(l => l.name === label);
            if (labelIndex !== -1) {
                labels[labelIndex].x = midX;
                labels[labelIndex].y = midY;
                labels[labelIndex].width = textWidth(label) + 10;
                labels[labelIndex].height = 20;
            }
        }
    </script>
