<body>
    <div id="main-container">
        <!-- Control Panel -->
        <div id="control-panel">
            <!-- Cylinder Radius Slider -->
            <div>
                <label for="slider-cylinder-radius">cylinder radius</label>
                <input type="range" id="slider-cylinder-radius">
                <span id="label-cylinder-radius" class="value-display">2.10</span>
            </div>

            <!-- Opacity Slider -->
            <div>
                <label for="slider-opacity">opacity</label>
                <input type="range" id="slider-opacity">
                <span id="label-opacity" class="value-display">0.95</span>
            </div>

            <!-- Drill Radius Slider -->
            <div>
                <label for="slider-drill-radius">drill radius</label>
                <input type="range" id="slider-drill-radius">
                <span id="label-drill-radius" class="value-display">1.35</span>
            </div>

            <!-- Inclination Slider -->
            <div>
                <label for="slider-inclination">inclination</label>
                <input type="range" id="slider-inclination">
                <span id="label-inclination" class="value-display">0.76</span>
            </div>

            <!-- Rotation Slider -->
            <div>
