            <div>
                <label for="slider-offset">offset</label>
                <input type="range" id="slider-offset">
                <span id="label-offset" class="value-display">-0.92</span>
            </div>

            <!-- Boundary Curve Checkbox -->
            <div>
                <label for="checkbox-boundary-only">
                    <input type="checkbox" id="checkbox-boundary-only">
                    boundary curve only
                </label>
            </div>
        </div>

        <!-- Visualization Area -->
        <div id="visualization-container">
            <canvas id="main-canvas"></canvas>
            <div class="reset-icon" id="reset-view"></div>
        </div>
    </div>

    <script type="module">
        import * as THREE from 'https://unpkg.com/three@0.158.0/build/three.module.js';
        import { OrbitControls } from 'https://unpkg.com/three@0.158.0/examples/jsm/controls/OrbitControls.js';
        import { CSG } from 'https://unpkg.com/three-bvh-csg@0.0.16/dist/index.module.js';

        // Scene setup
        const canvas = document.getElementById('main-canvas');
        const renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
        renderer.setPixelRatio(window.devicePixelRatio);
        renderer.setClearColor(0xe0e0e0);

        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(50, 1, 0.1, 1000);
        camera.position.set(10, 5, 10);
        camera.lookAt(0, 0, 0);

        const controls = new OrbitControls(camera, canvas);
        controls.enableDamping = true;
        controls.dampingFactor = 0.25;

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);

        const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight1.position.set(1, 1, 1);
        scene.add(directionalLight1);

        const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight2.position.set(-1, -1, -1);
        scene.add(directionalLight2);

        // Materials
        const cylinderMaterial = new THREE.MeshStandardMaterial({
            color: 0xD2B48C,
            transparent: true,
            opacity: 0.95,
            side: THREE.DoubleSide
        });

        const ghostMaterial = new THREE.MeshStandardMaterial({
            color: 0xF0F0F0,
            transparent: true,
            opacity: 0.15,
            side: THREE.DoubleSide
        });

        const frontCurveMaterial = new THREE.MeshStandardMaterial({ color: 0xFFA500 });
        const backCurveMaterial = new THREE.MeshStandardMaterial({ color: 0x4169E1 });

        // Variables to store our objects
        let csgResult = null;
        let frontCurve = null;
        let backCurve = null;
        let mainCylinder = null;

        // Initialize controls
        const initControls = () => {
            // Set up sliders with their default values and ranges
            const sliders = [
                { id: 'slider-cylinder-radius', min: 0.5, max: 3.0, step: 0.05, value: 2.1 },
                { id: 'slider-opacity', min: 0.0, max: 1.0, step: 0.01, value: 0.95 },
                { id: 'slider-drill-radius', min: 0.1, max: 2.5, step: 0.05, value: 1.35 },
                { id: 'slider-inclination', min: 0, max: 1.57, step: 0.01, value: 0.76 },
                { id: 'slider-rotation', min: -3.14, max: 3.14, step: 0.01, value: 0.9 },
                { id: 'slider-offset', min: -2.0, max: 2.0, step: 0.01, value: -0.92 }
            ];

            sliders.forEach(slider => {
                const element = document.getElementById(slider.id);
                element.min = slider.min;
                element.max = slider.max;
                element.step = slider.step;
                element.value = slider.value;

                // Update the display value
                document.getElementById(`label-${slider.id.split('-')[1]}`).textContent = parseFloat(slider.value).toFixed(2);

                // Add event listener
                element.addEventListener('input', (e) => {
                    const value = parseFloat(e.target.value);
                    document.getElementById(`label-${slider.id.split('-')[1]}`).textContent = value.toFixed(2);
                    updateScene();
                });
            });

            // Checkbox
            const checkbox = document.getElementById('checkbox-boundary-only');
            checkbox.addEventListener('change', updateScene);

            // Reset view button
            document.getElementById('reset-view').addEventListener('click', () => {
                camera.position.set(10, 5, 10);
                camera.lookAt(0, 0, 0);
                controls.update();
            });
        };

        // Calculate boundary curve points
        const calculateBoundaryCurve = (mainRadius, drillRadius, inclination, rotation, offset) => {
            const frontPoints = [];
            const backPoints = [];
            const segments = 100;

            // Create a matrix that represents the drill cylinder's transformation
            const drillMatrix = new THREE.Matrix4();
            drillMatrix.makeRotationZ(Math.PI / 2); // Make it horizontal first
            drillMatrix.multiply(new THREE.Matrix4().makeRotationX(inclination));
            drillMatrix.multiply(new THREE.Matrix4().makeRotationY(rotation));
            drillMatrix.setPosition(0, offset, 0);

            // Inverse matrix for transforming points back
            const inverseMatrix = new THREE.Matrix4().copy(drillMatrix).invert();

            for (let i = 0; i <= segments; i++) {
                const t = (i / segments) * Math.PI * 2;

                // Create a point on the edge of the drill cylinder's cap
                const point = new THREE.Vector3(drillRadius * Math.cos(t), 5, drillRadius * Math.sin(t));

                // Transform it to world space
                point.applyMatrix4(drillMatrix);

                // Calculate intersection with main cylinder (x² + z² = R²)
                // The line is from the transformed point in the direction of the drill cylinder's local Y axis
                const direction = new THREE.Vector3(0, 1, 0).transformDirection(drillMatrix).normalize();

                // Parametric equation of line: P = point + t*direction
                // Substitute into cylinder equation:
                // (point.x + t*direction.x)² + (point.z + t*direction.z)² = mainRadius²
                // Solve quadratic equation for t
                const a = direction.x * direction.x + direction.z * direction.z;
                const b = 2 * (point.x * direction.x + point.z * direction.z);
                const c = point.x * point.x + point.z * point.z - mainRadius * mainRadius;

                const discriminant = b * b - 4 * a * c;

                if (discriminant >= 0) {
                    const sqrtDiscriminant = Math.sqrt(discriminant);
                    const t1 = (-b + sqrtDiscriminant) / (2 * a);
                    const t2 = (-b - sqrtDiscriminant) / (2 * a);

                    const p1 = new THREE.Vector3().copy(point).addScaledVector(direction, t1);
                    const p2 = new THREE.Vector3().copy(point).addScaledVector(direction, t2);

                    // Transform points back to drill cylinder's local space to determine front/back
                    const localP1 = p1.clone().applyMatrix4(inverseMatrix);
                    const localP2 = p2.clone().applyMatrix4(inverseMatrix);

                    // Determine which point is front and which is back based on local Z coordinate
                    if (localP1.z > localP2.z) {
                        frontPoints.push(p1);
                        backPoints.push(p2);
                    } else {
                        frontPoints.push(p2);
                        backPoints.push(p1);
                    }
                }
            }

            return { frontPoints, backPoints };
        };

        // Main scene update function
        const updateScene = () => {
            // Get current control values
            const mainRadius = parseFloat(document.getElementById('slider-cylinder-radius').value);
            const opacity = parseFloat(document.getElementById('slider-opacity').value);
            const drillRadius = parseFloat(document.getElementById('slider-drill-radius').value);
            const inclination = parseFloat(document.getElementById('slider-inclination').value);
            const rotation = parseFloat(document.getElementById('slider-rotation').value);
            const offset = parseFloat(document.getElementById('slider-offset').value);
            const boundaryOnly = document.getElementById('checkbox-boundary-only').checked;

            // Remove old objects from scene
            if (csgResult) scene.remove(csgResult);
            if (frontCurve) scene.remove(frontCurve);
            if (backCurve) scene.remove(backCurve);
            if (mainCylinder) scene.remove(mainCylinder);

            // Create main cylinder
            const mainCylinderGeometry = new THREE.CylinderGeometry(mainRadius, mainRadius, 8, 32, 1, true);
            mainCylinder = new THREE.Mesh(mainCylinderGeometry, boundaryOnly ? ghostMaterial : cylinderMaterial);
            mainCylinder.rotation.x = Math.PI / 2; // Make it vertical

            // Create drill cylinder
            const drillCylinderGeometry = new THREE.CylinderGeometry(drillRadius, drillRadius, 10, 32, 1, true);
            const drillCylinder = new THREE.Mesh(drillCylinderGeometry, cylinderMaterial);
            drillCylinder.rotation.z = Math.PI / 2; // Make it horizontal
            drillCylinder.rotation.x = inclination;
            drillCylinder.rotation.y = rotation;
            drillCylinder.position.y = offset;

            // Perform CSG operation
            const csg = new CSG();
            const mainCSG = csg.fromMesh(mainCylinder);
            const drillCSG = csg.fromMesh(drillCylinder);
            const resultCSG = mainCSG.subtract(drillCSG);

            csgResult = csg.toMesh(resultCSG, cylinderMaterial);
            csgResult.visible = !boundaryOnly;
            scene.add(csgResult);

            // Add main cylinder as ghost if in boundary-only mode
            if (boundaryOnly) {
                mainCylinder.material = ghostMaterial;
                scene.add(mainCylinder);
            }

            // Calculate and create boundary curves
            const { frontPoints, backPoints } = calculateBoundaryCurve(mainRadius, drillRadius, inclination, rotation, offset);

            if (frontPoints.length > 0) {
                const frontCurveGeometry = new THREE.TubeGeometry(
                    new THREE.CatmullRomCurve3(frontPoints),
                    100,
                    0.05,
                    8,
                    false
                );
                frontCurve = new THREE.Mesh(frontCurveGeometry, frontCurveMaterial);
                scene.add(frontCurve);

                const backCurveGeometry = new THREE.TubeGeometry(
                    new THREE.CatmullRomCurve3(backPoints),
                    100,
                    0.05,
                    8,
                    false
                );
                backCurve = new THREE.Mesh(backCurveGeometry, backCurveMaterial);
                scene.add(backCurve);
            }

            // Update opacity if not in boundary-only mode
            if (!boundaryOnly) {
                cylinderMaterial.opacity = opacity;
                csgResult.material = cylinderMaterial;
            }
        };

        // Handle window resize
        const onWindowResize = () => {
            const width = canvas.clientWidth;
            const height = canvas.clientHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height, false);
        };

        // Animation loop
        const animate = () => {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        };

        // Initialize and start
        initControls();
        updateScene();
        window.addEventListener('resize', onWindowResize);
        onWindowResize();
        animate();
    </script>
</body>
</html>
