                <div id="canvas-brightness-parent"></div>
                <div id="arrow-brightness" class="arrow">↑</div>
            </div>
            <div class="bar-wrapper">
                <div id="canvas-result-parent"></div>
            </div>
        </div>
    </main>

    <script>
        let hueCanvas, saturationCanvas, brightnessCanvas, resultCanvas;
        let hueSlider, saturationSlider, brightnessSlider;
        let hueValue, saturationValue, brightnessValue;
        let hueArrow, saturationArrow, brightnessArrow;
        let resetButton;

        function setup() {
            // Get DOM elements
            hueSlider = document.getElementById('slider-hue');
            saturationSlider = document.getElementById('slider-saturation');
            brightnessSlider = document.getElementById('slider-brightness');

            hueValue = document.getElementById('value-hue');
            saturationValue = document.getElementById('value-saturation');
            brightnessValue = document.getElementById('value-brightness');

            hueArrow = document.getElementById('arrow-hue');
            saturationArrow = document.getElementById('arrow-saturation');
            brightnessArrow = document.getElementById('arrow-brightness');

            resetButton = document.getElementById('btn-reset');

            // Create p5 canvases
            hueCanvas = new p5(hueSketch, 'canvas-hue-parent');
            saturationCanvas = new p5(saturationSketch, 'canvas-saturation-parent');
            brightnessCanvas = new p5(brightnessSketch, 'canvas-brightness-parent');
            resultCanvas = new p5(resultSketch, 'canvas-result-parent');

            // Set up event listeners
            hueSlider.addEventListener('input', updateVisuals);
            saturationSlider.addEventListener('input', updateVisuals);
            brightnessSlider.addEventListener('input', updateVisuals);
            resetButton.addEventListener('click', resetAll);

            // Initial update
            updateVisuals();
        }

        function updateVisuals() {
            // Update displayed values
            hueValue.textContent = parseFloat(hueSlider.value).toFixed(3);
            saturationValue.textContent = parseFloat(saturationSlider.value).toFixed(3);
            brightnessValue.textContent = parseFloat(brightnessSlider.value).toFixed(3);

            // Update arrows position
            updateArrowPosition(hueArrow, hueSlider.value);
            updateArrowPosition(saturationArrow, saturationSlider.value);
            updateArrowPosition(brightnessArrow, brightnessSlider.value);

            // Redraw canvases that need updating
            saturationCanvas.redraw();
            brightnessCanvas.redraw();
            resultCanvas.redraw();
        }

        function updateArrowPosition(arrow, value) {
            const parent = arrow.parentElement;
            const canvasWidth = parent.offsetWidth;
            const arrowWidth = arrow.offsetWidth;
            const position = (value * canvasWidth) - (arrowWidth / 2);
            arrow.style.left = `${position}px`;
        }

        function resetAll() {
            hueSlider.value = 0;
            saturationSlider.value = 0.5;
            brightnessSlider.value = 1;
            updateVisuals();
        }

        function hueSketch(p) {
            p.setup = function() {
                const parent = document.getElementById('canvas-hue-parent');
                const canvas = p.createCanvas(parent.offsetWidth, 30);
                canvas.parent('canvas-hue-parent');
                p.colorMode(p.HSB, 1, 1, 1);
                p.noLoop();

                // Draw the hue gradient
                p.noStroke();
                for (let x = 0; x < p.width; x++) {
                    const hue = x / p.width;
                    p.fill(hue, 1, 1);
                    p.rect(x, 0, 1, p.height);
                }

                // Add rounded corners
                p.fill(255);
                p.noStroke();
                p.rect(0, 0, p.width, p.height, 15);

                // Redraw the gradient on top
                for (let x = 0; x < p.width; x++) {
                    const hue = x / p.width;
                    p.fill(hue, 1, 1);
                    p.rect(x, 0, 1, p.height);
                }
            };
        }

        function saturationSketch(p) {
            p.setup = function() {
                const parent = document.getElementById('canvas-saturation-parent');
                const canvas = p.createCanvas(parent.offsetWidth, 30);
                canvas.parent('canvas-saturation-parent');
                p.colorMode(p.HSB, 1, 1, 1);
            };

            p.draw = function() {
                const hue = parseFloat(hueSlider.value);
                const brightness = parseFloat(brightnessSlider.value);

                p.noStroke();
                for (let x = 0; x < p.width; x++) {
                    const saturation = x / p.width;
                    p.fill(hue, saturation, brightness);
                    p.rect(x, 0, 1, p.height);
                }

                // Add rounded corners
                p.fill(255);
                p.noStroke();
                p.rect(0, 0, p.width, p.height, 15);

                // Redraw the gradient on top
                for (let x = 0; x < p.width; x++) {
                    const saturation = x / p.width;
                    p.fill(hue, saturation, brightness);
                    p.rect(x, 0, 1, p.height);
                }
            };
        }

        function brightnessSketch(p) {
            p.setup = function() {
                const parent = document.getElementById('canvas-brightness-parent');
                const canvas = p.createCanvas(parent.offsetWidth, 30);
                canvas.parent('canvas-brightness-parent');
                p.colorMode(p.HSB, 1, 1, 1);
            };

            p.draw = function() {
                const hue = parseFloat(hueSlider.value);
                const saturation = parseFloat(saturationSlider.value);

                p.noStroke();
                for (let x = 0; x < p.width; x++) {
                    const brightness = x / p.width;
                    p.fill(hue, saturation, brightness);
                    p.rect(x, 0, 1, p.height);
                }

                // Add rounded corners
                p.fill(255);
                p.noStroke();
                p.rect(0, 0, p.width, p.height, 15);

                // Redraw the gradient on top
                for (let x = 0; x < p.width; x++) {
                    const brightness = x / p.width;
                    p.fill(hue, saturation, brightness);
                    p.rect(x, 0, 1, p.height);
                }
            };
        }

        function resultSketch(p) {
            p.setup = function() {
                const parent = document.getElementById('canvas-result-parent');
                const canvas = p.createCanvas(parent.offsetWidth, 30);
                canvas.parent('canvas-result-parent');
                p.colorMode(p.HSB, 1, 1, 1);
            };

            p.draw = function() {
                const hue = parseFloat(hueSlider.value);
                const saturation = parseFloat(saturationSlider.value);
                const brightness = parseFloat(brightnessSlider.value);

                p.noStroke();
                p.fill(hue, saturation, brightness);
                p.rect(0, 0, p.width, p.height, 15);
            };
        }

        // Initialize when the page loads
        window.addEventListener('load', setup);
    </script>
</body>
</html>
