<body>
    <div id="main-container">
        <div id="controls-container">
            <div class="control-row">
                <label>α</label>
                <input type="range" id="slider-alpha" min="0.01" max="0.2" step="0.0001" value="0.05">
                <span id="value-alpha">0.05</span>
            </div>
            <div class="control-row">
                <label>β</label>
                <input type="range" id="slider-beta" min="1" max="20" step="0.01" value="5">
                <span id="value-beta">5.00</span>
            </div>
            <div class="control-row">
                <label>γ</label>
                <input type="range" id="slider-gamma" min="100" max="2000" step="1" value="500">
                <span id="value-gamma">500</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Initialize parameters
        let alpha = 0.05;
        let beta = 5;
        let gamma = 500;

        // DOM elements
        const sliderAlpha = document.getElementById('slider-alpha');
        const sliderBeta = document.getElementById('slider-beta');
        const sliderGamma = document.getElementById('slider-gamma');
        const valueAlpha = document.getElementById('value-alpha');
        const valueBeta = document.getElementById('value-beta');
        const valueGamma = document.getElementById('value-gamma');
        const plotContainer = document.getElementById('plot-container');

        // Event listeners for sliders
        sliderAlpha.addEventListener('input', function() {
            alpha = parseFloat(this.value);
            valueAlpha.textContent = alpha.toFixed(4);
            updatePlot();
        });

        sliderBeta.addEventListener('input', function() {
            beta = parseFloat(this.value);
            valueBeta.textContent = beta.toFixed(2);
            updatePlot();
        });

        sliderGamma.addEventListener('input', function() {
            gamma = parseInt(this.value);
            valueGamma.textContent = gamma;
            updatePlot();
        });

        // RK4 ODE solver
        function rk4(f, y0, t0, tf, h) {
            const n = Math.floor((tf - t0) / h);
            const y = new Array(n + 1);
            const t = new Array(n + 1);
            y[0] = y0.slice();
            t[0] = t0;

            for (let i = 0; i < n; i++) {
                const k1 = f(t[i], y[i]);
                const k2 = f(t[i] + h/2, y[i].map((yj, j) => yj + h/2 * k1[j]));
                const k3 = f(t[i] + h/2, y[i].map((yj, j) => yj + h/2 * k2[j]));
                const k4 = f(t[i] + h, y[i].map((yj, j) => yj + h * k3[j]));

                y[i+1] = y[i].map((yj, j) => yj + h/6 * (k1[j] + 2*k2[j] + 2*k3[j] + k4[j]));
                t[i+1] = t[i] + h;
            }

            return { t, y };
        }

        // ODE system
        function odeSystem(alpha, beta, gamma) {
            return function(t, y) {
                const dT = y[0];
                const CX = y[1];
                const expTerm = Math.exp(dT / (1 + dT/alpha));

                return [
                    gamma * CX * expTerm - beta * dT,
                    1 - CX * expTerm
                ];
            };
        }

        // Generate plot data
        function generatePlotData(alpha, beta, gamma) {
            const f = odeSystem(alpha, beta, gamma);
            const y0 = [0, 0]; // Initial conditions: ΔT(0) = 0, C_X(0) = 0
            const t0 = 0;
            const tf = 20;
            const h = 0.05;

            const solution = rk4(f, y0, t0, tf, h);
            const t = solution.t;
            const dT = solution.y.map(y => y[0]);
            const CX = solution.y.map(y => y[1]);
            const scaledCX = CX.map(c => 1000 * c);

            return { t, dT, scaledCX };
        }

        // Create or update plot
        function updatePlot() {
            const { t, dT, scaledCX } = generatePlotData(alpha, beta, gamma);

            const trace1 = {
                x: t,
                y: dT,
                mode: 'lines',
                line: { color: 'orange', width: 2 }
            };

            const trace2 = {
                x: t,
                y: scaledCX,
                mode: 'lines',
                line: { color: 'steelblue', width: 2 }
            };

            const layout = {
                xaxis: { title: '', range: [0, 20], zeroline: true },
                yaxis: { title: '', range: [0, 100], zeroline: true },
                showlegend: false,
                margin: { l: 50, r: 20, b: 30, t: 20 },
                plot_bgcolor: '#f0f0f0',
                paper_bgcolor: 'white',
                annotations: [
                    {
                        text: 'ΔT',
                        x: -0.05,
                        y: 100,
                        xref: 'paper',
                        yref: 'y',
                        xanchor: 'right',
                        yanchor: 'top',
                        showarrow: false,
                        font: { color: 'purple' }
                    },
                    {
                        text: '1000 C<sub>X</sub>',
                        x: -0.05,
                        y: 95,
                        xref: 'paper',
                        yref: 'y',
                        xanchor: 'right',
                        yanchor: 'top',
                        showarrow: false,
                        font: { color: 'purple' }
                    }
                ]
            };

            Plotly.newPlot(plotContainer, [trace1, trace2], layout);
        }

        // Initial plot
        updatePlot();
    </script>
</body>
