<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Rayleigh Wave Visualization</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 20px;
            background-color: #f5f5f5;
        }

        #main-container {
            max-width: 800px;
            margin: 0 auto;
            display: flex;
            flex-direction: column;
            gap: 20px;
        }

        #controls-container {
            background-color: white;
            padding: 20px;
            border-radius: 8px;
            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }

        .control-group {
            display: flex;
            align-items: center;
            margin-bottom: 15px;
        }

        .control-group label {
            width: 150px;
            font-weight: bold;
        }

        .control-group input[type="range"] {
            flex-grow: 1;
            margin: 0 10px;
        }

        .control-group span {
            width: 50px;
            text-align: right;
        }

        #canvas-container {
            background-color: white;
            border-radius: 8px;
            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
            padding: 10px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="controls-container">
            <div class="control-group">
                <label for="slider-time">Time</label>
                <input type="range" id="slider-time" min="0" max="25" step="0.01" value="7.0">
                <span id="value-time">7.00</span>
            </div>
            <div class="control-group">
                <label for="slider-wavelength">Wavelength</label>
                <input type="range" id="slider-wavelength" min="1" max="8" step="0.01" value="4.0">
                <span id="value-wavelength">4.00</span>
            </div>
            <div class="control-group">
                <label for="slider-longitudinal-amplitude">Longitudinal Amplitude</label>
                <input type="range" id="slider-longitudinal-amplitude" min="0" max="3" step="0.01" value="2.0">
                <span id="value-longitudinal-amplitude">2.00</span>
            </div>
            <div class="control-group">
                <label for="slider-latitudinal-amplitude">Latitudinal Amplitude</label>
                <input type="range" id="slider-latitudinal-amplitude" min="0" max="3" step="0.01" value="1.0">
                <span id="value-latitudinal-amplitude">1.00</span>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script>
        let sketch = function(p) {
            let particles = [];
            let gridCols = 10;
            let gridRows = 6;
            let time = 7.0;
            let isTimeSliderActive = false;
            let wavelength = 4.0;
            let longitudinalAmplitude = 2.0;
            let latitudinalAmplitude = 1.0;

            p.setup = function() {
                let canvas = p.createCanvas(700, 450);
                canvas.parent('canvas-container');

                // Initialize particle grid
                let spacingX = p.width / (gridCols + 1);
                let spacingY = p.height / (gridRows + 1);

                for (let row = 0; row < gridRows; row++) {
                    for (let col = 0; col < gridCols; col++) {
                        particles.push({
                            x0: spacingX * (col + 1),
                            y0: spacingY * (row + 1),
                            row: row
                        });
                    }
                }

                // Setup slider event listeners
                p.select('#slider-time').input(function() {
                    time = parseFloat(this.value());
                    p.select('#value-time').html(time.toFixed(2));
                    isTimeSliderActive = true;
                });

                p.select('#slider-time').mouseReleased(function() {
                    isTimeSliderActive = false;
                });

                p.select('#slider-wavelength').input(function() {
                    wavelength = parseFloat(this.value());
                    p.select('#value-wavelength').html(wavelength.toFixed(2));
                });

                p.select('#slider-longitudinal-amplitude').input(function() {
                    longitudinalAmplitude = parseFloat(this.value());
                    p.select('#value-longitudinal-amplitude').html(longitudinalAmplitude.toFixed(2));
                });

                p.select('#slider-latitudinal-amplitude').input(function() {
                    latitudinalAmplitude = parseFloat(this.value());
                    p.select('#value-latitudinal-amplitude').html(latitudinalAmplitude.toFixed(2));
                });
            };

            p.draw = function() {
                // Update time if slider is not being dragged
                if (!isTimeSliderActive) {
                    time += 0.03;
                    if (time > 25) time = 0;
                    p.select('#slider-time').value(time);
                    p.select('#value-time').html(time.toFixed(2));
                }

                // Clear canvas
                p.background('#E0F0FF');

                // Calculate current positions and draw paths
                let currentPositions = [];

                // First pass: draw elliptical paths and calculate positions
                for (let i = 0; i < particles.length; i++) {
                    let particle = particles[i];
                    let x0 = particle.x0;
                    let y0 = particle.y0;
                    let rowIndex = particle.row;

                    // Calculate wave properties
                    let k = p.TWO_PI / wavelength;
                    let ampDecay = p.exp(- (rowIndex / (gridRows - 1)) * 1.5);
                    let ampX = longitudinalAmplitude * ampDecay * 20;
                    let ampY = latitudinalAmplitude * ampDecay * 20;
                    let phase = k * x0 - time;

                    // Draw elliptical path
                    p.noFill();
                    p.stroke('#8F998F');
                    p.strokeWeight(2);
                    p.ellipse(x0, y0, 2 * ampX, 2 * ampY);

                    // Calculate current position
                    let dx = ampX * p.cos(phase);
                    let dy = ampY * p.sin(phase);
                    let x = x0 + dx;
                    let y = y0 + dy;

                    currentPositions.push({x: x, y: y, row: rowIndex});
                }

                // Second pass: draw connecting lines and particles
                for (let row = 0; row < gridRows; row++) {
                    // Get all particles in this row
                    let rowParticles = currentPositions.filter(p => p.row === row);

                    // Draw connecting lines
                    p.stroke('#0000FF');
                    p.strokeWeight(2);
                    p.drawingContext.setLineDash([5, 5]);

                    p.beginShape();
                    for (let i = 0; i < rowParticles.length; i++) {
                        let particle = rowParticles[i];
                        p.vertex(particle.x, particle.y);
                    }
                    p.endShape();

                    p.drawingContext.setLineDash([]);
                }

                // Draw particles
                p.fill('#FF0000');
                p.noStroke();
                for (let i = 0; i < currentPositions.length; i++) {
                    let pos = currentPositions[i];
                    p.ellipse(pos.x, pos.y, 8, 8);
                }
            };
        };

        new p5(sketch);
    </script>
</body>
</html>
