            b: document.getElementById('value-b'),
            a: document.getElementById('value-a')
        };

        // Event listeners
        btnArrhenius.addEventListener('click', () => {
            currentModel = "Arrhenius";
            btnArrhenius.classList.add('active');
            btnExponential.classList.remove('active');
            updateVisualization();
