        btnExponential.addEventListener('click', () => {
            currentModel = "exponential";
            btnExponential.classList.add('active');
            btnArrhenius.classList.remove('active');
            updateVisualization();
        });

        // Update slider value displays and trigger visualization update
        for (const [key, slider] of Object.entries(sliders)) {
            slider.addEventListener('input', () => {
                // Special handling for T_max to ensure it's always > T_min
                if (key === 'tmax' && parseFloat(slider.value) <= parseFloat(sliders.tmin.value)) {
                    slider.value = parseFloat(sliders.tmin.value) + 1;
                }

                valueDisplays[key].textContent = slider.value;
                updateVisualization();
            });

            // Initialize display values
            valueDisplays[key].textContent = slider.value;
        }

        // Linear regression helper function
        function linearRegression(x, y) {
            const n = x.length;
            let sumX = 0, sumY = 0, sumXY = 0, sumX2 = 0;

            for (let i = 0; i < n; i++) {
                sumX += x[i];
                sumY += y[i];
                sumXY += x[i] * y[i];
                sumX2 += x[i] * x[i];
            }

            const slope = (n * sumXY - sumX * sumY) / (n * sumX2 - sumX * sumX);
            const intercept = (sumY - slope * sumX) / n;

            // Calculate R-squared
            let ssTot = 0, ssRes = 0;
            const meanY = sumY / n;

            for (let i = 0; i < n; i++) {
                const yPred = slope * x[i] + intercept;
                ssTot += Math.pow(y[i] - meanY, 2);
                ssRes += Math.pow(y[i] - yPred, 2);
            }

            const rSquared = 1 - (ssRes / ssTot);

            return { slope, intercept, rSquared };
        }

        // Main visualization update function
        function updateVisualization() {
            // Get current parameter values
            const params = {
                n: parseInt(sliders.n.value),
                tmin: parseFloat(sliders.tmin.value),
                tmax: parseFloat(sliders.tmax.value),
                tref: parseFloat(sliders.tref.value),
                c: parseFloat(sliders.c.value),
                b: parseFloat(sliders.b.value),
                a: parseFloat(sliders.a.value)
            };

            // Generate temperature points
            const temperatures = [];
            for (let i = 0; i < params.n; i++) {
                temperatures.push(params.tmin + (params.tmax - params.tmin) * i / (params.n - 1));
            }

            // Generate data points based on current model
            let yValues, fitResult, fitEquation;

            if (currentModel === "Arrhenius") {
                // Generate Arrhenius data
                const kRef = Math.exp(-params.a / (params.tref + params.b));
                yValues = temperatures.map(T => 
                    Math.exp(-params.a / (T + params.b)) / kRef
                );

                // Fit with exponential model (linear regression on log(y) vs (T-Tref))
                const xFit = temperatures.map(T => T - params.tref);
                const yFit = yValues.map(y => Math.log(y));
                fitResult = linearRegression(xFit, yFit);

                // The fitted c parameter is the slope
                const fittedC = fitResult.slope;
                const rSquared = fitResult.rSquared;

                // Generate points for the fitted curve
                const fitYValues = temperatures.map(T => 
                    Math.exp(fittedC * (T - params.tref))
                );

                // Update top plot (Arrhenius data with exponential fit)
