                    xaxis: { title: 'T (°C)' },
                    yaxis: { title: 'k(T)/k(T<sub>ref</sub>)' },
                    margin: { t: 60 }
                });

                // Update bottom plot (linearized version)
                Plotly.newPlot('plot-bottom', [
                    {
                        x: xFit,
                        y: yFit,
                        mode: 'markers',
                        marker: { color: '#2ca02c' },
                        name: 'Transformed Data'
                    },
                    {
                        x: xFit,
                        y: xFit.map(x => fitResult.slope * x + fitResult.intercept),
                        mode: 'lines',
                        line: { color: '#2ca02c' },
                        name: 'Linear Fit'
                    }
                ], {
                    title: `linear fit of the exponential model<br>R² = ${rSquared.toFixed(4)}`,
                    xaxis: { title: 'T - T<sub>ref</sub>' },
                    yaxis: { title: 'log(k(T)/k(T<sub>ref</sub>))' },
                    shapes: [
                        { type: 'line', x0: 0, x1: 0, y0: Math.min(...yFit), y1: Math.max(...yFit), line: { color: 'black', width: 1 } },
                        { type: 'line', x0: Math.min(...xFit), x1: Math.max(...xFit), y0: 0, y1: 0, line: { color: 'black', width: 1 } }
                    ],
                    margin: { t: 60 }
                });

            } else {
                // Generate exponential data
                yValues = temperatures.map(T => 
                    Math.exp(params.c * (T - params.tref))
                );

                // Fit with Arrhenius model (linear regression on log(y) vs 1/(T+b))
                const xFit = temperatures.map(T => 1 / (T + params.b));
                const yFit = yValues.map(y => Math.log(y));
                fitResult = linearRegression(xFit, yFit);

                // The fitted a parameter is -slope
                const fittedA = -fitResult.slope;
                const rSquared = fitResult.rSquared;

                // Generate points for the fitted curve
                const kRef = Math.exp(-fittedA / (params.tref + params.b));
                const fitYValues = temperatures.map(T => 
                    Math.exp(-fittedA / (T + params.b)) / kRef
                );

                // Update top plot (exponential data with Arrhenius fit)
                Plotly.newPlot('plot-top', [
                    {
                        x: temperatures,
                        y: yValues,
                        mode: 'markers',
                        marker: { color: '#d62728' },
