            const processedPlaintext = fullPlaintext.toLowerCase().replace(/[^a-z]/g, '');

            // Calculate original frequency
            const originalFreq = calculateFrequency(processedPlaintext);

            // Encrypt the text
            let ciphertext = '';
            if (cipher === 'Caesar') {
                ciphertext = caesarCipher(processedPlaintext, offset);
            } else if (cipher === 'De Vigenere') {
                ciphertext = vigenereCipher(processedPlaintext, keyword, false);
            } else if (cipher === 'Autokey') {
                ciphertext = vigenereCipher(processedPlaintext, keyword, true);
            }

            encryptedText.textContent = ciphertext;

            // Calculate encrypted frequency
            const encryptedFreq = calculateFrequency(ciphertext);

            // Create charts
            createChart(originalChart, originalFreq, 'Original Message Frequency');
            createChart(encryptedChart, encryptedFreq, 'Encrypted Message Frequency');
