        // Helper functions
        function calculateFrequency(text) {
            const freq = {};
            for (let i = 0; i < 26; i++) {
                freq[String.fromCharCode(97 + i)] = 0;
            }

            for (const char of text) {
                freq[char]++;
            }

            const total = text.length || 1;
            const result = [];
            for (let i = 0; i < 26; i++) {
                const char = String.fromCharCode(97 + i);
                result.push({
                    char: char,
                    freq: (freq[char] / total * 100).toFixed(2)
                });
            }

            // Sort by frequency descending
            result.sort((a, b) => b.freq - a.freq);
            return result;
        }

