            if (x <= 0) return Infinity;
            if (x <= 2) {
                const t = x * x / 4;
                return Math.log(x/2) * besselI1(x) + (1/x) + 
                    0.15443144 * t - 0.67278579 * t * t - 0.18156897 * t * t * t - 
                    0.01919402 * t * t * t * t - 0.00110404 * t * t * t * t * t - 
                    0.00004686 * t * t * t * t * t * t;
            } else {
                const t = 2 / x;
                return Math.exp(-x) / Math.sqrt(x) * 
                    (1.25331414 + 0.23498619 * t - 0.03655620 * t * t + 
                     0.01504268 * t * t * t - 0.00780353 * t * t * t * t + 
                     0.00325614 * t * t * t * t * t - 0.00068245 * t * t * t * t * t * t);
            }
        }

        // Helper function for Bessel K calculations
        function besselI0(x) {
            const t = Math.abs(x);
            if (t <= 3.75) {
                const r = x / 3.75;
                const r2 = r * r;
                return 1.0 + 3.5156229 * r2 + 3.0899424 * r2 * r2 + 
                       1.2067492 * r2 * r2 * r2 + 0.2659732 * r2 * r2 * r2 * r2 + 
                       0.0360768 * r2 * r2 * r2 * r2 * r2 + 0.0045813 * r2 * r2 * r2 * r2 * r2 * r2;
            } else {
                const r = 3.75 / t;
                return Math.exp(t) / Math.sqrt(t) * 
                    (0.39894228 + 0.01328592 * r + 0.00225319 * r * r - 
                     0.00157565 * r * r * r + 0.00916281 * r * r * r * r - 
                     0.02057706 * r * r * r * r * r + 0.02635537 * r * r * r * r * r * r - 
                     0.01647633 * r * r * r * r * r * r * r + 0.00392377 * r * r * r * r * r * r * r * r);
            }
        }

        function besselI1(x) {
            const t = Math.abs(x);
            if (t <= 3.75) {
                const r = x / 3.75;
                const r2 = r * r;
                return t * (0.5 + 0.87890594 * r2 + 0.51498869 * r2 * r2 + 
                            0.15084934 * r2 * r2 * r2 + 0.02658733 * r2 * r2 * r2 * r2 + 
                            0.00301532 * r2 * r2 * r2 * r2 * r2 + 0.00032411 * r2 * r2 * r2 * r2 * r2 * r2);
            } else {
                const r = 3.75 / t;
                const s = Math.exp(t) / Math.sqrt(t);
                if (x >= 0) {
                    return s * (0.39894228 - 0.03988024 * r - 0.00362018 * r * r + 
                                0.00163801 * r * r * r - 0.01031555 * r * r * r * r + 
                                0.02282967 * r * r * r * r * r - 0.02895312 * r * r * r * r * r * r + 
                                0.01787654 * r * r * r * r * r * r * r - 0.00420059 * r * r * r * r * r * r * r * r);
                } else {
                    return -s * (0.39894228 - 0.03988024 * r - 0.00362018 * r * r + 
                                 0.00163801 * r * r * r - 0.01031555 * r * r * r * r + 
                                 0.02282967 * r * r * r * r * r - 0.02895312 * r * r * r * r * r * r + 
                                 0.01787654 * r * r * r * r * r * r * r - 0.00420059 * r * r * r * r * r * r * r * r);
                }
            }
        }

        // Stehfest algorithm for numerical inverse Laplace transform
        function stehfest(laplaceFunc, t, n) {
            n = n || 10; // Default to 10 terms
            const ln2 = Math.log(2);
            const v = new Array(n);
            let sum = 0;

            // Precompute coefficients
            for (let i = 1; i <= n; i++) {
                let min = Math.min(i, Math.floor(n/2));
                let max = Math.floor((i+1)/2);
                let sumk = 0;

                for (let k = max; k <= min; k++) {
                    const numerator = Math.pow(k, n/2) * factorial(2*k);
                    const denominator = factorial(n/2 - k) * factorial(k) * factorial(k-1) * factorial(i-k) * factorial(2*k - i);
                    sumk += numerator / denominator;
                }

                v[i-1] = sumk * Math.pow(-1, i + n/2);
            }

            // Compute the sum
            for (let i = 1; i <= n; i++) {
                const s = i * ln2 / t;
                sum += v[i-1] * laplaceFunc(s);
            }

            return sum * ln2 / t;
        }

        // Factorial function for Stehfest coefficients
        function factorial(n) {
            if (n < 0) return NaN;
            let result = 1;
            for (let i = 2; i <= n; i++) {
                result *= i;
            }
            return result;
        }

        // Laplace domain solution P_bar(s)/s
        function laplaceSolution(s, lambda, omega) {
            const f = (omega * (1 - omega) * s + lambda) / ((1 - omega) * s + lambda);
            const sqrt_sf = Math.sqrt(s * f);

            if (sqrt_sf === 0) return 0; // Handle potential division by zero

            const K0 = besselK0(sqrt_sf);
            const K1 = besselK1(sqrt_sf);

            return K0 / (sqrt_sf * K1 * s);
        }

        // Calculate pressure drop for a given tau
        function calculatePressureDrop(tau, lambda, omega) {
            // Wrap laplaceSolution for Stehfest
            const laplaceFunc = function(s) {
                return laplaceSolution(s, lambda, omega);
            };

            // Use Stehfest to invert the Laplace transform
            return stehfest(laplaceFunc, tau, 10);
        }

        // Generate time points (logarithmically spaced)
        function generateTimePoints() {
            const points = 200;
            const minLog = 0; // log10(1)
            const maxLog = 10; // log10(10^10)
            const logStep = (maxLog - minLog) / (points - 1);

            const timePoints = [];
            for (let i = 0; i < points; i++) {
                const logT = minLog + i * logStep;
                timePoints.push(Math.pow(10, logT));
            }

            return timePoints;
        }

        // Main function to update the plot
        function updatePlot() {
            // Get current slider values
            const lambdaExp = parseFloat(document.getElementById('slider-lambda').value);
            const omegaExp = parseFloat(document.getElementById('slider-omega').value);

            // Calculate actual parameter values
            const lambda = Math.pow(10, lambdaExp);
            const omega = Math.pow(10, omegaExp);

            // Update value displays
            document.getElementById('value-lambda').textContent = lambda < 1e-4 ? lambda.toExponential(2) : lambda.toFixed(5);
            document.getElementById('value-omega').textContent = omega.toFixed(5);

            // Generate time points
            const timePoints = generateTimePoints();

            // Calculate y-values based on current option
            const isPressureDrop = document.getElementById('btn-pressure-drop').classList.contains('active');
            const yValues = [];

            for (let i = 0; i < timePoints.length; i++) {
                const tau = timePoints[i];
                const p = calculatePressureDrop(tau, lambda, omega);

                if (isPressureDrop) {
                    yValues.push(p);
                } else {
                    // Difference plot: ΔP - 0.5 * (ln(τ) + 0.8091)
                    yValues.push(p - 0.5 * (Math.log(tau) + 0.8091));
                }
            }

            // Prepare plot data
            const trace = {
                x: timePoints,
                y: yValues,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'red', width: 2 }
            };

            // Prepare layout based on current option
            const layout = {
                xaxis: {
                    title: 'dimensionless time, τ',
                    type: 'log',
                    range: [0, 10],
                    dtick: 2,
                    gridcolor: '#ddd'
                },
                yaxis: {
                    title: isPressureDrop ? 'dimensionless pressure drop, ΔP' : 'dimensionless difference, ΔP − 1/2 (ln(τ) + 0.8091)',
                    range: isPressureDrop ? [0, 12] : [0, 3.5],
                    gridcolor: '#ddd'
                },
                title: isPressureDrop ? 'pressure drawdown at the wellbore' : 'deviation from the asymptote',
                margin: { l: 80, r: 50, b: 60, t: 60 },
                paper_bgcolor: '#fff',
                plot_bgcolor: '#fff'
            };

            // Update the plot
            Plotly.react('plot-container', [trace], layout);
        }
    </script>
</body>
</html>
