                    if (intersection) {
                        const dist = p5.Vector.dist(sourcePoint, intersection);
                        if (dist < minDist) {
                            minDist = dist;
                            closestIntersection = intersection;
                        }
                    }
                }

                if (closestIntersection) {
                    visibilityVertices.push(closestIntersection);
                }
            }

            // Draw visibility polygon
            if (visibilityVertices.length >= 2) {
                // Draw committed part (up to N-1)
                if (sliderValue < 38) {
                    fill(255, 255, 153); // light yellow
                    beginShape();
                    vertex(sourcePoint.x, sourcePoint.y);
                    for (let i = 0; i < visibilityVertices.length - 1; i++) {
                        vertex(visibilityVertices[i].x, visibilityVertices[i].y);
                    }
                    vertex(visibilityVertices[visibilityVertices.length - 1].x, visibilityVertices[visibilityVertices.length - 1].y);
                    endShape(CLOSE);
                }

                // Draw active triangle (last segment)
                if (sliderValue < 38 && visibilityVertices.length >= 2) {
                    fill(255, 204, 102); // orange-yellow
                    beginShape();
                    vertex(sourcePoint.x, sourcePoint.y);
                    vertex(visibilityVertices[visibilityVertices.length - 2].x, visibilityVertices[visibilityVertices.length - 2].y);
                    vertex(visibilityVertices[visibilityVertices.length - 1].x, visibilityVertices[visibilityVertices.length - 1].y);
                    endShape(CLOSE);
                } else if (sliderValue === 38) {
                    // Draw complete visibility polygon
                    fill(255, 204, 102); // orange-yellow
                    beginShape();
                    vertex(sourcePoint.x, sourcePoint.y);
                    for (const v of visibilityVertices) {
                        vertex(v.x, v.y);
                    }
                    endShape(CLOSE);
                }

                // Draw outline
                stroke(170, 170, 170, 128);
                strokeWeight(1);
                noFill();
                beginShape();
                vertex(sourcePoint.x, sourcePoint.y);
                for (const v of visibilityVertices) {
                    vertex(v.x, v.y);
                }
                endShape(CLOSE);
            }
        }

        function drawVertices() {
            // Draw all vertices as small red circles
            fill(255, 0, 0);
            noStroke();
            for (const v of allVertices) {
                ellipse(v.x, v.y, 4, 4);
            }
        }

        function drawSourcePoint() {
            // Draw source point as blue circle
            fill(0, 0, 255);
            stroke(0, 0, 255);
            strokeWeight(1);
            ellipse(sourcePoint.x, sourcePoint.y, 16, 16);
        }

        function drawHelperElements() {
            if (sortedVertices.length === 0 || sliderValue >= 38) return;

            const currentVertex = sortedVertices[sliderValue - 1];

            // Draw ray to current vertex
            stroke(0, 0, 255);
            strokeWeight(1);
            line(sourcePoint.x, sourcePoint.y, currentVertex.x, currentVertex.y);

            // Draw sweep arc
            noFill();
            stroke(0, 0, 255);
            strokeWeight(1);
            const startAngle = atan2(sortedVertices[0].y - sourcePoint.y, sortedVertices[0].x - sourcePoint.x);
            const endAngle = atan2(currentVertex.y - sourcePoint.y, currentVertex.x - sourcePoint.x);
            const radius = 30;
            arc(sourcePoint.x, sourcePoint.y, radius * 2, radius * 2, startAngle, endAngle);

            // Find and highlight active edge
            const ray = { v1: sourcePoint, v2: currentVertex };
            let closestIntersection = null;
            let activeEdge = null;
            let minDist = Infinity;

            for (const edge of allEdges) {
                const intersection = getIntersection(ray, edge);
                if (intersection) {
                    const dist = p5.Vector.dist(sourcePoint, intersection);
                    if (dist < minDist) {
                        minDist = dist;
                        closestIntersection = intersection;
                        activeEdge = edge;
                    }
                }
            }

            if (activeEdge) {
                stroke(0, 204, 0);
                strokeWeight(3);
                line(activeEdge.v1.x, activeEdge.v1.y, activeEdge.v2.x, activeEdge.v2.y);
            }
        }

        function getIntersection(ray, edge) {
            // Line-line intersection algorithm
            const x1 = ray.v1.x;
            const y1 = ray.v1.y;
            const x2 = ray.v2.x;
            const y2 = ray.v2.y;
            const x3 = edge.v1.x;
            const y3 = edge.v1.y;
            const x4 = edge.v2.x;
            const y4 = edge.v2.y;

            const denom = (y4 - y3) * (x2 - x1) - (x4 - x3) * (y2 - y1);
            if (denom === 0) return null; // Lines are parallel

            const ua = ((x4 - x3) * (y1 - y3) - (y4 - y3) * (x1 - x3)) / denom;
            const ub = ((x2 - x1) * (y1 - y3) - (y2 - y1) * (x1 - x3)) / denom;

            if (ua >= 0 && ub >= 0 && ub <= 1) {
                return createVector(x1 + ua * (x2 - x1), y1 + ua * (y2 - y1));
            }
            return null;
        }

        function handleMouseDown(e) {
            const mouseX = e.clientX - myCanvas.elt.getBoundingClientRect().left;
            const mouseY = e.clientY - myCanvas.elt.getBoundingClientRect().top;

            // Check if mouse is near source point
            if (dist(mouseX, mouseY, sourcePoint.x, sourcePoint.y) < 20) {
                isDragging = true;
            }
        }

        function handleMouseMove(e) {
            if (!isDragging) return;

            const mouseX = e.clientX - myCanvas.elt.getBoundingClientRect().left;
            const mouseY = e.clientY - myCanvas.elt.getBoundingClientRect().top;

            // Check if new position is inside outer polygon and outside holes
            if (isPointInPolygon(mouseX, mouseY, polygons[0].vertices) && 
                !isPointInAnyHole(mouseX, mouseY)) {
                sourcePoint.x = mouseX;
                sourcePoint.y = mouseY;
            }
        }

        function handleMouseUp() {
            isDragging = false;
        }

        function isPointInPolygon(x, y, polygon) {
            // Ray casting algorithm
            let inside = false;
            for (let i = 0, j = polygon.length - 1; i < polygon.length; j = i++) {
                const xi = polygon[i][0], yi = polygon[i][1];
                const xj = polygon[j][0], yj = polygon[j][1];

                const intersect = ((yi > y) !== (yj > y)) &&
                    (x < (xj - xi) * (y - yi) / (yj - yi) + xi);
                if (intersect) inside = !inside;
            }
            return inside;
        }

        function isPointInAnyHole(x, y) {
            for (let i = 1; i < polygons.length; i++) {
                if (isPointInPolygon(x, y, polygons[i].vertices)) {
                    return true;
                }
            }
            return false;
        }
    </script>
</body>
</html>
