    <script>
        // Initialize plot
        let plotInitialized = false;
        let layout = {
            title: '',
            xaxis: { title: 'x', range: [0, 1.5], zeroline: false },
            yaxis: { title: 'f(x)', range: [0, 4], zeroline: false },
            margin: { l: 50, r: 20, t: 50, b: 40 },
            shapes: [{
                type: 'rect',
                xref: 'x',
                yref: 'paper',
                x0: 0,
                y0: 0,
                x1: 0,
                y1: 1,
                fillcolor: 'lightblue',
                opacity: 0.4,
                layer: 'below',
                line: { width: 0 }
            }]
        };

        // Factorial function
        function factorial(n) {
            if (n <= 1) return 1;
            return n * factorial(n - 1);
        }

        // RK4 solver for the ODE system
        function rk4(f, y0, t0, tEnd, h) {
            let n = Math.floor((tEnd - t0) / h);
            let t = new Array(n + 1);
            let y = new Array(n + 1);
            t[0] = t0;
            y[0] = y0;

            for (let i = 0; i < n; i++) {
                let k1 = f(t[i], y[i]);
                let k2 = f(t[i] + h / 2, [y[i][0] + h * k1[0] / 2, y[i][1] + h * k1[1] / 2]);
                let k3 = f(t[i] + h / 2, [y[i][0] + h * k2[0] / 2, y[i][1] + h * k2[1] / 2]);
                let k4 = f(t[i] + h, [y[i][0] + h * k3[0], y[i][1] + h * k3[1]]);

                t[i + 1] = t[i] + h;
                y[i + 1] = [
                    y[i][0] + h * (k1[0] + 2 * k2[0] + 2 * k3[0] + k4[0]) / 6,
                    y[i][1] + h * (k1[1] + 2 * k2[1] + 2 * k3[1] + k4[1]) / 6
                ];
            }

            return { t: t, y: y };
        }

        // ODE system definition
        function odeSystem(t, y) {
            return [
                y[1], // y1' = y2
                -(y[1] + 1.5 * y[0]) / y[0] // y2' = -(y2 + 1.5*y1)/y1
            ];
        }

        // Format polynomial string
        function formatPolynomial(coefficients, order) {
            let terms = [];
            for (let k = 0; k <= order; k++) {
                let coeff = coefficients[k];
                if (Math.abs(coeff) < 1e-10) continue;

                let term = '';
                if (k === 0) {
                    term = coeff.toFixed(5);
                } else {
                    let absCoeff = Math.abs(coeff).toFixed(5);
                    let sign = coeff >= 0 ? '+' : '-';
                    term = ` ${sign} ${absCoeff}`;
                    if (k === 1) {
                        term += ' x';
                    } else {
                        term += ` x^${k}`;
                    }
                }

                // Handle first term
                if (k === 0) {
                    terms.push(term);
                } else {
                    // For subsequent terms, we've already included the sign
                    terms.push(term);
                }
            }

            // Join terms and clean up
            let polyStr = terms.join('');
            if (polyStr.startsWith(' + ')) {
                polyStr = polyStr.substring(3);
            }
            return polyStr;
        }

        // Update function triggered by slider changes
        function updateVisualization() {
            // Get current slider values
            const order = parseInt(document.getElementById('slider-order').value);
            const a = parseFloat(document.getElementById('slider-a').value);
            const b = parseFloat(document.getElementById('slider-b').value);

            // Update value displays
            document.getElementById('value-order').textContent = order;
            document.getElementById('value-a').textContent = a.toFixed(1);
            document.getElementById('value-b').textContent = b.toFixed(1);

            // Calculate power series coefficients
            const f_derivs = [a, b];

            // f''(0) = -(f'(0) + 1.5*f(0)) / f(0)
            f_derivs[2] = -(f_derivs[1] + 1.5 * f_derivs[0]) / f_derivs[0];

            // f'''(0) = -[f''(0)f'(0) + f''(0) + 1.5*f'(0)] / f(0)
            if (order >= 3) {
                f_derivs[3] = -(f_derivs[2] * f_derivs[1] + f_derivs[2] + 1.5 * f_derivs[1]) / f_derivs[0];
            }

            // f''''(0) = -[2f'''(0)f'(0) + (f''(0))^2 + f'''(0) + 1.5*f''(0)] / f(0)
            if (order >= 4) {
                f_derivs[4] = -(2 * f_derivs[3] * f_derivs[1] + Math.pow(f_derivs[2], 2) + f_derivs[3] + 1.5 * f_derivs[2]) / f_derivs[0];
            }

            // f'''''(0) = -[3f''''(0)f'(0) + 4f'''(0)f''(0) + f''''(0) + 1.5*f'''(0)] / f(0)
            if (order >= 5) {
                f_derivs[5] = -(3 * f_derivs[4] * f_derivs[1] + 4 * f_derivs[3] * f_derivs[2] + f_derivs[4] + 1.5 * f_derivs[3]) / f_derivs[0];
            }

            // Calculate polynomial coefficients c_k = f_derivs[k] / k!
            const coefficients = [];
            for (let k = 0; k <= order; k++) {
                coefficients.push(f_derivs[k] / factorial(k));
            }

            // Generate power series data (red curve)
            const xValues = [];
            const yApprox = [];
            const numPoints = 200;
            const xMax = 1.5;

            for (let i = 0; i <= numPoints; i++) {
                const x = (i / numPoints) * xMax;
                xValues.push(x);

                // Calculate power series approximation
                let y = 0;
                for (let k = 0; k <= order; k++) {
                    y += coefficients[k] * Math.pow(x, k);
                }
                yApprox.push(y);
            }

            // Generate numerical solution data (green curve)
            const rk4Result = rk4(odeSystem, [a, b], 0, xMax, xMax / numPoints);
            const yNumerical = rk4Result.y.map(item => item[0]);

            // Find agreement region (x_limit)
            let x_limit = xMax;
            for (let i = 0; i < xValues.length; i++) {
                if (Math.abs(yApprox[i] - yNumerical[i]) >= 0.01) {
                    x_limit = xValues[i];
                    break;
                }
            }

            // Update plot title with polynomial
            const polynomialStr = formatPolynomial(coefficients, order);
            layout.title = `f(x) ≈ ${polynomialStr}`;
            layout.shapes[0].x1 = x_limit;

            // Create plot traces
            const traceRed = {
                x: xValues,
                y: yApprox,
                type: 'scatter',
                mode: 'lines',
                name: 'Power Series',
                line: { color: 'red', width: 2 }
            };

            const traceGreen = {
                x: rk4Result.t,
                y: yNumerical,
                type: 'scatter',
                mode: 'lines',
                name: 'Numerical Solution',
                line: { color: 'green', width: 3 }
            };

            // Update or create plot
            if (plotInitialized) {
                Plotly.react('plot-container', [traceRed, traceGreen], layout);
            } else {
                Plotly.newPlot('plot-container', [traceRed, traceGreen], layout);
                plotInitialized = true;
            }
        }

        // Set up event listeners
        document.getElementById('slider-order').addEventListener('input', updateVisualization);
        document.getElementById('slider-a').addEventListener('input', updateVisualization);
        document.getElementById('slider-b').addEventListener('input', updateVisualization);

        // Initialize visualization
        window.addEventListener('load', updateVisualization);
    </script>
