                    const labelTexture = createTextTexture(freq.toString(), 'black');
                    const labelMaterial = new THREE.SpriteMaterial({ map: labelTexture });
                    const label = new THREE.Sprite(labelMaterial);
                    label.position.set(-13.5, freq, 0);
                    label.scale.set(1, 1, 1);
                    axesGroup.add(label);
                }
            }

            // Vertical grid lines (for 2D view)
            for (let i = 0; i < 26; i++) {
                const gridGeometry = new THREE.BufferGeometry();
                const gridPoints = [
                    new THREE.Vector3(i - 12.5, 0, 0),
                    new THREE.Vector3(i - 12.5, maxFrequency, 0)
                ];
                gridGeometry.setFromPoints(gridPoints);
                const gridLine = new THREE.Line(gridGeometry, gridMaterial);
                gridGroup.add(gridLine);
            }

            axesGroup.add(gridGroup);
            return axesGroup;
        }

        // Create 2D bar chart
        function create2DBarChart(frequencies) {
            const chartGroup = new THREE.Group();
            const maxFrequency = Math.max(...Object.values(frequencies));

            // Create axes
            const axes = createAxes(maxFrequency);
            chartGroup.add(axes);

            // Create bars
            const letters = Object.keys(frequencies).sort();
            const barWidth = 0.8;
            const barDepth = 0.5;

            letters.forEach((letter, index) => {
                const freq = frequencies[letter];
                const height = freq;
                const x = index - 12.5;

                // Calculate color based on frequency
                const normalizedFreq = freq / maxFrequency;
                let color;
                if (normalizedFreq < 0.33) {
                    color = new THREE.Color(0x1E90FF); // Blue
                } else if (normalizedFreq < 0.66) {
                    color = new THREE.Color(0x9932CC); // Purple
                } else {
                    color = new THREE.Color(0xFF4500); // Orange-red
                }

                // Create bar
                const geometry = new THREE.BoxGeometry(barWidth, height, barDepth);
                const material = new THREE.MeshStandardMaterial({ color });
                const bar = new THREE.Mesh(geometry, material);
                bar.position.set(x, height / 2, 0);
                chartGroup.add(bar);

                // Add letter label
                const labelTexture = createTextTexture(letter, 'black');
                const labelMaterial = new THREE.SpriteMaterial({ map: labelTexture });
                const label = new THREE.Sprite(labelMaterial);
                label.position.set(x, height + 0.5, 0);
                label.scale.set(1, 1, 1);
                chartGroup.add(label);
            });

            return chartGroup;
        }

        // Create 3D keyboard
        function create3DKeyboard(frequencies, layout) {
            const keyboardGroup = new THREE.Group();
            const maxFrequency = Math.max(...Object.values(frequencies));

            // Create axes
            const axes = createAxes(maxFrequency);
            keyboardGroup.add(axes);

            // Get layout data
            const layoutData = keyboardLayouts[layout];

            // Create keys for each row
            createKeyboardRow(layoutData.top, 2, colors.topRow, frequencies, keyboardGroup);
            createKeyboardRow(layoutData.home, 0, colors.homeRow, frequencies, keyboardGroup);
            createKeyboardRow(layoutData.bottom, -2, colors.bottomRow, frequencies, keyboardGroup);

            return keyboardGroup;
        }

        // Helper to create a single row of keyboard keys
        function createKeyboardRow(letters, yPos, color, frequencies, parentGroup) {
            const keyRadius = 0.8;
            const keyHeight = 0.2;
            const startX = -((letters.length - 1) * 1.5) / 2;

            letters.forEach((letter, index) => {
                const freq = frequencies[letter.toUpperCase()] || 0;
                const height = Math.max(keyHeight, freq * 0.5);
                const x = startX + index * 1.5;

                // Create key cylinder
                const geometry = new THREE.CylinderGeometry(
                    keyRadius, keyRadius, height, 32
                );
                geometry.rotateX(Math.PI / 2);
                const material = new THREE.MeshStandardMaterial({ 
                    color: new THREE.Color(color),
                    roughness: 0.7,
                    metalness: 0.1
                });
                const key = new THREE.Mesh(geometry, material);
                key.position.set(x, height / 2, yPos);
                parentGroup.add(key);

                // Create key top (with letter)
                const topGeometry = new THREE.CircleGeometry(keyRadius * 0.9, 32);
                const topMaterial = new THREE.MeshStandardMaterial({
                    color: new THREE.Color(0xeeeeee),
                    roughness: 0.3,
                    metalness: 0.2
                });
                const keyTop = new THREE.Mesh(topGeometry, topMaterial);
                keyTop.position.set(x, height + 0.01, yPos);
                keyTop.rotation.x = -Math.PI / 2;
                parentGroup.add(keyTop);

                // Add letter label
                const labelTexture = createTextTexture(letter.toUpperCase(), 'black');
                const labelMaterial = new THREE.SpriteMaterial({ map: labelTexture });
                const label = new THREE.Sprite(labelMaterial);
                label.position.set(x, height + 0.02, yPos);
                label.scale.set(0.8, 0.8, 0.8);
                parentGroup.add(label);
            });
        }

        // Clear the scene
        function clearScene() {
            while (scene.children.length > 0) {
                const child = scene.children[0];
                if (child !== ambientLight && child !== directionalLight) {
                    scene.remove(child);
                }
            }
        }

        // Update visualization based on current state
        function updateVisualization() {
            clearScene();

            // Calculate frequencies for current text
            letterFrequencies = calculateFrequencies(currentText);

            // Create appropriate visualization
            if (currentView === '2d') {
                const chart = create2DBarChart(letterFrequencies);
                scene.add(chart);
                camera = orthoCamera;
            } else {
                const keyboard = create3DKeyboard(letterFrequencies, currentLayout);
                scene.add(keyboard);
                camera = perspectiveCamera;
            }
        }

        // Handle window resize
        function onWindowResize() {
            const width = canvas.clientWidth;
            const height = canvas.clientHeight;

            // Update renderer
            renderer.setSize(width, height, false);

            // Update cameras
            const aspect = width / height;

            orthoCamera.left = -10 * aspect;
            orthoCamera.right = 10 * aspect;
            orthoCamera.top = 10;
            orthoCamera.bottom = -10;
            orthoCamera.updateProjectionMatrix();

            perspectiveCamera.aspect = aspect;
            perspectiveCamera.updateProjectionMatrix();
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }

        // Initialize the application
        function init() {
            // Set up event listeners
            document.getElementById('select-text').addEventListener('change', (e) => {
                currentText = e.target.value;
                updateVisualization();
            });

            document.getElementById('btn-plot-2d').addEventListener('click', () => {
                if (currentView !== '2d') {
                    currentView = '2d';
                    document.getElementById('btn-plot-2d').classList.add('active');
                    document.getElementById('btn-plot-3d').classList.remove('active');
                    updateVisualization();
                }
            });

            document.getElementById('btn-plot-3d').addEventListener('click', () => {
                if (currentView !== '3d') {
                    currentView = '3d';
                    document.getElementById('btn-plot-3d').classList.add('active');
                    document.getElementById('btn-plot-2d').classList.remove('active');
                    updateVisualization();
                }
            });

            document.getElementById('btn-keyboard-qwerty').addEventListener('click', () => {
                if (currentLayout !== 'qwerty') {
                    currentLayout = 'qwerty';
                    document.getElementById('btn-keyboard-qwerty').classList.add('active');
                    document.getElementById('btn-keyboard-dvorak').classList.remove('active');
                    if (currentView === '3d') {
                        updateVisualization();
                    }
                }
            });

            document.getElementById('btn-keyboard-dvorak').addEventListener('click', () => {
                if (currentLayout !== 'dvorak') {
                    currentLayout = 'dvorak';
                    document.getElementById('btn-keyboard-dvorak').classList.add('active');
                    document.getElementById('btn-keyboard-qwerty').classList.remove('active');
                    if (currentView === '3d') {
                        updateVisualization();
                    }
                }
            });

            // Initialize cameras and first visualization
            initCameras();
            updateVisualization();

            // Set up resize handler
            window.addEventListener('resize', onWindowResize);
            onWindowResize();

            // Start animation loop
            animate();
        }

        // Start the application
        init();
    </script>
</body>
</html>
