                document.getElementById('label-p-amp').textContent = '0.2';

                document.getElementById('slider-p-wl').value = 3;
                document.getElementById('label-p-wl').textContent = '3';

                document.getElementById('slider-s-amp').value = 0.1;
                document.getElementById('label-s-amp').textContent = '0.1';

                document.getElementById('slider-s-wl').value = 1;
                document.getElementById('label-s-wl').textContent = '1';

                // Stop animation
                isPlaying = false;
                document.getElementById('btn-play-pause').textContent = 'Play';
                time = 0;

                // Reinitialize particles
                initParticles(50);

                // Redraw
                redrawCanvas();
            });

            // Pressure wave controls
            document.getElementById('slider-p-amp').addEventListener('input', function() {
                document.getElementById('label-p-amp').textContent = this.value;
                redrawCanvas();
            });

            document.getElementById('slider-p-wl').addEventListener('input', function() {
                document.getElementById('label-p-wl').textContent = this.value;
                redrawCanvas();
            });

            // Shear wave controls
            document.getElementById('slider-s-amp').addEventListener('input', function() {
                document.getElementById('label-s-amp').textContent = this.value;
                redrawCanvas();
            });

            document.getElementById('slider-s-wl').addEventListener('input', function() {
                document.getElementById('label-s-wl').textContent = this.value;
                redrawCanvas();
            });
        }

        function redrawCanvas() {
            // Get current values
            const trailLength = parseInt(document.getElementById('slider-trail').value);
            const pAmp = parseFloat(document.getElementById('slider-p-amp').value);
            const pWl = parseFloat(document.getElementById('slider-p-wl').value);
            const sAmp = parseFloat(document.getElementById('slider-s-amp').value);
            const sWl = parseFloat(document.getElementById('slider-s-wl').value);

            // Clear canvas
            background(255);

            // Draw axes
            drawAxes();

            // Update and draw particles
            for (let particle of particles) {
                // Calculate displacements
                const dx = pAmp * Math.sin(TWO_PI * (particle.x0 / pWl - time));
                const dy = sAmp * Math.sin(TWO_PI * (particle.x0 / sWl - time));

                // Add current position to history
                particle.history.push({x: particle.x0 + dx, y: dy});

                // Trim history to trail length
                if (particle.history.length > trailLength) {
                    particle.history.shift();
                }

                // Draw trail if enabled
                if (trailLength > 0) {
                    drawTrail(particle);
                }

                // Draw particle
                drawParticle(particle.x0 + dx, dy);

                // Draw vertical line at x position
                drawVerticalLine(particle.x0 + dx);
            }
        }

        function drawAxes() {
            stroke(200);
            strokeWeight(1);

            // X axis
            line(0, height/2, width, height/2);

            // Y axis
            line(width/2, 0, width/2, height);

            // X ticks
            for (let x = 0; x <= 5; x += 0.5) {
                const px = map(x, 0, 5, 0, width);
                line(px, height/2 - 5, px, height/2 + 5);
                if (x % 1 === 0) {
                    noStroke();
                    fill(100);
                    textSize(12);
                    textAlign(CENTER, TOP);
                    text(x.toString(), px, height/2 + 10);
                    stroke(200);
                }
            }

            // Y ticks
            for (let y = -0.4; y <= 0.4; y += 0.1) {
                const py = map(y, -0.5, 0.5, height, 0);
                line(width/2 - 5, py, width/2 + 5, py);
                if (y % 0.2 === 0 && y !== 0) {
                    noStroke();
                    fill(100);
                    textSize(12);
                    textAlign(RIGHT, CENTER);
                    text(y.toFixed(1), width/2 - 10, py);
                    stroke(200);
                }
            }
        }

        function drawVerticalLine(x) {
            const px = map(x, 0, 5, 0, width);
            stroke(211, 211, 211); // lightgray
            strokeWeight(1);
            line(px, 0, px, height);
        }

        function drawTrail(particle) {
            noStroke();
            fill(106, 90, 205, 50); // slateblue with transparency

            for (let pos of particle.history) {
                const px = map(pos.x, 0, 5, 0, width);
                const py = map(pos.y, -0.5, 0.5, height, 0);
                ellipse(px, py, 4, 4);
            }
        }

        function drawParticle(x, y) {
            const px = map(x, 0, 5, 0, width);
            const py = map(y, -0.5, 0.5, height, 0);

            noStroke();
            fill(106, 90, 205); // slateblue
            ellipse(px, py, 8, 8);
        }

        function windowResized() {
            resizeCanvas(800, 400);
            redrawCanvas();
        }
    </script>
</body>
