            document.getElementById('slider-depth').addEventListener('input', updateDepth);
            document.getElementById('slider-limit').addEventListener('input', updateLimit);

            // Set up button group event listeners
            document.querySelectorAll('#btn-group-component button').forEach(button => {
                button.addEventListener('click', updateComponent);
            });

            document.querySelectorAll('#btn-group-fault-type button').forEach(button => {
                button.addEventListener('click', updateFaultType);
            });

            // Initialize the plot
            updatePlot();
        });

        // Update functions for controls
        function updateDip(e) {
            currentDip = parseInt(e.target.value);
            document.getElementById('value-dip').textContent = currentDip;
            updatePlot();
        }

        function updateDepth(e) {
            currentDepth = parseInt(e.target.value);
            document.getElementById('value-depth').textContent = currentDepth;
            updatePlot();
        }

        function updateLimit(e) {
            currentLimit = parseInt(e.target.value);
            document.getElementById('value-limit').textContent = currentLimit;
            updatePlot();
        }

        function updateComponent(e) {
            // Remove active class from all buttons in the group
            document.querySelectorAll('#btn-group-component button').forEach(button => {
                button.classList.remove('active');
            });

            // Add active class to clicked button
            e.target.classList.add('active');

            // Update current component
            currentComponent = e.target.textContent;
            updatePlot();
        }

        function updateFaultType(e) {
            // Remove active class from all buttons in the group
            document.querySelectorAll('#btn-group-fault-type button').forEach(button => {
                button.classList.remove('active');
            });

            // Add active class to clicked button
            e.target.classList.add('active');

            // Update current fault type
            currentFaultType = e.target.id.replace('btn-fault-', '');
            updatePlot();
        }

        // Calculate displacements based on fault mechanics
        function calculateDisplacements(x, y, dip, depth, faultType, nu) {
            // Convert dip to radians
            const dipRad = dip * Math.PI / 180;

            // Calculate distance parameters
            const R = Math.sqrt(x*x + y*y + depth*depth);
            const q = depth * Math.cos(dipRad) - y * Math.sin(dipRad);
            const xi = depth * Math.sin(dipRad) + y * Math.cos(dipRad);

            // Initialize displacement components
            let ux = 0, uy = 0, uz = 0;

            // Calculate based on fault type
            if (faultType === 'tensile') {
                // Tensile fault (opening mode)
                const A = DISLOCATION_MAGNITUDE / (4 * Math.PI);
                const term1 = (x * q) / (R * (R + xi));
                const term2 = (x * q * (R + xi)) / (R * R * R);

                ux = A * (term1 - term2);
                uy = A * ((depth * Math.sin(dipRad) + y * Math.cos(dipRad)) / (R * (R + xi)) - 
                          (q * q) / (R * (R + xi)) - 
                          (q * q * (R + xi)) / (R * R * R));
                uz = A * ((depth * Math.cos(dipRad) - y * Math.sin(dipRad)) / (R * (R + xi)) - 
                          (q * (R + xi)) / (R * R * R));
            } 
            else if (faultType === 'strike-slip') {
                // Strike-slip fault
                const A = DISLOCATION_MAGNITUDE / (4 * Math.PI);
                const term1 = x * q / (R * R * R);
                const term2 = (2 * R + xi) / (R * (R + xi)) * Math.sin(dipRad);

                ux = A * (term1 + term2);
                uy = A * (q / (R * R * R) * y * Math.cos(dipRad) + 
                          (2 * R + xi) / (R * (R + xi)) * x * Math.sin(dipRad) * Math.sin(dipRad) - 
                          (R + xi) / (R * R * R) * x * Math.cos(dipRad) * Math.cos(dipRad));
                uz = A * (-q / (R * R * R) * depth + 
                          (2 * R + xi) / (R * (R + xi)) * x * Math.sin(dipRad) * Math.cos(dipRad));
            } 
            else if (faultType === 'normal') {
                // Normal fault
                const A = DISLOCATION_MAGNITUDE / (4 * Math.PI);
                const term1 = (x * q) / (R * (R + xi));
                const term2 = (x * q * (R + xi)) / (R * R * R);

                ux = A * (term1 - term2);
                uy = A * ((depth * Math.sin(dipRad) + y * Math.cos(dipRad)) / (R * (R + xi)) - 
                          (q * q) / (R * (R + xi)) - 
                          (q * q * (R + xi)) / (R * R * R));
                uz = A * ((depth * Math.cos(dipRad) - y * Math.sin(dipRad)) / (R * (R + xi)) - 
                          (q * (R + xi)) / (R * R * R));
            }

            return {
                ux: ux,
                uy: uy,
                uz: uz
            };
        }

        // Update the 3D plot
        function updatePlot() {
            // Create grid of coordinates
            const gridSize = 50;
            const xCoords = [];
            const yCoords = [];
            const zValues = [];

            for (let i = 0; i < gridSize; i++) {
                xCoords[i] = -currentLimit + (2 * currentLimit * i) / (gridSize - 1);
                yCoords[i] = -currentLimit + (2 * currentLimit * i) / (gridSize - 1);
                zValues[i] = new Array(gridSize);
            }

            // Calculate displacement values
            for (let i = 0; i < gridSize; i++) {
                for (let j = 0; j < gridSize; j++) {
                    const x = xCoords[i];
                    const y = yCoords[j];

                    const displacements = calculateDisplacements(
                        x, y, currentDip, currentDepth, currentFaultType, POISSON_RATIO
                    );

                    // Select the appropriate component and convert to mm
                    let value;
                    if (currentComponent === 'X') {
                        value = displacements.ux * 1000;
                    } else if (currentComponent === 'Y') {
                        value = displacements.uy * 1000;
                    } else {
                        value = displacements.uz * 1000;
                    }

                    zValues[i][j] = value;
                }
            }

            // Prepare plot data
            const plotData = [{
                type: 'surface',
                x: xCoords,
                y: yCoords,
                z: zValues,
                colorscale: [['0', '#FFA500'], ['1', '#FFD700']],
                showscale: false,
                contours: {
                    x: { show: true, color: 'black', width: 0.5 },
                    y: { show: true, color: 'black', width: 0.5 },
                    z: { show: false }
                },
                lighting: {
                    ambient: 0.8,
                    diffuse: 0.8,
                    specular: 0.2
                }
            }];

            // Prepare layout
            const plotLayout = {
                title: '',
                scene: {
                    xaxis: { title: 'x (m)', range: [-currentLimit, currentLimit] },
                    yaxis: { title: 'y (m)', range: [-currentLimit, currentLimit] },
                    zaxis: { title: currentComponent + ' (mm)', autorange: true },
                    camera: { eye: { x: 1.8, y: 1.8, z: 0.8 } }
                },
                margin: { l: 20, r: 20, b: 20, t: 20 }
            };

            // Update or create the plot
            Plotly.react('plot', plotData, plotLayout);
        }
    </script>
</body>
