    <script>
        // Bessel function J0 approximation
        function besselJ0(x) {
            let ax = Math.abs(x);
            if (ax < 8.0) {
                let y = x * x;
                let ans1 = 57568490574.0 + y * (-13362590354.0 + y * (651619640.7 
                    + y * (-11214424.18 + y * (77392.33017 + y * (-184.9052456)))));
                let ans2 = 57568490411.0 + y * (1029532985.0 + y * (9494680.718 
                    + y * (59272.64853 + y * (267.8532712 + y * 1.0))));
                return ans1 / ans2;
            } else {
                let z = 8.0 / ax;
                let y = z * z;
                let xx = ax - 0.785398164;
                let ans1 = 1.0 + y * (-0.1098628627e-2 + y * (0.2734510407e-4 
                    + y * (-0.2073370639e-5 + y * 0.2093887211e-6)));
                let ans2 = -0.1562499995e-1 + y * (0.1430488765e-3 
                    + y * (-0.6911147651e-5 + y * (0.7621095161e-6 
                    - y * 0.934935152e-7)));
                return Math.sqrt(0.636619772 / ax) * 
                    (Math.cos(xx) * ans1 - z * Math.sin(xx) * ans2);
            }
        }

        // Zeros of J0 (first 10)
        const zeros = [2.4048, 5.5201, 8.6537, 11.7915, 14.9309, 18.0711, 21.2116, 24.3525, 27.4935, 30.6346];

        // State variables
        let t = 0.1;
        let k = 2;
        let showAxes = true;
        let omega_k = zeros[k-1] / 2;
        let canvasWidth, canvasHeight;

        // p5.js sketch
        function setup() {
            const container = document.getElementById('canvas-container');
            canvasWidth = container.offsetWidth * 0.9;
            canvasHeight = canvasWidth * 0.6;

            let canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');

            // Set up event listeners
            document.getElementById('slider-t').addEventListener('input', function() {
                t = parseFloat(this.value);
                document.getElementById('t-value-display').textContent = t.toFixed(2);
            });

            document.getElementById('slider-k').addEventListener('input', function() {
                k = parseInt(this.value);
                document.getElementById('k-value-display').textContent = k;
                omega_k = zeros[k-1] / 2;
                updateFormula();
            });

            document.getElementById('checkbox-axes').addEventListener('change', function() {
                showAxes = this.checked;
            });
        }

        function draw() {
            background(240);

            // Set coordinate system
            scale(1, -1);
            translate(width/2, -height * 0.9);

            // Draw axes if enabled
            if (showAxes) {
                stroke(150);
                strokeWeight(1);
                drawingContext.setLineDash([5, 5]);

                // X axis
                line(-width/2 * 0.8, 0, width/2 * 0.8, 0);
                // Y axis
                line(0, 0, 0, -height * 0.8);

                // X axis ticks
                for (let x = -2; x <= 2; x += 1) {
                    let px = map(x, -2, 2, -width/2 * 0.8, width/2 * 0.8);
                    line(px, -5, px, 5);
                    push();
                    scale(1, -1);
                    textAlign(CENTER, TOP);
                    text(x.toFixed(0), px, 10);
                    pop();
                }

                // Y axis ticks
                for (let y = 0; y >= -1; y -= 0.2) {
                    let py = map(y, 0, -1, 0, height * 0.8);
                    line(-5, py, 5, py);
                    push();
                    scale(1, -1);
                    textAlign(RIGHT, CENTER);
                    text(y.toFixed(1), -10, -py);
                    pop();
                }

                drawingContext.setLineDash([]);
            }

            // Calculate parameters
            const z_k = zeros[k-1];
            const timeFactor = Math.cos(omega_k * t);
            const amplitude = 0.8 * width/4;

            // Draw the string
            stroke(0, 100, 200);
            strokeWeight(2);
            noFill();

            beginShape();
            for (let y_norm = 0; y_norm <= 1; y_norm += 0.01) {
                const bessel_arg = z_k * Math.sqrt(y_norm);
                const x_pos = amplitude * timeFactor * besselJ0(bessel_arg);
                const y_pos = map(y_norm, 0, 1, 0, -height * 0.8);
                vertex(x_pos, y_pos);
            }
            endShape();
        }

        function updateFormula() {
            const formulaDiv = document.getElementById('formula-display');
            formulaDiv.innerHTML = `\\(\\omega_{${k}} = ${omega_k.toFixed(2)} \\sqrt{g/l}\\)`;
