    <script>
        // Constants for propylene
        const Tc = 365.57; // K
        const Pc = 46.65;  // bar
        const omega = 0.144;
        const R = 0.0831446; // L·bar/(mol·K)

        // DOM elements
        const btnSRK = document.getElementById('btn-srk');
        const btnPR = document.getElementById('btn-pr');
        const checkboxAspen = document.getElementById('checkbox-aspen');
        const sliderPressure = document.getElementById('slider-pressure');
        const displayPressure = document.getElementById('display-pressure');
        const plotContainer = document.getElementById('plot-container');

        // State variables
        let currentEOS = 'srk';
        let currentPressure = 10;
        let showAspen = true;

        // Initialize plot
        let plotData = [];
        let plotLayout = {
            title: 'Gas-Phase Fugacity Coefficient vs Temperature',
            xaxis: {
                title: 'temperature (K)',
                range: [295, 505],
                showgrid: true,
                gridcolor: 'white',
                gridwidth: 1,
                minor: {
                    showgrid: true,
                    gridcolor: 'rgba(255,255,255,0.5)',
                    gridwidth: 0.5
                }
            },
            yaxis: {
                title: 'gas-phase fugacity coefficient',
                range: [0.68, 1.02],
                showgrid: true,
                gridcolor: 'white',
                gridwidth: 1,
                minor: {
                    showgrid: true,
                    gridcolor: 'rgba(255,255,255,0.5)',
                    gridwidth: 0.5
                }
            },
            plot_bgcolor: 'rgba(200,200,200,0.2)',
            paper_bgcolor: 'white',
            showlegend: true
        };

        // Event listeners
        btnSRK.addEventListener('click', () => {
            currentEOS = 'srk';
            btnSRK.classList.add('selected');
            btnPR.classList.remove('selected');
            updatePlot();
        });

        btnPR.addEventListener('click', () => {
            currentEOS = 'pr';
            btnPR.classList.add('selected');
            btnSRK.classList.remove('selected');
            updatePlot();
        });

        checkboxAspen.addEventListener('change', () => {
            showAspen = checkboxAspen.checked;
            updatePlot();
        });

        sliderPressure.addEventListener('input', () => {
            currentPressure = parseInt(sliderPressure.value);
            displayPressure.textContent = currentPressure;
            updatePlot();
        });

        // Cubic equation solver
        function solveCubic(a, b, c) {
            // Solves x³ + a*x² + b*x + c = 0
            // Returns array of real roots (1-3 roots)

            const Q = (a*a - 3*b)/9;
            const R = (2*a*a*a - 9*a*b + 27*c)/54;
            const Q3 = Q*Q*Q;
            const D = Q3 - R*R;

            if (D >= 0) {
                const theta = Math.acos(R/Math.sqrt(Q3));
                const sqrtQ = Math.sqrt(Q);
                const x1 = -2*sqrtQ*Math.cos(theta/3) - a/3;
                const x2 = -2*sqrtQ*Math.cos((theta + 2*Math.PI)/3) - a/3;
                const x3 = -2*sqrtQ*Math.cos((theta - 2*Math.PI)/3) - a/3;
                return [x1, x2, x3].filter(x => !isNaN(x));
            } else {
                const e = Math.pow(Math.sqrt(-D) + Math.abs(R), 1/3);
                const x1 = -(R/Math.abs(R))*(e + Q/e) - a/3;
                return [x1];
            }
        }

        // Calculate fugacity coefficient for a given T and P
        function calculateFugacity(T, P, eos) {
            const Tr = T / Tc;
            let a, b, m, alpha, A, B, Z;

            if (eos === 'srk') {
                // SRK equation
                a = 0.42748 * R * R * Tc * Tc / Pc;
                b = 0.08664 * R * Tc / Pc;
                m = 0.480 + 1.574 * omega - 0.176 * omega * omega;
                alpha = Math.pow(1 + m * (1 - Math.sqrt(Tr)), 2);
                const a_eff = a * alpha;

                A = a_eff * P / Math.pow(R * T, 2);
                B = b * P / (R * T);

                // Solve Z³ - Z² + (A - B - B²)Z - AB = 0
                const roots = solveCubic(-1, A - B - B*B, -A*B);
                Z = Math.max(...roots);

                // Calculate ln(φ)
                const lnPhi = Z - 1 - Math.log(Z - B) - (A/B) * Math.log(1 + B/Z);
                return Math.exp(lnPhi);
            } else {
                // PR equation
                a = 0.45724 * R * R * Tc * Tc / Pc;
                b = 0.07780 * R * Tc / Pc;
                m = 0.37464 + 1.54226 * omega - 0.26992 * omega * omega;
                alpha = Math.pow(1 + m * (1 - Math.sqrt(Tr)), 2);
                const a_eff = a * alpha;

                A = a_eff * P / Math.pow(R * T, 2);
                B = b * P / (R * T);

                // Solve Z³ - (1-B)Z² + (A - 3B² - 2B)Z - (AB - B² - B³) = 0
                const roots = solveCubic(-(1-B), A - 3*B*B - 2*B, -(A*B - B*B - B*B*B));
                Z = Math.max(...roots);

                // Calculate ln(φ)
                const sqrt2 = Math.sqrt(2);
                const term1 = (Z + (1 + sqrt2) * B) / (Z + (1 - sqrt2) * B);
                const lnPhi = Z - 1 - Math.log(Z - B) - (A / (2 * sqrt2 * B)) * Math.log(term1);
                return Math.exp(lnPhi);
            }
        }

        // Generate data for the plot
        function generatePlotData() {
            // Generate EOS data
            const temperatures = [];
            const fugacities = [];

            for (let T = 300; T <= 500; T += 5) {
                temperatures.push(T);
                fugacities.push(calculateFugacity(T, currentPressure, currentEOS));
            }

            const eosTrace = {
                x: temperatures,
                y: fugacities,
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: currentEOS === 'srk' ? 'red' : 'blue',
                    width: 2
                },
                name: currentEOS === 'srk' ? 'SRK' : 'PR'
            };

            // Aspen data (only shown at P=10 and checkbox checked)
            const aspenTrace = {
                x: [300, 310, 320, 330, 340, 350, 360, 370, 380, 390, 400],
                y: [0.868, 0.881, 0.891, 0.900, 0.909, 0.917, 0.924, 0.930, 0.937, 0.942, 0.946],
                type: 'scatter',
                mode: 'markers',
                marker: {
                    color: 'green',
                    symbol: 'square',
                    size: 8
                },
                name: 'Aspen Plus',
                visible: (currentPressure === 10 && showAspen)
            };

            return [eosTrace, aspenTrace];
        }

        // Update the plot
        function updatePlot() {
            plotData = generatePlotData();
            Plotly.react(plotContainer, plotData, plotLayout);
        }

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
