    <script>
        // Application state
        const state = {
            mode: 'da',
            inverse: false,
            focus: false,
            reactionProgress: 0,
            ergEwgIntensity: 0
        };

        // p5.js sketch
        let sketch = function(p) {
            let canvas;

            p.setup = function() {
                canvas = p.createCanvas(p.select('#canvas-container').width, p.select('#canvas-container').height);
                canvas.parent('canvas-container');
                p.background(255);
                drawInitialState();
            };

            p.draw = function() {
                p.background(255);

                switch(state.mode) {
                    case 'da':
                        drawDielsAlder();
                        break;
                    case 'regio':
                        drawRegioselectivity();
                        break;
                    case 'stereo-phile':
                        drawStereospecificityPhile();
                        break;
                    case 'stereo-diene':
                        drawStereospecificityDiene();
                        break;
                    case 'homo':
                        drawHOMOLUMO();
                        break;
                }
            };

            function drawInitialState() {
                p.text("Diels-Alder Reaction Visualizer", 20, 30);
            }

            function drawDielsAlder() {
                // Draw the Diels-Alder reaction based on reactionProgress
                const progress = state.reactionProgress / 100;

                // Calculate positions based on progress
                const startX1 = p.width * 0.2;
                const startX2 = p.width * 0.8;
                const currentX1 = p.lerp(startX1, p.width * 0.4, progress);
                const currentX2 = p.lerp(startX2, p.width * 0.6, progress);

                // Draw butadiene (simplified representation)
                p.stroke(0);
                p.strokeWeight(2);

                // Draw the diene
                const y = p.height * 0.5;
                p.line(currentX1, y, currentX1 + 60, y);
                p.line(currentX1 + 60, y, currentX1 + 120, y);
                p.line(currentX1 + 120, y, currentX1 + 180, y);

                // Draw double bonds (simplified)
                if (progress < 0.5) {
                    p.line(currentX1, y, currentX1 + 60, y);
                    p.line(currentX1 + 60, y, currentX1 + 120, y);
                    p.line(currentX1 + 120, y, currentX1 + 180, y);
                }

                // Draw dienophile (ethylene)
                p.line(currentX2, y, currentX2 + 40, y);

                // If focus is on, highlight relevant bonds
                if (state.focus) {
                    p.stroke(255, 0, 0, 150);
                    p.strokeWeight(6);

                    if (progress < 0.5) {
                        // Highlight the three π bonds that will break
                        p.line(currentX1, y, currentX1 + 60, y);
                        p.line(currentX1 + 60, y, currentX1 + 120, y);
                        p.line(currentX2, y, currentX2 + 40, y);
                    } else {
                        // Highlight the new bonds forming
                        const newBondProgress = (progress - 0.5) * 2;
                        p.line(p.width * 0.45, y, p.width * 0.55, y); // New σ bond
                        p.line(p.width * 0.4, y, p.width * 0.45, y);  // New π bond
                        p.line(p.width * 0.55, y, p.width * 0.6, y);  // New σ bond
                    }
                }

                // Draw progress text
                p.stroke(0);
                p.strokeWeight(1);
                p.textSize(16);
                p.text(`Reaction Progress: ${state.reactionProgress}%`, 20, 30);
            }

            function drawRegioselectivity() {
                // Draw the regioselectivity visualization
                p.textSize(16);
                p.text(`Regioselectivity Mode`, 20, 30);
                p.text(`ERG/EWG Intensity: ${state.ergEwgIntensity}%`, 20, 50);

                // Draw two reaction paths
                const centerY = p.height * 0.5;
                const path1Y = centerY - 100;
                const path2Y = centerY + 100;

                // Draw reactants
                p.stroke(0);
                p.strokeWeight(2);

                // Reactants for both paths
                const reactX1 = p.width * 0.2;
                const reactX2 = p.width * 0.8;

                // Path 1 (ortho product)
                p.line(reactX1, path1Y, reactX1 + 60, path1Y);
                p.line(reactX1 + 60, path1Y, reactX1 + 120, path1Y);
                p.line(reactX1 + 120, path1Y, reactX1 + 180, path1Y);
                p.line(reactX2, path1Y, reactX2 + 40, path1Y);

                // Path 2 (meta product)
                p.line(reactX1, path2Y, reactX1 + 60, path2Y);
                p.line(reactX1 + 60, path2Y, reactX1 + 120, path2Y);
                p.line(reactX1 + 120, path2Y, reactX1 + 180, path2Y);
                p.line(reactX2, path2Y, reactX2 + 40, path2Y);

                // Draw products based on reaction progress
                const progress = state.reactionProgress / 100;

                // Path 1 product (ortho)
                const prod1X = p.lerp(reactX1 + 200, reactX1 + 100, progress);
                p.line(prod1X, path1Y, prod1X + 60, path1Y);
                p.line(prod1X + 60, path1Y, prod1X + 120, path1Y);
                p.line(prod1X + 120, path1Y, prod1X + 180, path1Y);

                // Path 2 product (meta)
                const prod2X = p.lerp(reactX1 + 200, reactX1 + 100, progress);
                p.line(prod2X, path2Y, prod2X + 60, path2Y);
                p.line(prod2X + 60, path2Y, prod2X + 120, path2Y);
                p.line(prod2X + 120, path2Y, prod2X + 180, path2Y);

                // Adjust visibility based on ergEwgIntensity
                const intensity = state.ergEwgIntensity / 100;
                let path1Alpha = 255;
                let path2Alpha = 255;

                if (state.inverse) {
                    path1Alpha = 255 * (1 - intensity * 0.8);
                    path2Alpha = 255;
                } else {
                    path1Alpha = 255;
                    path2Alpha = 255 * (1 - intensity * 0.8);
                }

                // Highlight the dominant path
                p.stroke(255, 0, 0, path1Alpha);
                p.strokeWeight(4);
                p.line(prod1X, path1Y, prod1X + 60, path1Y);

                p.stroke(255, 0, 0, path2Alpha);
                p.line(prod2X, path2Y, prod2X + 60, path2Y);

                // If focus is on, highlight the new bonds
                if (state.focus) {
                    p.stroke(255, 0, 0, 150);
                    p.strokeWeight(6);
                    p.line(prod1X + 60, path1Y, prod1X + 120, path1Y);
                    p.line(prod2X + 60, path2Y, prod2X + 120, path2Y);
