                p.line(p.width * 0.6 + 60, bottomY, p.width * 0.6 + 120, bottomY);
                p.line(p.width * 0.6 + 120, bottomY, p.width * 0.6 + 180, bottomY);

                // If focus is on, highlight the new bonds
                if (state.focus) {
                    p.stroke(255, 0, 0, 150);
                    p.strokeWeight(6);
                    p.line(p.width * 0.5 + 60, topY, p.width * 0.5 + 120, topY);
                    p.line(p.width * 0.4 + 60, bottomY, p.width * 0.4 + 120, bottomY);
                    p.line(p.width * 0.6 + 60, bottomY, p.width * 0.6 + 120, bottomY);
                }
            }

            function drawStereospecificityDiene() {
                // Draw stereospecificity (diene) visualization
                p.textSize(16);
                p.text(`Stereospecificity (Diene) Mode`, 20, 30);

                const centerY = p.height * 0.5;
                const topY = centerY - 150;
                const bottomY = centerY + 150;

                // Top section: trans,trans-diene
                p.stroke(0);
