                p.line(p.width * 0.5 + 60, topY - 30, p.width * 0.5 + 120, topY + 30);
                p.line(p.width * 0.5 + 120, topY + 30, p.width * 0.5 + 180, topY + 30);

                // Draw symmetry plane
                p.stroke(255, 0, 0);
                p.line(p.width * 0.5 + 90, topY - 50, p.width * 0.5 + 90, topY + 50);

                // Bottom section: trans,cis-diene
                p.stroke(0);

                // Draw trans,cis-diene
                p.line(p.width * 0.2, bottomY - 30, p.width * 0.2 + 60, bottomY - 30);
                p.line(p.width * 0.2 + 60, bottomY - 30, p.width * 0.2 + 120, bottomY - 30);
                p.line(p.width * 0.2 + 120, bottomY - 30, p.width * 0.2 + 180, bottomY + 30);

                // Draw dienophile
                p.line(p.width * 0.7, bottomY, p.width * 0.7 + 40, bottomY);

                // Draw products (enantiomers)
                p.line(p.width * 0.4, bottomY - 30, p.width * 0.4 + 60, bottomY - 30);
                p.line(p.width * 0.4 + 60, bottomY - 30, p.width * 0.4 + 120, bottomY + 30);
                p.line(p.width * 0.4 + 120, bottomY + 30, p.width * 0.4 + 180, bottomY + 30);

                p.line(p.width * 0.6, bottomY - 30, p.width * 0.6 + 60, bottomY - 30);
                p.line(p.width * 0.6 + 60, bottomY - 30, p.width * 0.6 + 120, bottomY + 30);
                p.line(p.width * 0.6 + 120, bottomY + 30, p.width * 0.6 + 180, bottomY + 30);

                // If focus is on, highlight the new bonds
                if (state.focus) {
                    p.stroke(255, 0, 0, 150);
                    p.strokeWeight(6);
                    p.line(p.width * 0.5 + 60, topY - 30, p.width * 0.5 + 120, topY + 30);
                    p.line(p.width * 0.4 + 60, bottomY - 30, p.width * 0.4 + 120, bottomY + 30);
                    p.line(p.width * 0.6 + 60, bottomY - 30, p.width * 0.6 + 120, bottomY + 30);
                }
            }

            function drawHOMOLUMO() {
                // Draw HOMO/LUMO interactions visualization
                p.textSize(16);
                p.text(`HOMO/LUMO Interactions Mode`, 20, 30);
                p.text(`ERG/EWG Intensity: ${state.ergEwgIntensity}%`, 20, 50);

                if (state.inverse) {
                    p.text(`diene EWG, dienophile ERG`, 20, 70);
                } else {
                    p.text(`diene ERG, dienophile EWG`, 20, 70);
                }

                const centerY = p.height * 0.5;
                const dieneX = p.width * 0.3;
                const dienophileX = p.width * 0.7;

                // Draw energy levels
                p.stroke(0);
                p.strokeWeight(1);

                // Base energy levels
                const baseDieneHOMO = centerY - 80;
                const baseDieneLUMO = centerY - 40;
                const baseDienophileHOMO = centerY + 40;
                const baseDienophileLUMO = centerY + 80;

                // Adjust energy levels based on ergEwgIntensity
                const intensity = state.ergEwgIntensity / 100;
                let dieneHOMO, dieneLUMO, dienophileHOMO, dienophileLUMO;

                if (state.inverse) {
                    // diene EWG, dienophile ERG
                    dieneHOMO = baseDieneHOMO + intensity * 40;
                    dieneLUMO = baseDieneLUMO + intensity * 40;
                    dienophileHOMO = baseDienophileHOMO - intensity * 40;
                    dienophileLUMO = baseDienophileLUMO - intensity * 40;
                } else {
                    // diene ERG, dienophile EWG
                    dieneHOMO = baseDieneHOMO - intensity * 40;
                    dieneLUMO = baseDieneLUMO - intensity * 40;
                    dienophileHOMO = baseDienophileHOMO + intensity * 40;
                    dienophileLUMO = baseDienophileLUMO + intensity * 40;
                }

                // Draw diene energy levels
                p.line(dieneX - 50, dieneHOMO, dieneX + 50, dieneHOMO);
                p.line(dieneX - 50, dieneLUMO, dieneX + 50, dieneLUMO);
                p.text("diene HOMO", dieneX - 60, dieneHOMO - 5);
                p.text("diene LUMO", dieneX - 60, dieneLUMO - 5);

                // Draw dienophile energy levels
                p.line(dienophileX - 50, dienophileHOMO, dienophileX + 50, dienophileHOMO);
                p.line(dienophileX - 50, dienophileLUMO, dienophileX + 50, dienophileLUMO);
                p.text("dienophile HOMO", dienophileX - 80, dienophileHOMO - 5);
                p.text("dienophile LUMO", dienophileX - 80, dienophileLUMO - 5);

                // Draw interaction arrows
                if (state.inverse) {
                    // Dominant interaction: dienophile HOMO -> diene LUMO
                    p.stroke(255, 0, 0);
                    p.strokeWeight(2);
                    p.drawingContext.setLineDash([5, 5]);
                    p.line(dienophileX, dienophileHOMO, dieneX, dieneLUMO);

                    // Secondary interaction
                    p.stroke(0, 100);
                    p.strokeWeight(1);
                    p.line(dieneX, dieneHOMO, dienophileX, dienophileLUMO);
                } else {
                    // Dominant interaction: diene HOMO -> dienophile LUMO
                    p.stroke(255, 0, 0);
                    p.strokeWeight(2);
                    p.drawingContext.setLineDash([5, 5]);
                    p.line(dieneX, dieneHOMO, dienophileX, dienophileLUMO);

                    // Secondary interaction
                    p.stroke(0, 100);
                    p.strokeWeight(1);
                    p.line(dienophileX, dienophileHOMO, dieneX, dieneLUMO);
                }

                // Reset line style
                p.drawingContext.setLineDash([]);
            }

            p.windowResized = function() {
                p.resizeCanvas(p.select('#canvas-container').width, p.select('#canvas-container').height);
            };
        };

        // Initialize p5.js sketch
        new p5(sketch);

        // DOM event listeners
        document.addEventListener('DOMContentLoaded', function() {
            // Mode selector buttons
            const modeButtons = document.querySelectorAll('.mode-button');
            modeButtons.forEach(button => {
                button.addEventListener('click', function() {
                    // Remove active class from all buttons
                    modeButtons.forEach(btn => btn.classList.remove('active'));

                    // Add active class to clicked button
                    this.classList.add('active');

                    // Update state
                    state.mode = this.id.split('-')[2];

                    // Update control visibility
                    updateControlVisibility();
                });
            });

            // Checkbox controls
            document.getElementById('check-inverse').addEventListener('change', function() {
                state.inverse = this.checked;
            });

            document.getElementById('check-focus').addEventListener('change', function() {
                state.focus = this.checked;
            });

            // Slider controls
            const reactionSlider = document.getElementById('slider-reaction');
            reactionSlider.addEventListener('input', function() {
                state.reactionProgress = parseInt(this.value);
            });

            const ergEwgSlider = document.getElementById('slider-erg-ewg');
            ergEwgSlider.addEventListener('input', function() {
                state.ergEwgIntensity = parseInt(this.value);
            });

            // Plus/Minus buttons
            document.getElementById('btn-reaction-minus').addEventListener('click', function() {
                const newValue = Math.max(0, state.reactionProgress - 1);
                state.reactionProgress = newValue;
                reactionSlider.value = newValue;
            });

            document.getElementById('btn-reaction-plus').addEventListener('click', function() {
                const newValue = Math.min(100, state.reactionProgress + 1);
                state.reactionProgress = newValue;
                reactionSlider.value = newValue;
            });

            document.getElementById('btn-erg-ewg-minus').addEventListener('click', function() {
                const newValue = Math.max(0, state.ergEwgIntensity - 1);
                state.ergEwgIntensity = newValue;
                ergEwgSlider.value = newValue;
            });

            document.getElementById('btn-erg-ewg-plus').addEventListener('click', function() {
                const newValue = Math.min(100, state.ergEwgIntensity + 1);
                state.ergEwgIntensity = newValue;
                ergEwgSlider.value = newValue;
            });

            // Initialize control visibility
            updateControlVisibility();
        });

        function updateControlVisibility() {
            // Hide all control groups first
            document.getElementById('inverse-container').classList.add('hidden');
            document.getElementById('focus-container').classList.add('hidden');
            document.getElementById('reaction-slider-container').classList.add('hidden');
            document.getElementById('erg-ewg-slider-container').classList.add('hidden');

            // Show controls based on current mode
            switch(state.mode) {
                case 'da':
                    document.getElementById('focus-container').classList.remove('hidden');
                    document.getElementById('reaction-slider-container').classList.remove('hidden');
                    break;
                case 'regio':
                    document.getElementById('inverse-container').classList.remove('hidden');
                    document.getElementById('focus-container').classList.remove('hidden');
                    document.getElementById('reaction-slider-container').classList.remove('hidden');
                    document.getElementById('erg-ewg-slider-container').classList.remove('hidden');
                    break;
                case 'stereo-phile':
                case 'stereo-diene':
                    document.getElementById('focus-container').classList.remove('hidden');
                    break;
                case 'homo':
                    document.getElementById('inverse-container').classList.remove('hidden');
                    document.getElementById('erg-ewg-slider-container').classList.remove('hidden');
                    break;
            }
        }
    </script>
