<body>
    <div id="demo-container">
        <!-- Section 1: Control Panel -->
        <div id="control-panel">
            <label>step</label>
            <input type="radio" id="step1" name="step-selector" value="1" checked>
            <label for="step1">1</label>
            <input type="radio" id="step2" name="step-selector" value="2">
            <label for="step2">2</label>
            <input type="radio" id="step3" name="step-selector" value="3">
            <label for="step3">3</label>
        </div>
        <!-- Section 2: Visualization Area -->
        <div id="canvas-container">
            <!-- p5.js canvas will be created here -->
        </div>
    </div>
    <script>
        let currentStep = 1;
        let p;
        let triangleVertices = [];
        let canvasWidth = 600;
        let canvasHeight = 520;
        let isDragging = false;
        let originalP = {x: 0, y: 0};

        function setup() {
            let canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');

            // Define the equilateral triangle vertices
            let sideLength = Math.min(canvasWidth * 0.8, canvasHeight * 0.8);
            let centerX = canvasWidth / 2;
            let centerY = canvasHeight / 2 + 50;

            triangleVertices = [
                {x: centerX - sideLength/2, y: centerY + (sideLength * Math.sqrt(3)/2)/2},
                {x: centerX + sideLength/2, y: centerY + (sideLength * Math.sqrt(3)/2)/2},
                {x: centerX, y: centerY - (sideLength * Math.sqrt(3)/2)/2}
            ];

            // Initialize point P inside the triangle
            p = {
                x: canvasWidth * 0.4,
                y: canvasHeight * 0.6
            };
            originalP = {...p};

            // Set up radio button event listeners
            document.querySelectorAll('input[name="step-selector"]').forEach(radio => {
                radio.addEventListener('change', function() {
                    currentStep = parseInt(this.value);
                    redraw();
                });
            });
        }

        function draw() {
            background(255);

            // Draw the main equilateral triangle
            drawMainTriangle();

            // Draw the main altitude
            drawMainAltitude();

            // Draw based on current step
            if (currentStep === 1) {
                drawStep1();
            } else if (currentStep === 2) {
                drawStep2();
            } else if (currentStep === 3) {
                drawStep3();
            }

            // Draw point P (or its ghost in step 3)
            drawPointP();
        }

        function drawMainTriangle() {
            stroke(0);
            strokeWeight(2);
            noFill();
            triangle(
                triangleVertices[0].x, triangleVertices[0].y,
                triangleVertices[1].x, triangleVertices[1].y,
                triangleVertices[2].x, triangleVertices[2].y
            );

            // Label vertices
            textSize(16);
            fill(0);
            noStroke();
            text('A', triangleVertices[0].x - 20, triangleVertices[0].y + 5);
            text('B', triangleVertices[1].x + 10, triangleVertices[1].y + 5);
            text('C', triangleVertices[2].x - 5, triangleVertices[2].y - 10);
        }

        function drawMainAltitude() {
            stroke(100, 200, 255);
            strokeWeight(1);
            drawingContext.setLineDash([5, 5]);
            line(
                triangleVertices[2].x, triangleVertices[2].y,
                (triangleVertices[0].x + triangleVertices[1].x) / 2,
                (triangleVertices[0].y + triangleVertices[1].y) / 2
            );
            drawingContext.setLineDash([]);
        }

        function drawPointP() {
            if (currentStep === 3) {
                // Draw ghost of original P
                fill(200, 200, 200, 150);
                stroke(150, 150, 150, 150);
                strokeWeight(1);
                ellipse(originalP.x, originalP.y, 10, 10);
                line(originalP.x - 6, originalP.y, originalP.x + 6, originalP.y);
                line(originalP.x, originalP.y - 6, originalP.x, originalP.y + 6);

                fill(255, 0, 0, 150);
                textSize(12);
                text('P', originalP.x + 8, originalP.y + 5);
            }

            // Draw current P (unless we're in step 3 and dragging)
            if (currentStep !== 3 || isDragging) {
                fill(150);
                stroke(0);
                strokeWeight(1);
                ellipse(p.x, p.y, 10, 10);
                line(p.x - 6, p.y, p.x + 6, p.y);
                line(p.x, p.y - 6, p.x, p.y + 6);

                fill(255, 0, 0);
                textSize(12);
                text('P', p.x + 8, p.y + 5);
            }
        }

        function drawStep1() {
            // Calculate perpendicular distances and feet
            let abFoot = getFootOfPerpendicular(p, triangleVertices[0], triangleVertices[1]);
            let bcFoot = getFootOfPerpendicular(p, triangleVertices[1], triangleVertices[2]);
            let acFoot = getFootOfPerpendicular(p, triangleVertices[0], triangleVertices[2]);

            // Draw perpendicular lines
            stroke(0);
            strokeWeight(1);
            line(p.x, p.y, abFoot.x, abFoot.y);
            line(p.x, p.y, bcFoot.x, bcFoot.y);
            line(p.x, p.y, acFoot.x, acFoot.y);

            // Draw feet points
            fill(0, 0, 255);
            noStroke();
            ellipse(abFoot.x, abFoot.y, 6, 6);
            ellipse(bcFoot.x, bcFoot.y, 6, 6);
            ellipse(acFoot.x, acFoot.y, 6, 6);
        }

        function drawStep2() {
            // Get perpendicular distances
            let dAB = distanceToLine(p, triangleVertices[0], triangleVertices[1]);
            let dBC = distanceToLine(p, triangleVertices[1], triangleVertices[2]);
            let dAC = distanceToLine(p, triangleVertices[0], triangleVertices[2]);

            // Calculate side lengths for small triangles
            let sideAB = dAB * 2 / Math.sqrt(3);
            let sideBC = dBC * 2 / Math.sqrt(3);
            let sideAC = dAC * 2 / Math.sqrt(3);

            // Draw red triangle (AB)
            fill(255, 0, 0, 100);
            stroke(255, 0, 0);
            strokeWeight(1);
            drawEquilateralTriangle(p, sideAB, triangleVertices[0], triangleVertices[1]);

            // Draw blue triangle (BC)
            fill(0, 0, 255, 100);
            stroke(0, 0, 255);
            strokeWeight(1);
            drawEquilateralTriangle(p, sideBC, triangleVertices[1], triangleVertices[2]);
            let bcFoot = getFootOfPerpendicular(p, triangleVertices[1], triangleVertices[2]);
            fill(0);
            noStroke();
            text('y', bcFoot.x + 5, bcFoot.y + 5);

            // Draw green triangle (AC)
            fill(0, 255, 0, 100);
            stroke(0, 255, 0);
            strokeWeight(1);
            drawEquilateralTriangle(p, sideAC, triangleVertices[0], triangleVertices[2]);
            let acFoot = getFootOfPerpendicular(p, triangleVertices[0], triangleVertices[2]);
            fill(0);
            noStroke();
            text('x', acFoot.x + 5, acFoot.y + 5);
        }

        function drawStep3() {
            // Get perpendicular distances
            let dAB = distanceToLine(p, triangleVertices[0], triangleVertices[1]);
            let dBC = distanceToLine(p, triangleVertices[1], triangleVertices[2]);
            let dAC = distanceToLine(p, triangleVertices[0], triangleVertices[2]);

            // Calculate side lengths for small triangles
            let sideAB = dAB * 2 / Math.sqrt(3);
            let sideBC = dBC * 2 / Math.sqrt(3);
            let sideAC = dAC * 2 / Math.sqrt(3);

            // Main altitude length
            let mainAltitude = distanceToLine(
                triangleVertices[2], 
                triangleVertices[0], 
                triangleVertices[1]
            );

            // Base position (AB)
            let baseY = triangleVertices[0].y;
            let midX = (triangleVertices[0].x + triangleVertices[1].x) / 2;

            // Draw red triangle (AB)
            fill(255, 0, 0, 100);
            stroke(255, 0, 0);
            strokeWeight(1);
            let redTopY = baseY - dAB;
            triangle(
                midX - sideAB/2, baseY,
                midX + sideAB/2, baseY,
                midX, redTopY
            );

            // Draw blue triangle (BC) on top of red
            fill(0, 0, 255, 100);
            stroke(0, 0, 255);
            strokeWeight(1);
            let blueTopY = redTopY - dBC;
            triangle(
                midX - sideBC/2, redTopY,
                midX + sideBC/2, redTopY,
                midX, blueTopY
            );

            // Label y
            fill(0);
            noStroke();
            text('y', midX + 5, redTopY - dBC/2);

            // Draw green triangle (AC) on top of blue
            fill(0, 255, 0, 100);
            stroke(0, 255, 0);
            strokeWeight(1);
            let greenTopY = blueTopY - dAC;
            triangle(
                midX - sideAC/2, blueTopY,
                midX + sideAC/2, blueTopY,
                midX, greenTopY
            );

            // Check if sum equals main altitude
            let sum = dAB + dBC + dAC;
            let diff = Math.abs(sum - mainAltitude);

            // Display the sum (for educational purposes)
            fill(0);
            noStroke();
            textSize(14);
            text(`Sum of distances: ${sum.toFixed(2)}`, 20, 30);
            text(`Main altitude: ${mainAltitude.toFixed(2)}`, 20, 50);

            if (diff < 0.1) {
                fill(0, 150, 0);
                text("Sum equals main altitude!", 20, 70);
            }
        }

        function drawEquilateralTriangle(p, sideLength, lineP1, lineP2) {
            // Calculate angle of the base line
            let baseAngle = atan2(lineP2.y - lineP1.y, lineP2.x - lineP1.x);

            // Calculate vertices
            let angle1 = baseAngle + PI; // Opposite direction
            let angle2 = baseAngle + PI/3;
            let angle3 = baseAngle - PI/3;

            let x2 = p.x + sideLength * cos(angle2);
            let y2 = p.y + sideLength * sin(angle2);
            let x3 = p.x + sideLength * cos(angle3);
            let y3 = p.y + sideLength * sin(angle3);

            triangle(p.x, p.y, x2, y2, x3, y3);
        }

        function mousePressed() {
            // Check if mouse is near point P
            if (dist(mouseX, mouseY, p.x, p.y) < 15) {
                isDragging = true;
                if (currentStep === 3) {
                    originalP = {...p};
                }
            }
        }

        function mouseDragged() {
            if (isDragging) {
                // Update P's position
                p.x = mouseX;
                p.y = mouseY;

                // Constrain P to be inside the triangle
                if (!isPointInTriangle(p, triangleVertices[0], triangleVertices[1], triangleVertices[2])) {
                    // Find closest point on triangle boundary
                    p = constrainToTriangle(p, triangleVertices[0], triangleVertices[1], triangleVertices[2]);
                }

                redraw();
            }
        }

        function mouseReleased() {
            isDragging = false;
            if (currentStep === 3) {
                originalP = {...p};
            }
            redraw();
        }

        // Helper functions
        function getFootOfPerpendicular(p, lineP1, lineP2) {
            let x1 = lineP1.x;
            let y1 = lineP1.y;
            let x2 = lineP2.x;
            let y2 = lineP2.y;

            let A = p.x - x1;
            let B = p.y - y1;
            let C = x2 - x1;
            let D = y2 - y1;

            let dot = A * C + B * D;
            let len_sq = C * C + D * D;
            let param = (len_sq !== 0) ? dot / len_sq : -1;

            let xx, yy;

            if (param < 0) {
                xx = x1;
                yy = y1;
            } else if (param > 1) {
                xx = x2;
                yy = y2;
            } else {
                xx = x1 + param * C;
                yy = y1 + param * D;
            }

            return {x: xx, y: yy};
        }

        function distanceToLine(p, lineP1, lineP2) {
            let foot = getFootOfPerpendicular(p, lineP1, lineP2);
            return dist(p.x, p.y, foot.x, foot.y);
        }

        function isPointInTriangle(p, a, b, c) {
            function sign(p1, p2, p3) {
                return (p1.x - p3.x) * (p2.y - p3.y) - (p2.x - p3.x) * (p1.y - p3.y);
            }

            let d1 = sign(p, a, b);
            let d2 = sign(p, b, c);
            let d3 = sign(p, c, a);

            let has_neg = (d1 < 0) || (d2 < 0) || (d3 < 0);
            let has_pos = (d1 > 0) || (d2 > 0) || (d3 > 0);

            return !(has_neg && has_pos);
        }

        function constrainToTriangle(p, a, b, c) {
            // Check each edge and find closest point
            let ab = closestPointOnLine(p, a, b);
            let bc = closestPointOnLine(p, b, c);
            let ac = closestPointOnLine(p, a, c);

            let dAB = dist(p.x, p.y, ab.x, ab.y);
            let dBC = dist(p.x, p.y, bc.x, bc.y);
            let dAC = dist(p.x, p.y, ac.x, ac.y);

            let minDist = Math.min(dAB, dBC, dAC);

            if (minDist === dAB) return ab;
            if (minDist === dBC) return bc;
            return ac;
        }

        function closestPointOnLine(p, lineP1, lineP2) {
            let x1 = lineP1.x;
            let y1 = lineP1.y;
            let x2 = lineP2.x;
            let y2 = lineP2.y;

            let A = p.x - x1;
            let B = p.y - y1;
            let C = x2 - x1;
            let D = y2 - y1;

            let dot = A * C + B * D;
            let len_sq = C * C + D * D;
            let param = (len_sq !== 0) ? dot / len_sq : -1;

            let xx, yy;

            if (param < 0) {
                xx = x1;
                yy = y1;
            } else if (param > 1) {
                xx = x2;
                yy = y2;
            } else {
                xx = x1 + param * C;
                yy = y1 + param * D;
            }

            return {x: xx, y: yy};
        }
    </script>
