    <script>
        // Orbital elements data (J2000 epoch)
        const orbitalElements = {
            Mercury: { a: 0.3871, e: 0.2056, i: 7.005, Ω: 48.331, ω: 29.124, M: 174.796 },
            Venus: { a: 0.7233, e: 0.0068, i: 3.395, Ω: 76.680, ω: 54.884, M: 50.416 },
            Earth: { a: 1.0000, e: 0.0167, i: 0.000, Ω: -11.260, ω: 114.208, M: 358.617 },
            Mars: { a: 1.5237, e: 0.0934, i: 1.850, Ω: 49.562, ω: 286.502, M: 19.412 },
            Ceres: { a: 2.7691, e: 0.0760, i: 10.593, Ω: 80.393, ω: 73.598, M: 77.372 },
            Pluto: { a: 39.482, e: 0.2488, i: 17.160, Ω: 110.299, ω: 113.834, M: 14.882 },
            Haumea: { a: 43.132, e: 0.1912, i: 28.220, Ω: 122.155, ω: 239.544, M: 204.03 },
            Makemake: { a: 45.791, e: 0.1559, i: 29.006, Ω: 79.623, ω: 294.821, M: 95.895 },
            Eris: { a: 67.668, e: 0.4418, i: 44.187, Ω: 35.951, ω: 151.291, M: 206.51 }
        };

        // Three.js scene setup
        const canvas = document.getElementById('3d-canvas');
        const renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
        renderer.setClearColor(0xf0f0f0);

        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(45, canvas.clientWidth / canvas.clientHeight, 0.1, 1000);
        camera.position.set(0, 15, 30);

        const controls = new THREE.OrbitControls(camera, canvas);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);

        const sunLight = new THREE.PointLight(0xffffff, 1, 100);
        sunLight.position.set(0, 0, 0);
        scene.add(sunLight);

        // Celestial bodies
        const bodies = {};
        const orbits = {};
        const dwarfPlanetOrbit = { line: null, points: [] };

        // Create the Sun
        const sunGeometry = new THREE.SphereGeometry(0.5, 32, 32);
        const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
        const sun = new THREE.Mesh(sunGeometry, sunMaterial);
        scene.add(sun);

        // Create planets and their orbits
        const planetColors = {
            Mercury: 0xaaaaaa,
            Venus: 0xe6c229,
            Earth: 0x3498db,
            Mars: 0xe74c3c
        };

        Object.keys(orbitalElements).forEach(body => {
            if (['Mercury', 'Venus', 'Earth', 'Mars'].includes(body)) {
                // Create planet
                const geometry = new THREE.SphereGeometry(0.1, 32, 32);
                const material = new THREE.MeshLambertMaterial({ color: planetColors[body] });
                const planet = new THREE.Mesh(geometry, material);
                bodies[body] = planet;
                scene.add(planet);

                // Create orbit path
                createOrbitPath(body, 0x3498db);
            }
        });

        // Initial dwarf planet (Pluto)
        let selectedDwarfPlanet = 'Pluto';
        createDwarfPlanet(selectedDwarfPlanet);
        createDwarfPlanetOrbit(selectedDwarfPlanet);

        // Animation state
        let isAnimating = false;
        let animationId = null;
        const startDate = new Date('2000-01-01T00:00:00Z');

        // Event listeners
        document.getElementById('dwarf-planet-selector').addEventListener('click', (e) => {
            if (e.target.tagName === 'BUTTON') {
                const planet = e.target.dataset.planet;
                if (planet && planet !== selectedDwarfPlanet) {
                    // Update UI
                    document.querySelectorAll('#dwarf-planet-selector button').forEach(btn => {
                        btn.classList.remove('active');
                    });
                    e.target.classList.add('active');

                    // Update scene
                    selectedDwarfPlanet = planet;
                    scene.remove(bodies[selectedDwarfPlanet]);
                    scene.remove(dwarfPlanetOrbit.line);

                    createDwarfPlanet(selectedDwarfPlanet);
                    createDwarfPlanetOrbit(selectedDwarfPlanet);

                    // Update positions
                    updateAllPositions();
                }
            }
        });

        document.getElementById('time-slider').addEventListener('input', (e) => {
            updateDateDisplay();
            updateAllPositions();
        });

        document.getElementById('play-pause-button').addEventListener('click', () => {
            isAnimating = !isAnimating;
            const button = document.getElementById('play-pause-button');

            if (isAnimating) {
                button.textContent = '⏸';
                animate();
            } else {
                button.textContent = '▶';
                cancelAnimationFrame(animationId);
            }
        });

        // Handle window resize
        window.addEventListener('resize', () => {
            camera.aspect = canvas.clientWidth / canvas.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight, false);
        });

        // Functions
        function createOrbitPath(body, color) {
            const elements = orbitalElements[body];
            const curve = new THREE.EllipseCurve(
                0, 0,                            // center
                elements.a, elements.a * Math.sqrt(1 - elements.e * elements.e), // xRadius, yRadius
                0, 2 * Math.PI,                  // startAngle, endAngle
                false,                           // clockwise
                0                                // rotation
            );

            const points = curve.getPoints(200);
            const geometry = new THREE.BufferGeometry().setFromPoints(points);

            // Rotate the orbit to match inclination and longitude of ascending node
            const rotationMatrix = new THREE.Matrix4();
            rotationMatrix.makeRotationFromEuler(
                new THREE.Euler(
                    THREE.MathUtils.degToRad(elements.i),
                    THREE.MathUtils.degToRad(elements.Ω),
                    THREE.MathUtils.degToRad(elements.ω),
                    'ZXY'
                )
            );
            geometry.applyMatrix4(rotationMatrix);

            const material = new THREE.LineBasicMaterial({ color });
            const line = new THREE.Line(geometry, material);
            orbits[body] = { line, points };
            scene.add(line);
        }

        function createDwarfPlanet(planet) {
            const geometry = new THREE.SphereGeometry(0.15, 32, 32);
            const material = new THREE.MeshLambertMaterial({ color: 0x9b59b6 });
            const dwarfPlanet = new THREE.Mesh(geometry, material);
            bodies[planet] = dwarfPlanet;
            scene.add(dwarfPlanet);
        }

        function createDwarfPlanetOrbit(planet) {
            const elements = orbitalElements[planet];
            const curve = new THREE.EllipseCurve(
                0, 0,                            // center
                elements.a, elements.a * Math.sqrt(1 - elements.e * elements.e), // xRadius, yRadius
                0, 2 * Math.PI,                  // startAngle, endAngle
                false,                           // clockwise
                0                                // rotation
            );

            dwarfPlanetOrbit.points = curve.getPoints(200);
            const geometry = new THREE.BufferGeometry().setFromPoints(dwarfPlanetOrbit.points);

            // Rotate the orbit to match inclination and longitude of ascending node
            const rotationMatrix = new THREE.Matrix4();
            rotationMatrix.makeRotationFromEuler(
                new THREE.Euler(
                    THREE.MathUtils.degToRad(elements.i),
                    THREE.MathUtils.degToRad(elements.Ω),
                    THREE.MathUtils.degToRad(elements.ω),
                    'ZXY'
                )
            );
            geometry.applyMatrix4(rotationMatrix);

            const material = new THREE.LineBasicMaterial({ color: 0xe74c3c });
            dwarfPlanetOrbit.line = new THREE.Line(geometry, material);
            scene.add(dwarfPlanetOrbit.line);
        }

        function calculatePosition(date, elements) {
            // Days since J2000 epoch
            const d = (date - startDate) / (1000 * 60 * 60 * 24);

            // Calculate mean anomaly (degrees)
            const P = 365.25 * Math.pow(elements.a, 1.5); // Orbital period in days
            const M = (elements.M + 360 * d / P) % 360;
            const M_rad = THREE.MathUtils.degToRad(M);

            // Solve Kepler's equation for eccentric anomaly (E)
            let E = M_rad;
            let delta = 1;
            const epsilon = 1e-6;
            let iterations = 0;

            while (Math.abs(delta) > epsilon && iterations < 100) {
                delta = (E - elements.e * Math.sin(E) - M_rad) / (1 - elements.e * Math.cos(E));
                E -= delta;
                iterations++;
            }

            // Calculate true anomaly (ν)
            const ν = 2 * Math.atan2(
                Math.sqrt(1 + elements.e) * Math.sin(E / 2),
                Math.sqrt(1 - elements.e) * Math.cos(E / 2)
            );

            // Calculate heliocentric distance
            const r = elements.a * (1 - elements.e * Math.cos(E));

            // Position in orbital plane
            const x_orb = r * Math.cos(ν);
            const y_orb = r * Math.sin(ν);

            // Transform to ecliptic coordinates
            const Ω_rad = THREE.MathUtils.degToRad(elements.Ω);
            const ω_rad = THREE.MathUtils.degToRad(elements.ω);
            const i_rad = THREE.MathUtils.degToRad(elements.i);

            const cosΩ = Math.cos(Ω_rad);
            const sinΩ = Math.sin(Ω_rad);
            const cosω = Math.cos(ω_rad);
            const sinω = Math.sin(ω_rad);
            const cosi = Math.cos(i_rad);
            const sini = Math.sin(i_rad);

            const x = (cosΩ * cosω - sinΩ * sinω * cosi) * x_orb + 
                     (-cosΩ * sinω - sinΩ * cosω * cosi) * y_orb;

            const y = (sinΩ * cosω + cosΩ * sinω * cosi) * x_orb + 
                     (-sinΩ * sinω + cosΩ * cosω * cosi) * y_orb;

            const z = (sinω * sini) * x_orb + (cosω * sini) * y_orb;

            return new THREE.Vector3(x, y, z);
        }

        function updateAllPositions() {
            const slider = document.getElementById('time-slider');
            const days = parseInt(slider.value);
            const currentDate = new Date(startDate.getTime() + days * 24 * 60 * 60 * 1000);

            // Update planet positions
            Object.keys(bodies).forEach(body => {
                if (body in orbitalElements) {
                    const position = calculatePosition(currentDate, orbitalElements[body]);
                    bodies[body].position.copy(position);
                }
            });

            render();
        }

        function updateDateDisplay() {
            const slider = document.getElementById('time-slider');
            const days = parseInt(slider.value);
            const currentDate = new Date(startDate.getTime() + days * 24 * 60 * 60 * 1000);

            const options = { weekday: 'short', year: 'numeric', month: 'short', day: 'numeric' };
            const dateStr = currentDate.toLocaleDateString('en-US', {
                weekday: 'short',
                month: 'short',
                day: 'numeric',
                year: 'numeric',
                hour: '2-digit',
                minute: '2-digit',
                second: '2-digit',
                hour12: false
            }).replace(',', '');

            document.getElementById('date-display').textContent = dateStr;
        }

        function animate() {
            const slider = document.getElementById('time-slider');
            let value = parseInt(slider.value) + 2;

            if (value > parseInt(slider.max)) {
                value = parseInt(slider.min);
            }

            slider.value = value;
            updateDateDisplay();
            updateAllPositions();

            if (isAnimating) {
                animationId = requestAnimationFrame(animate);
            }
        }

        function render() {
            controls.update();
            renderer.render(scene, camera);
        }

        // Initial render
        updateAllPositions();
        render();
    </script>
</body>
</html>
