<body>
    <div id="app-container">
        <div id="control-panel">
            <button id="btn-reset">+</button>

            <div class="control-group">
                <label for="slider-moon-pos">position of moon</label>
                <input type="range" id="slider-moon-pos" min="0" max="360" value="144" step="1">
            </div>

            <div class="control-group">
                <label for="slider-moon-pull">pull from moon</label>
                <input type="range" id="slider-moon-pull" min="0" max="100" value="40" step="1">
            </div>

            <div class="control-group">
                <label for="slider-sun-pos">position of sun</label>
                <input type="range" id="slider-sun-pos" min="0" max="360" value="144" step="1">
            </div>

            <div class="control-group">
                <label for="slider-sun-pull">pull from sun</label>
                <input type="range" id="slider-sun-pull" min="0" max="100" value="20" step="1">
            </div>
        </div>

        <div id="viz-container"></div>
    </div>

    <script>
        let earthRadius = 180;
        let baseWaterRadius = 190;
        let scalingFactor = 0.5;
        let canvasSize = 500;

        function setup() {
            let canvas = createCanvas(canvasSize, canvasSize);
            canvas.parent('viz-container');
            noLoop();

            // Set up event listeners for sliders
            document.getElementById('slider-moon-pos').addEventListener('input', () => redraw());
            document.getElementById('slider-moon-pull').addEventListener('input', () => redraw());
            document.getElementById('slider-sun-pos').addEventListener('input', () => redraw());
            document.getElementById('slider-sun-pull').addEventListener('input', () => redraw());

            // Set up reset button
            document.getElementById('btn-reset').addEventListener('click', () => {
                document.getElementById('slider-moon-pos').value = 144;
                document.getElementById('slider-moon-pull').value = 40;
                document.getElementById('slider-sun-pos').value = 144;
                document.getElementById('slider-sun-pull').value = 20;
                redraw();
            });
        }

        function draw() {
            background(255);
            translate(width / 2, height / 2);

            // Get current slider values
            let moonAngle = parseInt(document.getElementById('slider-moon-pos').value);
            let moonPull = parseInt(document.getElementById('slider-moon-pull').value);
            let sunAngle = parseInt(document.getElementById('slider-sun-pos').value);
            let sunPull = parseInt(document.getElementById('slider-sun-pull').value);

            // Convert angles to radians
            let moonAngleRad = radians(moonAngle);
            let sunAngleRad = radians(sunAngle);

            // Draw water layer
            fill('#aed6f1');
            stroke(0);
            strokeWeight(1);

            beginShape();
            for (let thetaDeg = 0; thetaDeg <= 360; thetaDeg += 1) {
                let theta = radians(thetaDeg);

                // Calculate tidal effects
                let moonEffect = moonPull * pow(cos(theta - moonAngleRad), 2);
                let sunEffect = sunPull * pow(cos(theta - sunAngleRad), 2);
                let totalRadius = baseWaterRadius + scalingFactor * (moonEffect + sunEffect);

                let x = totalRadius * cos(theta);
                let y = totalRadius * sin(theta);
                vertex(x, y);
            }
            endShape(CLOSE);

            // Draw Earth
            noStroke();
            fill('#966F33');
            ellipse(0, 0, earthRadius * 2, earthRadius * 2);

            // Optional: Draw markers for moon and sun positions
            drawCelestialMarker(moonAngle, '#888', 'Moon');
            drawCelestialMarker(sunAngle, '#FFD700', 'Sun');
        }

        function drawCelestialMarker(angle, color, label) {
            push();
            let r = baseWaterRadius + 30;
            let theta = radians(angle);
            let x = r * cos(theta);
            let y = r * sin(theta);

            fill(color);
            ellipse(x, y, 10, 10);

            // Draw label
            textSize(12);
            textAlign(CENTER, CENTER);
            fill(0);
            text(label, x, y - 20);
            pop();
        }
    </script>
</body>
