    <script>
        // Initialize Three.js scenes, cameras and renderers
        let orbitScene, orbitCamera, orbitRenderer;
        let telescopicScene, telescopicCamera, telescopicRenderer;
        let cometGroup, earthMesh, sunMesh;
        let orbitLines = {};
        let animationId;

        // Parameters for orbits (semi-major axis, eccentricity)
        const orbitParams = {
            earth: { a: 4, e: 0.0167, color: 0x3498db },
            mars: { a: 6.1, e: 0.0934, color: 0xe74c3c },
            jupiter: { a: 20.8, e: 0.0489, color: 0xe67e22 },
            comet: { a: 14.4, e: 0.432, color: 0xf1c40f }
        };

        // Initialize the visualization
        function init() {
            // Setup orbit diagram scene
            setupOrbitScene();

            // Setup telescopic view scene
            setupTelescopicScene();

            // Create celestial bodies
            createCelestialBodies();

            // Create orbit lines
            createOrbitLines();

            // Add event listeners for sliders
            setupEventListeners();

            // Start animation loop
            animate();
        }

        function setupOrbitScene() {
            orbitScene = new THREE.Scene();
            orbitScene.background = new THREE.Color(0xffffff);

            orbitCamera = new THREE.PerspectiveCamera(45, 1, 0.1, 1000);
            orbitCamera.position.set(0, 20, 20);
            orbitCamera.lookAt(0, 0, 0);

            const orbitCanvas = document.getElementById('orbit-canvas');
            orbitRenderer = new THREE.WebGLRenderer({ canvas: orbitCanvas, antialias: true });

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040);
            orbitScene.add(ambientLight);

            const sunLight = new THREE.PointLight(0xffffff, 1, 100);
            sunLight.position.set(0, 0, 0);
            orbitScene.add(sunLight);
        }

        function setupTelescopicScene() {
            telescopicScene = new THREE.Scene();
            telescopicScene.background = new THREE.Color(0x000000);

            telescopicCamera = new THREE.PerspectiveCamera(15, 1, 0.1, 1000);

            const telescopicCanvas = document.getElementById('telescopic-canvas');
            telescopicRenderer = new THREE.WebGLRenderer({ canvas: telescopicCanvas, antialias: true });

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040);
            telescopicScene.add(ambientLight);
        }

        function createCelestialBodies() {
            // Sun
            const sunGeometry = new THREE.SphereGeometry(0.5, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ 
                color: 0xf39c12,
                emissive: 0xf39c12,
                emissiveIntensity: 1
            });
            sunMesh = new THREE.Mesh(sunGeometry, sunMaterial);
            orbitScene.add(sunMesh);

            // Earth
            const earthGeometry = new THREE.SphereGeometry(0.3, 32, 32);
            const earthMaterial = new THREE.MeshBasicMaterial({ color: 0x3498db });
            earthMesh = new THREE.Mesh(earthGeometry, earthMaterial);
            orbitScene.add(earthMesh);

            // Comet (as a group)
            cometGroup = new THREE.Group();

            // Nucleus
            const nucleusGeometry = new THREE.SphereGeometry(0.2, 32, 32);
            const nucleusMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
            const nucleus = new THREE.Mesh(nucleusGeometry, nucleusMaterial);
            cometGroup.add(nucleus);

            // Coma
            const comaGeometry = new THREE.SphereGeometry(0.5, 32, 32);
            const comaMaterial = new THREE.MeshBasicMaterial({ 
                color: 0x2ecc71,
                transparent: true,
                opacity: 0.5,
                blending: THREE.AdditiveBlending
            });
            const coma = new THREE.Mesh(comaGeometry, comaMaterial);
            coma.scale.set(0.4, 0.4, 0.4); // Initial scale based on default slider value
            cometGroup.add(coma);

            // Tail
            const tailGeometry = new THREE.ConeGeometry(0.3, 1, 32);
            const tailMaterial = new THREE.MeshBasicMaterial({ 
                color: 0x2ecc71,
                transparent: true,
                opacity: 0.4,
                blending: THREE.AdditiveBlending,
                side: THREE.DoubleSide
            });
            const tail = new THREE.Mesh(tailGeometry, tailMaterial);
            tail.position.set(0, 0, -0.5); // Position at the back of the nucleus
            tail.rotation.x = Math.PI / 2; // Rotate to point along Z axis
            tail.scale.set(0.4, 1, 0.4); // Initial scale based on default slider values
            cometGroup.add(tail);

            orbitScene.add(cometGroup);
            telescopicScene.add(cometGroup.clone()); // Add a copy to the telescopic view

            // Set initial positions based on default slider values
            updatePositions();
        }

        function createOrbitLines() {
            const orbitMaterial = new THREE.LineBasicMaterial({ color: 0x888888, linewidth: 1 });

            // Create orbit lines for each body
            for (const [body, params] of Object.entries(orbitParams)) {
                const points = [];
                const steps = 100;

                for (let i = 0; i <= steps; i++) {
                    const angle = (i / steps) * Math.PI * 2;
                    const r = params.a * (1 - params.e * params.e) / (1 + params.e * Math.cos(angle));
                    const x = r * Math.cos(angle);
                    const z = r * Math.sin(angle);
                    points.push(new THREE.Vector3(x, 0, z));
                }

                const geometry = new THREE.BufferGeometry().setFromPoints(points);
                const line = new THREE.Line(geometry, orbitMaterial);
                orbitScene.add(line);
                orbitLines[body] = line;
            }
        }

        function setupEventListeners() {
            const sliders = [
                'slider-earth-position',
                'slider-comet-position',
                'slider-coma',
                'slider-tail',
                'slider-tail-spread'
            ];

            sliders.forEach(id => {
                document.getElementById(id).addEventListener('input', update);
            });

            window.addEventListener('resize', onWindowResize);
        }

        function update() {
            updatePositions();
            updateCometFeatures();
        }

        function updatePositions() {
            // Get current slider values
            const earthAngle = parseFloat(document.getElementById('slider-earth-position').value) * Math.PI / 180;
            const cometAngle = parseFloat(document.getElementById('slider-comet-position').value) * Math.PI / 180;

            // Update Earth position
            const earthR = orbitParams.earth.a * (1 - orbitParams.earth.e * orbitParams.earth.e) / 
                          (1 + orbitParams.earth.e * Math.cos(earthAngle));
            earthMesh.position.set(
                earthR * Math.cos(earthAngle),
                0,
                earthR * Math.sin(earthAngle)
            );

            // Update Comet position
            const cometR = orbitParams.comet.a * (1 - orbitParams.comet.e * orbitParams.comet.e) / 
                           (1 + orbitParams.comet.e * Math.cos(cometAngle));
            cometGroup.position.set(
                cometR * Math.cos(cometAngle),
                0,
                cometR * Math.sin(cometAngle)
            );

            // Update telescopic camera position and target
            telescopicCamera.position.copy(earthMesh.position);
            telescopicCamera.lookAt(cometGroup.position);

            // Update tail orientation to point away from sun
            const tail = cometGroup.children[2];
            const direction = new THREE.Vector3().subVectors(cometGroup.position, sunMesh.position).normalize();
            tail.quaternion.setFromUnitVectors(new THREE.Vector3(0, 0, 1), direction);
        }

        function updateCometFeatures() {
            // Get current slider values
            const comaValue = parseFloat(document.getElementById('slider-coma').value) / 100;
            const tailValue = parseFloat(document.getElementById('slider-tail').value) / 100;
            const tailSpreadValue = parseFloat(document.getElementById('slider-tail-spread').value) / 100;

            // Update coma scale
            const coma = cometGroup.children[1];
            const comaScale = 0.2 + comaValue * 2; // Scale from 0.2 to 2.2
            coma.scale.set(comaScale, comaScale, comaScale);

            // Update tail scale
            const tail = cometGroup.children[2];
            const tailLength = 0.5 + tailValue * 3; // Length from 0.5 to 3.5
            const tailWidth = 0.2 + tailSpreadValue * 1.5; // Width from 0.2 to 1.7
            tail.scale.set(tailWidth, tailLength, tailWidth);

            // Also update the copy in the telescopic view
            const telescopicComet = telescopicScene.children[1];
            if (telescopicComet && telescopicComet.children.length === 3) {
                telescopicComet.children[1].scale.copy(coma.scale);
                telescopicComet.children[2].scale.copy(tail.scale);
                telescopicComet.children[2].quaternion.copy(tail.quaternion);
            }
        }

        function onWindowResize() {
            const container = document.getElementById('visualization-panel');
            const width = container.clientWidth;
            const height = container.clientHeight / 2;

            // Update orbit view
            orbitCamera.aspect = width / height;
            orbitCamera.updateProjectionMatrix();
            orbitRenderer.setSize(width, height);

            // Update telescopic view
            telescopicCamera.aspect = width / height;
            telescopicCamera.updateProjectionMatrix();
            telescopicRenderer.setSize(width, height);
        }

        function animate() {
            animationId = requestAnimationFrame(animate);

            // Render both scenes
            orbitRenderer.render(orbitScene, orbitCamera);
            telescopicRenderer.render(telescopicScene, telescopicCamera);
        }

        // Start the application
        init();
    </script>
</body>
</html>
