<body>
    <div id="app">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-m">m</label>
                <input type="range" id="slider-m" min="0" max="10" step="0.001" value="2.845">
                <span id="label-m">2.845</span>
            </div>
            <div class="control-group">
                <label for="slider-x">x</label>
                <input type="range" id="slider-x" min="0" max="10" step="0.1" value="4.1">
                <span id="label-x">4.1</span>
            </div>
            <div class="control-group">
                <label for="slider-y">y</label>
                <input type="range" id="slider-y" min="0.1" max="5" step="0.001" value="1.498">
                <span id="label-y">1.498</span>
            </div>
        </div>
        <div id="visualization">
            <div id="tensions-display">T₁ = 0.0000 N | T₂ = 0.0000 N</div>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script>
        // Physical constants
        const L = 10;  // Distance between posts
        const g = 9.81;  // Acceleration due to gravity

        // Canvas dimensions
        const canvasWidth = 800;
        const canvasHeight = 500;
        const anchorY = 150;
        const leftAnchorX = 100;
        const rightAnchorX = 700;
        const yScale = 50;  // Pixels per unit for y coordinate

        // p5.js sketch
        let sketch = function(p) {
            let m, x, y;
            let T1, T2, theta1, theta2;

            p.setup = function() {
                let canvas = p.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('canvas-container');
                p.background(255);

                // Initialize values from sliders
                updateValues();
                calculateTensions();
                drawDiagram();
            };

            p.draw = function() {
                updateValues();
                calculateTensions();
                drawDiagram();
            };

            function updateValues() {
                m = parseFloat(document.getElementById('slider-m').value);
                x = parseFloat(document.getElementById('slider-x').value);
                y = parseFloat(document.getElementById('slider-y').value);

                // Update displayed values
                document.getElementById('label-m').textContent = m.toFixed(3);
                document.getElementById('label-x').textContent = x.toFixed(1);
                document.getElementById('label-y').textContent = y.toFixed(3);
            }

            function calculateTensions() {
                // Calculate angles
                theta1 = Math.atan(y / x);
                theta2 = Math.atan(y / (L - x));

                // Calculate gravitational force
                const Fg = m * g;

                // Calculate tensions
                const denominator = Math.sin(theta1 + theta2);
                T1 = (Fg * Math.cos(theta2)) / denominator;
                T2 = (Fg * Math.cos(theta1)) / denominator;

                // Update tensions display
                document.getElementById('tensions-display').textContent = 
                    `T₁ = ${T1.toFixed(4)} N | T₂ = ${T2.toFixed(4)} N`;
            }

            function drawDiagram() {
                p.background(255);

                // Calculate pixel positions
                const massX = p.map(x, 0, L, leftAnchorX, rightAnchorX);
                const massY = anchorY + y * yScale;

                // Draw posts
                p.stroke(0);
                p.strokeWeight(10);
                p.line(leftAnchorX, anchorY - 100, leftAnchorX, anchorY + 50);
                p.line(rightAnchorX, anchorY - 100, rightAnchorX, anchorY + 50);

                // Draw horizontal reference line (dashed)
                p.stroke(150);
                p.strokeWeight(1);
                p.drawingContext.setLineDash([5, 5]);
                p.line(leftAnchorX, anchorY, rightAnchorX, anchorY);
                p.drawingContext.setLineDash([]);

                // Draw ropes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(leftAnchorX, anchorY, massX, massY);
                p.line(rightAnchorX, anchorY, massX, massY);

                // Draw mass
                p.fill(0);
                p.noStroke();
                p.ellipse(massX, massY, 40, 40);

                // Draw angles
                const angleRadius = 50;

                // θ₁ (left angle)
                p.stroke(0);
                p.strokeWeight(1);
                p.noFill();
                p.arc(leftAnchorX, anchorY, angleRadius, angleRadius, 0, theta1);

                // θ₂ (right angle)
                p.arc(rightAnchorX, anchorY, angleRadius, angleRadius, Math.PI - theta2, Math.PI);

                // Draw angle labels
                p.textSize(16);
                p.fill(0);
                p.noStroke();
                p.text('θ₁', leftAnchorX + angleRadius/2 * Math.cos(theta1/2), 
                      anchorY + angleRadius/2 * Math.sin(theta1/2));
                p.text('θ₂', rightAnchorX - angleRadius/2 * Math.cos(theta2/2), 
                      anchorY + angleRadius/2 * Math.sin(theta2/2));

                // Draw tension labels
                const tensionLabelOffset = 20;
                p.text('T₁', (leftAnchorX + massX)/2 - tensionLabelOffset, 
                      (anchorY + massY)/2 - tensionLabelOffset);
                p.text('T₂', (rightAnchorX + massX)/2 + tensionLabelOffset, 
                      (anchorY + massY)/2 - tensionLabelOffset);
            }
        };

        // Initialize p5 sketch
        new p5(sketch);

        // Add event listeners to sliders to trigger redraw
        document.getElementById('slider-m').addEventListener('input', function() {
            document.getElementById('label-m').textContent = parseFloat(this.value).toFixed(3);
        });
        document.getElementById('slider-x').addEventListener('input', function() {
            document.getElementById('label-x').textContent = parseFloat(this.value).toFixed(1);
        });
        document.getElementById('slider-y').addEventListener('input', function() {
            document.getElementById('label-y').textContent = parseFloat(this.value).toFixed(3);
        });
    </script>
</body>
</html>
