        };

        // Three.js variables
        let scene, camera, renderer;
        let mass, spring, damperPiston, damperHousing, base, enclosure;

        // Plotly plots
        let displacementPlot, velocityPlot;

        // Initialize the application
        function init() {
            setupControls();
