            const massGeometry = new THREE.BoxGeometry(0.8, 0.8, 0.8);
            const massMaterial = new THREE.MeshStandardMaterial({ color: 0xFFEB3B });
            mass = new THREE.Mesh(massGeometry, massMaterial);
            mass.position.set(params.x0, 0, 0);
            scene.add(mass);

            // Create spring (red helix)
            const springCurve = new THREE.CatmullRomCurve3([
                new THREE.Vector3(-4, 0, 0),
                new THREE.Vector3(-3, 0, 0.5),
                new THREE.Vector3(-2, 0, -0.5),
                new THREE.Vector3(-1, 0, 0.5),
                new THREE.Vector3(0, 0, 0)
            ]);
            const springGeometry = new THREE.TubeGeometry(springCurve, 100, 0.05, 10, false);
            const springMaterial = new THREE.MeshStandardMaterial({ color: 0xF44336 });
            spring = new THREE.Mesh(springGeometry, springMaterial);
            scene.add(spring);

            // Create damper (blue)
            const damperHousingGeometry = new THREE.CylinderGeometry(0.2, 0.2, 1, 16);
            const damperPistonGeometry = new THREE.CylinderGeometry(0.1, 0.1, 0.5, 16);
            const damperMaterial = new THREE.MeshStandardMaterial({ color: 0x2196F3 });

            damperHousing = new THREE.Mesh(damperHousingGeometry, damperMaterial);
            damperHousing.position.set(-2, 0, 0);
            damperHousing.rotation.z = Math.PI / 2;
            scene.add(damperHousing);

            damperPiston = new THREE.Mesh(damperPistonGeometry, damperMaterial);
            damperPiston.position.set(-1.5, 0, 0);
            damperPiston.rotation.z = Math.PI / 2;
