            }, {
                x: [0],
                y: [0],
                mode: 'markers',
                marker: { color: 'blue', size: 10 }
            }], {
                title: 'Displacement vs Time',
                xaxis: { title: 'Time (s)', range: [0, 10] },
                yaxis: { title: 'Displacement (m)', range: [-3, 3] },
                margin: { t: 30, l: 50, r: 20, b: 50 },
                showlegend: false
            });

            // Velocity plot
            velocityPlot = Plotly.newPlot('plot-velocity', [{
                x: [],
                y: [],
                mode: 'lines',
                line: { color: 'green', width: 2 }
            }, {
                x: [0],
                y: [0],
                mode: 'markers',
                marker: { color: 'green', size: 10 }
            }], {
                title: 'Velocity vs Time',
                xaxis: { title: 'Time (s)', range: [0, 10] },
                yaxis: { title: 'Velocity (m/s)', range: [-5, 5] },
                margin: { t: 30, l: 50, r: 20, b: 50 },
                showlegend: false
            });
        }

        // Calculate the analytical solution for the system
        function calculateSolution() {
            const k_eff = params.k + params.Kp;
            const c_eff = params.c + params.Kd;

            // For simplicity, we'll use a damped harmonic oscillator solution
            // In a real implementation, you would solve the differential equation properly
            solution.t = [];
            solution.x = [];
            solution.v = [];

            const numPoints = 100;
            for (let i = 0; i <= numPoints; i++) {
                const t = i * 10 / numPoints;
                solution.t.push(t);

                // Damped harmonic oscillator solution
                const omega0 = Math.sqrt(k_eff / params.m);
                const zeta = c_eff / (2 * Math.sqrt(k_eff * params.m));

                if (zeta < 1) {
                    // Underdamped case
                    const omega = omega0 * Math.sqrt(1 - zeta*zeta);
                    const A = params.x0;
                    const B = (params.v0 + zeta * omega0 * params.x0) / omega;

                    const expTerm = Math.exp(-zeta * omega0 * t);
                    solution.x.push(expTerm * (A * Math.cos(omega * t) + B * Math.sin(omega * t)));
                    solution.v.push(expTerm * (-zeta * omega0 * (A * Math.cos(omega * t) + B * Math.sin(omega * t)) + 
                                              omega * (-A * Math.sin(omega * t) + B * Math.cos(omega * t))));
                } else if (zeta > 1) {
                    // Overdamped case
                    const alpha1 = -zeta * omega0 + omega0 * Math.sqrt(zeta*zeta - 1);
                    const alpha2 = -zeta * omega0 - omega0 * Math.sqrt(zeta*zeta - 1);

                    const A = (params.v0 - alpha2 * params.x0) / (alpha1 - alpha2);
                    const B = params.x0 - A;

                    solution.x.push(A * Math.exp(alpha1 * t) + B * Math.exp(alpha2 * t));
                    solution.v.push(A * alpha1 * Math.exp(alpha1 * t) + B * alpha2 * Math.exp(alpha2 * t));
                } else {
                    // Critically damped case
                    const A = params.x0;
                    const B = params.v0 + omega0 * params.x0;

                    solution.x.push((A + B * t) * Math.exp(-omega0 * t));
                    solution.v.push((B - omega0 * (A + B * t)) * Math.exp(-omega0 * t));
                }
            }

            // Update plots with new solution
            Plotly.react('plot-displacement', [{
                x: solution.t,
                y: solution.x,
                mode: 'lines',
                line: { color: 'blue', width: 2 }
            }, {
                x: [0],
                y: [solution.x[0]],
                mode: 'markers',
                marker: { color: 'blue', size: 10 }
            }], {
                xaxis: { range: [0, 10] },
                yaxis: { range: [-3, 3] }
            });

            Plotly.react('plot-velocity', [{
                x: solution.t,
                y: solution.v,
                mode: 'lines',
                line: { color: 'green', width: 2 }
            }, {
                x: [0],
                y: [solution.v[0]],
                mode: 'markers',
                marker: { color: 'green', size: 10 }
            }], {
                xaxis: { range: [0, 10] },
                yaxis: { range: [-5, 5] }
            });
        }

        // Update all visuals based on current time
        function updateVisuals() {
            // Find the closest time point in our solution
            const timeIndex = Math.min(Math.floor(params.time * solution.t.length / 10), solution.t.length - 1);
            const currentX = solution.x[timeIndex];
            const currentV = solution.v[timeIndex];

            // Update 3D scene
            mass.position.x = currentX;

            // Scale spring based on displacement
            const springScale = 1 + currentX / 4;
            spring.scale.x = springScale;

            // Update damper piston position
            damperPiston.position.x = -1.5 + currentX / 4;

            // Update plot markers
            Plotly.restyle('plot-displacement', {
                x: [[params.time]],
                y: [[currentX]]
            }, [1]);

            Plotly.restyle('plot-velocity', {
                x: [[params.time]],
                y: [[currentV]]
            }, [1]);

            // Render the scene
            renderer.render(scene, camera);
        }

        // Animation loop
        function animate() {
            if (!params.isRunning) return;

            params.time += 0.016; // Approximate 60fps

            if (params.time > 10) {
                pauseSimulation();
                params.time = 10;
            }

            updateVisuals();
            params.animationFrameId = requestAnimationFrame(animate);
        }

        // Start the simulation
        function startSimulation() {
            if (params.isRunning) return;
            params.isRunning = true;
            animate();
        }

        // Pause the simulation
        function pauseSimulation() {
            params.isRunning = false;
            if (params.animationFrameId) {
                cancelAnimationFrame(params.animationFrameId);
                params.animationFrameId = null;
            }
        }

        // Reset the simulation
        function resetSimulation() {
            pauseSimulation();
            params.time = 0;
            calculateSolution();
            updateVisuals();
        }

        // Initialize the application when the page loads
        window.onload = init;
    </script>
</body>
</html>
