    <script>
        // Simulation state
        let isAnimating = false;
        let t = 0;
        let animationId;
        let p5Instance;
        let displacementPlot, velocityPlot;

        // Physical parameters
        let T = 50;
        let m = [1.0, 1.0, 1.0];
        let x0 = [0.1, 0.1, 0.0];

        // Calculated parameters
        let omega = [0, 0, 0];
        let modes = [[0,0,0], [0,0,0], [0,0,0]];
        let c = [0, 0, 0];

        // Data for plots
        let timeData = [];
        let displacementData = [[], [], []];
        let velocityData = [[], [], []];

        // Initialize the simulation
        function initSimulation() {
            // Get current parameters from sliders
            T = parseFloat(document.getElementById('slider-tension').value);
            m = [
                parseFloat(document.getElementById('slider-m1').value),
                parseFloat(document.getElementById('slider-m2').value),
                parseFloat(document.getElementById('slider-m3').value)
            ];
            x0 = [
                parseFloat(document.getElementById('slider-x1').value),
                parseFloat(document.getElementById('slider-x2').value),
                parseFloat(document.getElementById('slider-x3').value)
            ];

            // Calculate mass matrix M
            const M = numeric.diag(m);

            // Calculate stiffness matrix K
            const K = numeric.mul(T, [[2, -1, 0], [-1, 2, -1], [0, -1, 2]]);

            // Transform to standard eigenvalue problem C = M^(-1/2) * K * M^(-1/2)
            const M_sqrt_inv = numeric.diag(m.map(mi => 1/Math.sqrt(mi)));
            const C = numeric.dot(numeric.dot(M_sqrt_inv, K), M_sqrt_inv);

            // Solve eigenvalue problem C*z = λ*z
            const eig = numeric.eig(C);
            const eigenvalues = eig.lambda.x;
            const eigenvectors = numeric.transpose(eig.E.x);

            // Sort eigenvalues and eigenvectors
            const sortedIndices = [0, 1, 2].sort((a, b) => eigenvalues[a] - eigenvalues[b]);

            // Calculate angular frequencies ω = sqrt(λ)
            omega = sortedIndices.map(i => Math.sqrt(eigenvalues[i]));

            // Calculate normal modes A = M^(-1/2) * z
            modes = sortedIndices.map(i => numeric.dot(M_sqrt_inv, eigenvectors[i]));

            // Normalize modes
            modes.forEach((mode, i) => {
                const norm = Math.sqrt(numeric.dot(mode, numeric.dot(M, mode)));
                modes[i] = numeric.mul(1/norm, mode);
            });

            // Calculate modal participation factors c = A^T * M * y(0)
            c = modes.map(mode => numeric.dot(mode, numeric.dot(M, x0)));

            // Pre-calculate data for plots
            timeData = [];
            displacementData = [[], [], []];
            velocityData = [[], [], []];

            const steps = 100;
            for (let i = 0; i <= steps; i++) {
                const ti = i * 5 / steps;
                timeData.push(ti);

                // Calculate displacement y(t) = Σ cᵢ * Aᵢ * cos(ωᵢ*t)
                let y = [0, 0, 0];
                let v = [0, 0, 0];

                for (let j = 0; j < 3; j++) {
                    const cosTerm = c[j] * Math.cos(omega[j] * ti);
                    const sinTerm = -c[j] * omega[j] * Math.sin(omega[j] * ti);

                    for (let k = 0; k < 3; k++) {
                        y[k] += modes[j][k] * cosTerm;
                        v[k] += modes[j][k] * sinTerm;
                    }
                }

                for (let k = 0; k < 3; k++) {
                    displacementData[k].push(y[k]);
                    velocityData[k].push(v[k]);
                }
            }

            // Update plots
            updatePlots();

            // Reset animation time
            t = 0;

            // Redraw animation
            if (p5Instance) {
                p5Instance.redraw();
            }
        }

        // Calculate current displacement and velocity
        function calculateCurrentState(t) {
            let y = [0, 0, 0];
            let v = [0, 0, 0];

            for (let j = 0; j < 3; j++) {
                const cosTerm = c[j] * Math.cos(omega[j] * t);
                const sinTerm = -c[j] * omega[j] * Math.sin(omega[j] * t);

                for (let k = 0; k < 3; k++) {
                    y[k] += modes[j][k] * cosTerm;
                    v[k] += modes[j][k] * sinTerm;
                }
            }

            return { displacement: y, velocity: v };
        }

        // Update Plotly charts
        function updatePlots() {
            // Displacement plot
            const displacementTraces = [
                { x: timeData, y: displacementData[0], name: 'y₁(t)', line: {color: 'blue'} },
                { x: timeData, y: displacementData[1], name: 'y₂(t)', line: {color: 'orange'} },
                { x: timeData, y: displacementData[2], name: 'y₃(t)', line: {color: 'green'} }
            ];

            const displacementLayout = {
                title: 'displacement',
                xaxis: { title: 'time t', range: [0, 5] },
                shapes: [{
                    type: 'line',
                    xref: 'x',
                    yref: 'paper',
                    x0: t,
                    y0: 0,
                    x1: t,
                    y1: 1,
                    line: { color: 'red', width: 1, dash: 'dot' }
                }]
            };

            displacementPlot = Plotly.newPlot('displacement-plot-container', displacementTraces, displacementLayout);

            // Velocity plot
            const velocityTraces = [
                { x: timeData, y: velocityData[0], name: 'v₁(t)', line: {color: 'blue'} },
                { x: timeData, y: velocityData[1], name: 'v₂(t)', line: {color: 'orange'} },
                { x: timeData, y: velocityData[2], name: 'v₃(t)', line: {color: 'green'} }
            ];

            const velocityLayout = {
                title: 'velocity',
                xaxis: { title: 'time t', range: [0, 5] },
                shapes: [{
                    type: 'line',
                    xref: 'x',
                    yref: 'paper',
                    x0: t,
                    y0: 0,
                    x1: t,
                    y1: 1,
                    line: { color: 'red', width: 1, dash: 'dot' }
                }]
            };

            velocityPlot = Plotly.newPlot('velocity-plot-container', velocityTraces, velocityLayout);
        }

        // Update time marker on plots
        function updateTimeMarker() {
            Plotly.relayout('displacement-plot-container', {
                shapes: [{
                    type: 'line',
                    xref: 'x',
                    yref: 'paper',
                    x0: t,
                    y0: 0,
                    x1: t,
                    y1: 1,
                    line: { color: 'red', width: 1, dash: 'dot' }
                }]
            });

            Plotly.relayout('velocity-plot-container', {
                shapes: [{
                    type: 'line',
                    xref: 'x',
                    yref: 'paper',
                    x0: t,
                    y0: 0,
                    x1: t,
                    y1: 1,
                    line: { color: 'red', width: 1, dash: 'dot' }
                }]
            });
        }

        // Animation loop
        function animate() {
            if (!isAnimating) return;

            t += 0.02;
            if (t > 5) t = 0;

            // Update animation
            if (p5Instance) {
                p5Instance.redraw();
            }

            // Update time marker on plots
            updateTimeMarker();

            animationId = requestAnimationFrame(animate);
        }

        // Setup p5.js sketch
        function setupP5() {
            return new p5((p) => {
                p.setup = function() {
                    const container = document.getElementById('main-animation-container');
                    const canvas = p.createCanvas(container.offsetWidth, container.offsetHeight);
                    canvas.parent('main-animation-container');
                    p.noLoop();
                };

                p.draw = function() {
                    p.background(255);

                    // Draw grid
                    p.stroke(200);
                    p.strokeWeight(1);
                    for (let i = 0; i <= 4; i++) {
                        p.line(i * p.width/4, 0, i * p.width/4, p.height);
                    }
                    for (let i = -1; i <= 1; i += 0.5) {
                        p.line(0, p.map(i, 1, -1, 0, p.height), p.width, p.map(i, 1, -1, 0, p.height));
                    }

                    // Draw axes
                    p.stroke(0);
                    p.strokeWeight(2);
                    p.line(0, p.height/2, p.width, p.height/2); // x-axis

                    // Calculate current state
                    const state = calculateCurrentState(t);
                    const y = state.displacement;

                    // Draw string
                    p.stroke(0);
                    p.strokeWeight(2);
                    p.line(0, p.height/2, p.width/4, p.map(y[0], 1, -1, 0, p.height));
                    p.line(p.width/4, p.map(y[0], 1, -1, 0, p.height), p.width/2, p.map(y[1], 1, -1, 0, p.height));
                    p.line(p.width/2, p.map(y[1], 1, -1, 0, p.height), 3*p.width/4, p.map(y[2], 1, -1, 0, p.height));
                    p.line(3*p.width/4, p.map(y[2], 1, -1, 0, p.height), p.width, p.height/2);

                    // Draw masses
                    p.fill(255, 0, 0);
                    p.noStroke();
                    p.circle(p.width/4, p.map(y[0], 1, -1, 0, p.height), 20 * Math.sqrt(m[0]));
                    p.circle(p.width/2, p.map(y[1], 1, -1, 0, p.height), 20 * Math.sqrt(m[1]));
                    p.circle(3*p.width/4, p.map(y[2], 1, -1, 0, p.height), 20 * Math.sqrt(m[2]));

                    // Draw labels
                    p.fill(0);
                    p.noStroke();
                    p.textSize(12);
                    p.text('m₁', p.width/4 - 15, p.map(y[0], 1, -1, 0, p.height) - 15);
                    p.text('m₂', p.width/2 - 15, p.map(y[1], 1, -1, 0, p.height) - 15);
                    p.text('m₃', 3*p.width/4 - 15, p.map(y[2], 1, -1, 0, p.height) - 15);
                };

                p.windowResized = function() {
                    const container = document.getElementById('main-animation-container');
                    p.resizeCanvas(container.offsetWidth, container.offsetHeight);
                };
            });
        }

        // Event listeners
        document.addEventListener('DOMContentLoaded', function() {
            // Initialize p5.js
            p5Instance = setupP5();

            // Initialize simulation
            initSimulation();

            // Setup event listeners for sliders
            const sliders = [
                'slider-tension', 'slider-x1', 'slider-x2', 'slider-x3',
                'slider-m1', 'slider-m2', 'slider-m3'
            ];

            sliders.forEach(id => {
                document.getElementById(id).addEventListener('input', function() {
                    if (isAnimating) {
                        toggleAnimation();
                    }
                    initSimulation();
                });
            });

            // Play/pause button
            document.getElementById('btn-play-pause').addEventListener('click', toggleAnimation);

            // Reset button
            document.getElementById('btn-reset').addEventListener('click', function() {
                if (isAnimating) {
                    toggleAnimation();
                }
                initSimulation();
            });
        });

        // Toggle animation state
        function toggleAnimation() {
            isAnimating = !isAnimating;
            const btn = document.getElementById('btn-play-pause');

            if (isAnimating) {
                btn.textContent = '❚❚';
                if (p5Instance) p5Instance.loop();
                animate();
            } else {
                btn.textContent = '▶';
                if (p5Instance) p5Instance.noLoop();
                cancelAnimationFrame(animationId);
            }
        }
    </script>
</body>
</html>
