    <script>
        // Global state variables
        let mode = 'move';
        let points = [
            {x: 150, y: 350},
            {x: 250, y: 150},
            {x: 350, y: 250},
            {x: 550, y: 180},
            {x: 480, y: 320},
            {x: 420, y: 400}
        ];
        let draggedPoint = null;
        let algorithmState = {
            step: 0,
            hullPointsIndices: [],
            fullHull: [],
            leftmostPointIndex: null,
            currentPointIndex: null,
            candidateLines: [],
            explanationSteps: []
        };

        // p5.js sketch
        function setup() {
            const canvas = createCanvas(700, 500);
            canvas.parent('canvas-container');
            background(255);
        }

        function draw() {
            background(255);

            // Draw canvas border
            stroke(220);
            noFill();
            rect(0, 0, width-1, height-1);

            // Draw candidate lines (teal)
            if (algorithmState.candidateLines.length > 0) {
                stroke(60, 150, 140);
                strokeWeight(2);
                for (const line of algorithmState.candidateLines) {
                    const p1 = points[line[0]];
                    const p2 = points[line[1]];
                    line(p1.x, p1.y, p2.x, p2.y);
                }
            }

            // Draw hull edges (dark blue)
            if (algorithmState.hullPointsIndices.length > 1) {
                stroke(45, 60, 110);
                strokeWeight(3);
                for (let i = 1; i < algorithmState.hullPointsIndices.length; i++) {
                    const p1 = points[algorithmState.hullPointsIndices[i-1]];
                    const p2 = points[algorithmState.hullPointsIndices[i]];
                    line(p1.x, p1.y, p2.x, p2.y);
                }

                // Draw final edge if back to start
                if (algorithmState.step === 3 && 
                    algorithmState.hullPointsIndices.length === algorithmState.fullHull.length) {
                    const p1 = points[algorithmState.hullPointsIndices[algorithmState.hullPointsIndices.length-1]];
                    const p2 = points[algorithmState.hullPointsIndices[0]];
                    line(p1.x, p1.y, p2.x, p2.y);
                }
            }

            // Draw points
            for (let i = 0; i < points.length; i++) {
                const p = points[i];

                // Determine point color
                if (algorithmState.hullPointsIndices.includes(i)) {
                    fill(45, 60, 110); // Dark blue for hull points
                } else {
                    fill(200, 220, 100); // Light green for other points
                }

                stroke(0);
                ellipse(p.x, p.y, 12, 12);

                // Draw crosshair
                line(p.x - 4, p.y, p.x + 4, p.y);
                line(p.x, p.y - 4, p.x, p.y + 4);
            }
        }

        // Mouse interaction for dragging points
        function mousePressed() {
            if (mode !== 'move') return;

            for (let i = 0; i < points.length; i++) {
                const p = points[i];
                const d = dist(mouseX, mouseY, p.x, p.y);
                if (d < 10) {
                    draggedPoint = i;
                    return;
                }
            }
        }

        function mouseDragged() {
            if (mode !== 'move' || draggedPoint === null) return;

            points[draggedPoint].x = mouseX;
            points[draggedPoint].y = mouseY;
        }

        function mouseReleased() {
            draggedPoint = null;
        }

        // Helper functions for the algorithm
        function findLeftmostPoint() {
            let minX = Infinity;
            let minIndex = 0;

            for (let i = 0; i < points.length; i++) {
                if (points[i].x < minX) {
                    minX = points[i].x;
                    minIndex = i;
                }
            }

            return minIndex;
        }

        function orientation(p, q, r) {
            const val = (q.y - p.y) * (r.x - q.x) - (q.x - p.x) * (r.y - q.y);
            if (val === 0) return 0; // Collinear
            return (val > 0) ? 1 : 2; // Clockwise or counter-clockwise
        }

        function calculateConvexHull() {
            const n = points.length;
            if (n < 3) return [];

            const hull = [];

            // Find the leftmost point
            let l = findLeftmostPoint();
            let p = l;
            let q;

            do {
                hull.push(p);
                q = (p + 1) % n;

                for (let i = 0; i < n; i++) {
                    if (orientation(points[p], points[i], points[q]) === 2) {
                        q = i;
                    }
                }

                p = q;
            } while (p !== l);

            return hull;
        }

        // Button event handlers
        document.getElementById('btn-move').addEventListener('click', function() {
            mode = 'move';
            this.classList.add('active');
            document.getElementById('btn-start-algo').classList.remove('active');

            // Reset algorithm state
            algorithmState = {
                step: 0,
                hullPointsIndices: [],
                fullHull: [],
                leftmostPointIndex: null,
                currentPointIndex: null,
                candidateLines: [],
                explanationSteps: []
            };

            // Hide step controls
            document.getElementById('step-controls').style.display = 'none';

            // Clear explanation
            document.getElementById('explanation-text').textContent = '';
        });

        document.getElementById('btn-start-algo').addEventListener('click', function() {
            mode = 'algorithm';
            this.classList.add('active');
            document.getElementById('btn-move').classList.remove('active');

            // Reset algorithm state
            algorithmState = {
                step: 0,
                hullPointsIndices: [],
                fullHull: calculateConvexHull(),
                leftmostPointIndex: null,
                currentPointIndex: null,
                candidateLines: [],
                explanationSteps: []
            };

            // Show step controls
            document.getElementById('step-controls').style.display = 'block';

            // Enable step 1 buttons
            document.getElementById('btn-step1-explain').disabled = false;
            document.getElementById('btn-step1-do').disabled = false;

            // Clear explanation
            document.getElementById('explanation-text').textContent = '';

            // Generate step 3 buttons
            const step3Actions = document.getElementById('step3-actions');
            step3Actions.innerHTML = '';

            if (algorithmState.fullHull.length > 2) {
                const doButton = document.createElement('button');
                doButton.id = 'btn-step3-do';
                doButton.textContent = 'do it';
                doButton.disabled = true;
                doButton.addEventListener('click', function() {
                    handleStep3Action(0);
                });
                step3Actions.appendChild(doButton);

                for (let i = 0; i < algorithmState.fullHull.length - 2; i++) {
                    const repeatButton = document.createElement('button');
                    repeatButton.id = `btn-step3-repeat-${i}`;
                    repeatButton.textContent = 'repeat';
                    repeatButton.disabled = true;
                    repeatButton.addEventListener('click', function() {
                        handleStep3Action(i + 1);
                    });
                    step3Actions.appendChild(repeatButton);
                }
            }
        });

        document.getElementById('btn-step1-explain').addEventListener('click', function() {
            algorithmState.explanationSteps.push("Step 1. Find the leftmost point.");
            updateExplanationText();
        });

        document.getElementById('btn-step1-do').addEventListener('click', function() {
            algorithmState.leftmostPointIndex = findLeftmostPoint();
            algorithmState.currentPointIndex = algorithmState.leftmostPointIndex;
            algorithmState.hullPointsIndices.push(algorithmState.leftmostPointIndex);
            algorithmState.step = 1;

            // Disable step 1 buttons
            this.disabled = true;
            document.getElementById('btn-step1-explain').disabled = true;

            // Enable step 2 buttons
            document.getElementById('btn-step2-explain').disabled = false;
            document.getElementById('btn-step2-do').disabled = false;
        });

        document.getElementById('btn-step2-explain').addEventListener('click', function() {
            algorithmState.explanationSteps.push("Step 2. Find the point clockwise furthest to the right relative to the leftmost point.");
            updateExplanationText();
        });

        document.getElementById('btn-step2-do').addEventListener('click', function() {
            // Show candidate lines
            algorithmState.candidateLines = [];
            for (let i = 0; i < points.length; i++) {
                if (i !== algorithmState.currentPointIndex) {
                    algorithmState.candidateLines.push([algorithmState.currentPointIndex, i]);
                }
            }

            // After a short delay, show the selected line and point
            setTimeout(() => {
                const nextPointIndex = algorithmState.fullHull[1];
                algorithmState.hullPointsIndices.push(nextPointIndex);
                algorithmState.currentPointIndex = nextPointIndex;
                algorithmState.candidateLines = [];
                algorithmState.step = 2;

                // Disable step 2 buttons
                this.disabled = true;
                document.getElementById('btn-step2-explain').disabled = true;

                // Enable step 3 buttons
                document.getElementById('btn-step3-explain').disabled = false;
                document.getElementById('btn-step3-do').disabled = false;
            }, 1000);
        });

        document.getElementById('btn-step3-explain').addEventListener('click', function() {
            algorithmState.explanationSteps.push("Step 3. Repeat finding the point clockwise furthest to the right relative to the previously found point, until the leftmost point is reached again.");
            updateExplanationText();
        });

        function handleStep3Action(stepIndex) {
            // Show candidate lines
            algorithmState.candidateLines = [];
            for (let i = 0; i < points.length; i++) {
                if (!algorithmState.hullPointsIndices.includes(i) && i !== algorithmState.currentPointIndex) {
                    algorithmState.candidateLines.push([algorithmState.currentPointIndex, i]);
                }
            }

            // After a short delay, show the selected line and point
            setTimeout(() => {
                const nextPointIndex = algorithmState.fullHull[stepIndex + 2];
                algorithmState.hullPointsIndices.push(nextPointIndex);
                algorithmState.currentPointIndex = nextPointIndex;
                algorithmState.candidateLines = [];
                algorithmState.step = 3;

                // Disable the current button
                const buttonId = stepIndex === 0 ? 'btn-step3-do' : `btn-step3-repeat-${stepIndex - 1}`;
                document.getElementById(buttonId).disabled = true;

                // Enable the next button if there are more steps
                if (stepIndex < algorithmState.fullHull.length - 3) {
                    const nextButtonId = `btn-step3-repeat-${stepIndex}`;
                    document.getElementById(nextButtonId).disabled = false;
                }

                // If we've completed the hull, disable all buttons
                if (algorithmState.hullPointsIndices.length === algorithmState.fullHull.length) {
                    const buttons = document.querySelectorAll('#step3-actions button');
                    buttons.forEach(btn => {
                        btn.disabled = true;
                    });
                }
            }, 1000);
        }

        function updateExplanationText() {
            document.getElementById('explanation-text').textContent = 
                algorithmState.explanationSteps.join('\n\n');
        }
    </script>
</body>
</html>
