    <script>
        // Simulation parameters and state
        const state = {
            timeSeries: null,
            plotType: 'fed batch'
        };

        // Initial conditions
        const initialConditions = {
            X: 0.1,    // Biomass [mol/m³]
            Glu: 60,   // Glucose [mol/m³]
            Eta: 0,    // Ethanol [mol/m³]
            E0: 1.0,   // Enzyme E0 [mol/m³]
            E1: 0.0,   // Enzyme E1 [mol/m³]
            E2: 0.0,   // Enzyme E2 [mol/m³]
            V: 1.0     // Volume [m³]
        };

        // Hill coefficient for α calculation
        const n = 4;

        // ODE solver (4th-order Runge-Kutta)
        function rk4(f, y0, t0, t1, dt, params) {
            const result = [];
            let y = [...y0];
            let t = t0;

            while (t <= t1) {
                result.push({t, X: y[0], Glu: y[1], Eta: y[2], E0: y[3], E1: y[4], E2: y[5], V: y[6]});

                const k1 = f(t, y, params);
                const k2 = f(t + dt/2, y.map((yi, i) => yi + dt/2 * k1[i]), params);
                const k3 = f(t + dt/2, y.map((yi, i) => yi + dt/2 * k2[i]), params);
                const k4 = f(t + dt, y.map((yi, i) => yi + dt * k3[i]), params);

                y = y.map((yi, i) => yi + dt/6 * (k1[i] + 2*k2[i] + 2*k3[i] + k4[i]));
                t += dt;
            }

            return result;
        }

        // ODE system for the fed-batch reactor
        function reactorODEs(t, y, params) {
            const [X, Glu, Eta, E0, E1, E2, V] = y;
            const { alphaMax, KI, F, Gin, tFeed } = params;

            // Calculate fraction of respiring biomass (α)
            const alpha = alphaMax * Math.pow(KI, n) / (Math.pow(KI, n) + Math.pow(Glu, n));

            // Growth rates
            const mu_resp = 0.3 * alpha * Glu / (5 + Glu);  // Respiring growth
            const mu_ferm = 0.5 * (1 - alpha) * Glu / (20 + Glu);  // Fermentative growth

            // Substrate consumption rates
            const qGlu_resp = mu_resp / 0.5;  // Yield coefficient for respiration
            const qGlu_ferm = mu_ferm / 0.2;  // Yield coefficient for fermentation
            const qEta = 0.1 * mu_ferm;      // Ethanol production rate

            // Enzyme dynamics
            const kE0 = 0.05;  // Basal enzyme synthesis rate
            const kE1 = 0.3;   // Fermentative enzyme synthesis rate
            const kE2 = 0.2;   // Respirative enzyme synthesis rate
            const dE = 0.1;    // Enzyme degradation rate

            // Feed conditions (only active during feed time)
            const feedActive = t <= tFeed ? 1 : 0;

            // ODEs
            const dXdt = (mu_resp + mu_ferm) * X - feedActive * F * X / V;
            const dGludt = -qGlu_resp * X - qGlu_ferm * X + feedActive * F * (Gin - Glu) / V;
            const dEtadt = qEta * X - feedActive * F * Eta / V;
            const dE0dt = kE0 * X - dE * E0 - feedActive * F * E0 / V;
            const dE1dt = (kE1 * (1 - alpha) + kE0) * X - dE * E1 - feedActive * F * E1 / V;
            const dE2dt = (kE2 * alpha + kE0) * X - dE * E2 - feedActive * F * E2 / V;
            const dVdt = feedActive * F;

            return [dXdt, dGludt, dEtadt, dE0dt, dE1dt, dE2dt, dVdt];
        }

        // Run simulation with current parameters
        function runSimulation() {
            const alphaMax = parseFloat(document.getElementById('slider-alpha-max').value);
            const tMax = parseFloat(document.getElementById('slider-t-max').value);
            const KI = parseFloat(document.getElementById('slider-enzyme-control').value);
            const F = parseFloat(document.getElementById('slider-feed-rate').value);
            const Gin = parseFloat(document.getElementById('slider-substrate-feed-conc').value);
            const tFeed = parseFloat(document.getElementById('slider-feed-time').value);

            const params = {
                alphaMax,
                KI,
                F,
                Gin,
                tFeed
            };

            const y0 = Object.values(initialConditions);
            const dt = 0.1;  // Time step

            state.timeSeries = rk4(reactorODEs, y0, 0, tMax, dt, params);

            // Add α to the time series for plotting
            state.timeSeries.forEach(point => {
                const alpha = alphaMax * Math.pow(KI, n) / (Math.pow(KI, n) + Math.pow(point.Glu, n));
                point.alpha = alpha;
            });

            drawPlots();
        }

        // Draw plots based on current selection
        function drawPlots() {
            if (!state.timeSeries) return;

            const plotType = document.getElementById('select-plot').value;
            state.plotType = plotType;

            if (plotType === 'fed batch') {
                drawFedBatchPlots();
            } else {
                drawAlphaPlots();
            }
        }

        // Draw fed batch plots
        function drawFedBatchPlots() {
            // Top plot: diauxic behavior
            const topTraceX = {
                x: state.timeSeries.map(d => d.t),
                y: state.timeSeries.map(d => d.X),
                name: 'X(t)',
                type: 'scatter',
                line: {color: 'blue'}
            };

            const topTraceGlu = {
                x: state.timeSeries.map(d => d.t),
