                name: 'Glu(t)',
                type: 'scatter',
                line: {color: 'red'}
            };

            const topTraceEta = {
                x: state.timeSeries.map(d => d.t),
                y: state.timeSeries.map(d => d.Eta),
                name: 'Eta(t)',
                type: 'scatter',
                line: {color: 'green'}
            };

            const topLayout = {
                title: 'diauxic behavior',
                xaxis: {title: 'time (h)'},
                yaxis: {title: 'concentration [mol/m³]'},
                margin: {t: 40, l: 60, r: 20, b: 50},
                showlegend: true
            };

            Plotly.react('plot-top', [topTraceX, topTraceGlu, topTraceEta], topLayout);

            // Bottom plot: enzyme concentration
            const bottomTraceE2 = {
                x: state.timeSeries.map(d => d.t),
                y: state.timeSeries.map(d => d.E2),
                name: 'E₂(t)',
                type: 'scatter',
                line: {color: 'black'}
            };

            const bottomTraceE1 = {
                x: state.timeSeries.map(d => d.t),
                y: state.timeSeries.map(d => d.E1),
                name: 'E₁(t)',
                type: 'scatter',
                line: {color: 'magenta'}
            };

            const bottomTraceE0 = {
                x: state.timeSeries.map(d => d.t),
                y: state.timeSeries.map(d => d.E0),
                name: 'E₀(t)',
                type: 'scatter',
                line: {color: 'orange'}
            };

            const bottomLayout = {
                title: 'enzyme concentration',
                xaxis: {title: 'time (h)'},
                yaxis: {title: 'enzyme concentration [mol/m³]'},
                margin: {t: 40, l: 60, r: 20, b: 50},
                showlegend: true
            };

            Plotly.react('plot-bottom', [bottomTraceE2, bottomTraceE1, bottomTraceE0], bottomLayout);
        }

        // Draw α plots
        function drawAlphaPlots() {
            // Top plot: glucose concentration
            const topTraceGlu = {
                x: state.timeSeries.map(d => d.t),
                y: state.timeSeries.map(d => d.Glu),
                name: 'Glu(t)',
                type: 'scatter',
                line: {color: 'red'}
            };

            const topLayout = {
                title: 'glucose concentration',
                xaxis: {title: 'time (h)'},
                yaxis: {title: 'glucose concentration [mol/m³]'},
                margin: {t: 40, l: 60, r: 20, b: 50},
                showlegend: true
            };

            Plotly.react('plot-top', [topTraceGlu], topLayout);

            // Bottom plot: fraction of respiring biomass
            const bottomTraceAlpha = {
                x: state.timeSeries.map(d => d.t),
                y: state.timeSeries.map(d => d.alpha),
                name: 'α',
                type: 'scatter',
                line: {color: 'blue'}
            };

            const bottomLayout = {
                title: 'fraction of respiring biomass',
                xaxis: {title: 'time (h)'},
                yaxis: {title: 'α'},
                margin: {t: 40, l: 60, r: 20, b: 50},
                showlegend: true
            };

            Plotly.react('plot-bottom', [bottomTraceAlpha], bottomLayout);
        }

        // Update slider value displays
        function updateSliderValues() {
            document.getElementById('value-alpha-max').textContent = document.getElementById('slider-alpha-max').value;
            document.getElementById('value-t-min').textContent = document.getElementById('slider-t-min').value;
            document.getElementById('value-t-max').textContent = document.getElementById('slider-t-max').value;
            document.getElementById('value-enzyme-control').textContent = document.getElementById('slider-enzyme-control').value;
            document.getElementById('value-feed-rate').textContent = document.getElementById('slider-feed-rate').value;
            document.getElementById('value-substrate-feed-conc').textContent = document.getElementById('slider-substrate-feed-conc').value;
            document.getElementById('value-feed-time').textContent = document.getElementById('slider-feed-time').value;
        }

        // Event listeners for sliders
        function setupSliderListeners() {
            const sliders = document.querySelectorAll('input[type="range"]');
            sliders.forEach(slider => {
                slider.addEventListener('input', function() {
                    // Update the displayed value
                    const valueSpan = document.getElementById(`value-${this.id}`);
                    if (valueSpan) valueSpan.textContent = this.value;

                    // Special handling for t-max and feed-time relationship
                    if (this.id === 'slider-t-max') {
                        const feedTimeSlider = document.getElementById('slider-feed-time');
                        feedTimeSlider.max = this.value;

                        // If current feed time exceeds new max, cap it
                        if (parseFloat(feedTimeSlider.value) > parseFloat(this.value)) {
                            feedTimeSlider.value = this.value;
                            document.getElementById('value-feed-time').textContent = this.value;
                        }
                    }

                    // Run simulation with updated parameters
                    runSimulation();
                });
            });
        }

        // Event listener for plot selector
        function setupPlotSelectorListener() {
            document.getElementById('select-plot').addEventListener('change', function() {
                drawPlots();
            });
        }

        // Initialize the application
        function init() {
            updateSliderValues();
            setupSliderListeners();
            setupPlotSelectorListener();
            runSimulation();
        }

        // Start the application when the page loads
        window.addEventListener('load', init);
    </script>
</body>
</html>
