    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Initialize Three.js scene
        const canvas = document.getElementById('main-canvas');
        const renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
        renderer.setSize(canvas.clientWidth, canvas.clientHeight);

        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0x000000);

        // Create cameras for different views
        const groundCamera = new THREE.PerspectiveCamera(60, canvas.clientWidth / canvas.clientHeight, 0.1, 1000);
        groundCamera.position.set(0, 1, 5);
        groundCamera.lookAt(0, 0, 0);

        const spaceCamera = new THREE.PerspectiveCamera(60, canvas.clientWidth / canvas.clientHeight, 0.1, 1000);
        spaceCamera.position.set(0, 0, 30);
        spaceCamera.lookAt(0, 0, 0);

        let currentCamera = groundCamera;

        // Set up orbit controls
        const controls = new THREE.OrbitControls(currentCamera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Create scene objects
        const sun = new THREE.Mesh(
            new THREE.SphereGeometry(0.5, 32, 32),
            new THREE.MeshBasicMaterial({ color: 0xffff00 })
        );
        scene.add(sun);

        // Sun light
        const sunLight = new THREE.DirectionalLight(0xffffff, 1);
        sunLight.position.set(0, 0, 0);
        sun.add(sunLight);

        // Ground view objects
        const ground = new THREE.Mesh(
            new THREE.CylinderGeometry(10, 10, 1, 32),
            new THREE.MeshPhongMaterial({ 
                color: 0x00aa00,
                side: THREE.DoubleSide
            })
        );
        ground.rotation.x = Math.PI / 2;
        ground.position.y = -0.5;
        scene.add(ground);

        // Celestial dome
        const celestialDome = new THREE.Mesh(
            new THREE.SphereGeometry(15, 32, 32),
            new THREE.MeshPhongMaterial({
                color: 0x87ceeb,
                transparent: true,
                opacity: 0.3,
                side: THREE.BackSide
            })
        );
        scene.add(celestialDome);

        // Sun path line
        const sunPathLine = new THREE.Line(
            new THREE.BufferGeometry(),
            new THREE.LineBasicMaterial({ color: 0xffff00 })
        );
        scene.add(sunPathLine);

        // Space view objects
        const earth = new THREE.Mesh(
            new THREE.SphereGeometry(5, 32, 32),
            new THREE.MeshPhongMaterial({ color: 0x1a66ff })
        );
        earth.rotation.x = -23.45 * Math.PI / 180;
        scene.add(earth);

        // Earth's axis
        const earthAxis = new THREE.Line(
            new THREE.BufferGeometry().setFromPoints([
                new THREE.Vector3(0, -6, 0),
                new THREE.Vector3(0, 6, 0)
            ]),
            new THREE.LineBasicMaterial({ color: 0xffffff })
        );
        earth.add(earthAxis);

        // Terminator plane
        const terminator = new THREE.Mesh(
            new THREE.PlaneGeometry(20, 20),
            new THREE.MeshPhongMaterial({
                color: 0x000000,
                transparent: true,
                opacity: 0.3,
                side: THREE.DoubleSide
            })
        );
        scene.add(terminator);

        // Latitude parallel
        const latitudeParallel = new THREE.Line(
            new THREE.BufferGeometry(),
            new THREE.LineBasicMaterial({ color: 0xffffff })
        );
        earth.add(latitudeParallel);

        // Day/night segment on parallel
        const dayNightSegment = new THREE.Line(
            new THREE.BufferGeometry(),
            new THREE.LineBasicMaterial({ color: 0xffff00, linewidth: 3 })
        );
        earth.add(dayNightSegment);

        // Constellations
        const constellations = new THREE.Group();
        scene.add(constellations);

        // Sample constellation lines (simplified)
        const createConstellationLine = (points) => {
            const vertices = [];
            points.forEach(p => {
                // Convert spherical coordinates to Cartesian
                const theta = p[0] * Math.PI / 180;
                const phi = p[1] * Math.PI / 180;
                const r = 15;
                vertices.push(new THREE.Vector3(
                    r * Math.sin(theta) * Math.cos(phi),
                    r * Math.sin(theta) * Math.sin(phi),
                    r * Math.cos(theta)
                ));
            });

            const geometry = new THREE.BufferGeometry().setFromPoints(vertices);
            const line = new THREE.Line(
                geometry,
                new THREE.LineBasicMaterial({ color: 0xffffff, transparent: true, opacity: 0.5 })
            );
            constellations.add(line);
        };

        // Add some sample constellations (simplified coordinates)
        createConstellationLine([[30, 45], [35, 50], [40, 45]]);  // Sample constellation 1
        createConstellationLine([[60, 30], [65, 35], [70, 30]]);  // Sample constellation 2

        // Set initial visibility
        ground.visible = true;
        celestialDome.visible = true;
        sunPathLine.visible = true;
        earth.visible = false;
        terminator.visible = false;
        latitudeParallel.visible = false;
        dayNightSegment.visible = false;
        constellations.visible = false;

        // State variables
        let needsRender = true;
        const dayNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
        const monthDays = [31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];

        // Helper functions
        function getMonthAndDay(dayOfYear) {
            let month = 0;
            let day = dayOfYear;

            while (day > monthDays[month]) {
                day -= monthDays[month];
                month++;
            }

            return { month: dayNames[month], day: day };
        }

        function formatTime(hours) {
            const h = Math.floor(hours);
            const m = Math.floor((hours - h) * 60);
            return `${h.toString().padStart(2, '0')}:${m.toString().padStart(2, '0')}`;
        }

        function calculateSunPosition(date, latitude, time) {
            // Simplified astronomical calculations
            const declination = 23.45 * Math.sin(2 * Math.PI * (date - 81) / 365);
            const hourAngle = 15 * (time - 12);

            const phi = latitude * Math.PI / 180;
            const delta = declination * Math.PI / 180;
            const h = hourAngle * Math.PI / 180;

            const altitude = Math.asin(
                Math.sin(phi) * Math.sin(delta) + 
                Math.cos(phi) * Math.cos(delta) * Math.cos(h)
            );

            const azimuth = Math.atan2(
                Math.sin(h),
                Math.cos(h) * Math.sin(phi) - Math.tan(delta) * Math.cos(phi)
            ) + Math.PI;

            return { altitude, azimuth };
        }

        function calculateSunriseSunset(date, latitude) {
            // Simplified calculation
            const declination = 23.45 * Math.sin(2 * Math.PI * (date - 81) / 365);
            const phi = latitude * Math.PI / 180;
            const delta = declination * Math.PI / 180;

            const cosH = -Math.tan(phi) * Math.tan(delta);
            if (cosH >= 1) return { sunrise: null, sunset: null }; // Polar night
            if (cosH <= -1) return { sunrise: null, sunset: null }; // Midnight sun

            const H = Math.acos(cosH) * 180 / Math.PI;
            const sunrise = 12 - H / 15;
            const sunset = 12 + H / 15;

            return { sunrise, sunset };
        }

        function updateSunPath(date, latitude) {
            const points = [];
            const { sunrise, sunset } = calculateSunriseSunset(date, latitude);

            if (sunrise === null || sunset === null) {
                sunPathLine.visible = false;
                return;
            }

            sunPathLine.visible = true;

            for (let t = sunrise; t <= sunset; t += 0.5) {
                const { altitude, azimuth } = calculateSunPosition(date, latitude, t);

                // Convert to Cartesian coordinates for the celestial dome
                const r = 15;
                const x = r * Math.cos(altitude) * Math.sin(azimuth);
                const y = r * Math.cos(altitude) * Math.cos(azimuth);
                const z = r * Math.sin(altitude);

                points.push(new THREE.Vector3(x, y, z));
            }

            sunPathLine.geometry.dispose();
            sunPathLine.geometry = new THREE.BufferGeometry().setFromPoints(points);
        }

        function updateLatitudeParallel(latitude) {
            const points = [];
            const segments = 64;
            const r = 5;
            const latRad = latitude * Math.PI / 180;

            for (let i = 0; i <= segments; i++) {
                const theta = 2 * Math.PI * i / segments;
                const x = r * Math.cos(theta) * Math.cos(latRad);
                const y = r * Math.sin(latRad);
                const z = r * Math.sin(theta) * Math.cos(latRad);
                points.push(new THREE.Vector3(x, y, z));
            }

            latitudeParallel.geometry.dispose();
            latitudeParallel.geometry = new THREE.BufferGeometry().setFromPoints(points);

            // Update day/night segment
            const { sunrise, sunset } = calculateSunriseSunset(
                parseInt(document.getElementById('slider-date').value),
                latitude
            );

            if (sunrise === null || sunset === null) {
                dayNightSegment.visible = false;
                return;
            }

            dayNightSegment.visible = true;
            const segmentPoints = [];
            const startAngle = (sunrise - 12) * 15 * Math.PI / 180;
            const endAngle = (sunset - 12) * 15 * Math.PI / 180;
            const steps = 32;

            for (let i = 0; i <= steps; i++) {
                const angle = startAngle + (endAngle - startAngle) * i / steps;
                const x = r * Math.cos(angle) * Math.cos(latRad);
                const y = r * Math.sin(latRad);
                const z = r * Math.sin(angle) * Math.cos(latRad);
                segmentPoints.push(new THREE.Vector3(x, y, z));
            }

            dayNightSegment.geometry.dispose();
            dayNightSegment.geometry = new THREE.BufferGeometry().setFromPoints(segmentPoints);
        }

        function updateReadouts(date, latitude) {
            const { sunrise, sunset } = calculateSunriseSunset(date, latitude);

            if (sunrise === null || sunset === null) {
                document.getElementById('readout-day-length').textContent = "24h (polar day)";
                document.getElementById('readout-night-length').textContent = "0h";
                document.getElementById('readout-sunrise-time').textContent = "N/A";
                document.getElementById('readout-sunrise-direction').textContent = "N/A";
                document.getElementById('readout-sunset-time').textContent = "N/A";
                document.getElementById('readout-sunset-direction').textContent = "N/A";
                return;
            }

            const dayLength = sunset - sunrise;
            const nightLength = 24 - dayLength;

            document.getElementById('readout-day-length').textContent = `${dayLength.toFixed(1)}h`;
            document.getElementById('readout-night-length').textContent = `${nightLength.toFixed(1)}h`;
            document.getElementById('readout-sunrise-time').textContent = formatTime(sunrise);
            document.getElementById('readout-sunset-time').textContent = formatTime(sunset);

            // Simplified direction calculation
            const sunriseDir = latitude >= 0 ? "NE" : "SE";
            const sunsetDir = latitude >= 0 ? "NW" : "SW";
            document.getElementById('readout-sunrise-direction').textContent = sunriseDir;
            document.getElementById('readout-sunset-direction').textContent = sunsetDir;
        }

        function updateSunPosition(date, latitude, time) {
            const { altitude, azimuth } = calculateSunPosition(date, latitude, time);

            // For ground view
            const r = 15;
            const x = r * Math.cos(altitude) * Math.sin(azimuth);
            const y = r * Math.cos(altitude) * Math.cos(azimuth);
            const z = r * Math.sin(altitude);
            sun.position.set(x, y, z);

            // For space view - update Earth's rotation
            if (!document.getElementById('check-stationary-earth').checked) {
                const earthRotation = (time - 12) * Math.PI / 12;
                earth.rotation.y = earthRotation;
            }

            // Update terminator position
            terminator.position.copy(sun.position);
            terminator.lookAt(0, 0, 0);
        }

        function updateView() {
            const view = document.getElementById('select-view').value;

            if (view === 'ground') {
                currentCamera = groundCamera;
                document.getElementById('options-ground').style.display = 'block';
                document.getElementById('options-space').style.display = 'none';

                ground.visible = true;
                celestialDome.visible = true;
                sunPathLine.visible = document.getElementById('check-sun-path').checked;
                constellations.visible = document.getElementById('check-constellations-day').checked;

                earth.visible = false;
                terminator.visible = false;
                latitudeParallel.visible = false;
                dayNightSegment.visible = false;
            } else {
                currentCamera = spaceCamera;
                document.getElementById('options-ground').style.display = 'none';
                document.getElementById('options-space').style.display = 'block';

                ground.visible = false;
                celestialDome.visible = false;
                sunPathLine.visible = false;

                earth.visible = true;
                terminator.visible = document.getElementById('check-day-night-division').checked;
                latitudeParallel.visible = true;
                dayNightSegment.visible = document.getElementById('check-day-night-parallel').checked;
                constellations.visible = document.getElementById('check-constellations').checked;
            }

            controls.object = currentCamera;
            needsRender = true;
        }

        // Event listeners
        document.getElementById('slider-date').addEventListener('input', function() {
            const date = parseInt(this.value);
            const { month, day } = getMonthAndDay(date);
            document.getElementById('label-date').textContent = `${month} ${day}`;

            updateSunPath(date, parseInt(document.getElementById('slider-latitude').value));
            updateReadouts(date, parseInt(document.getElementById('slider-latitude').value));
            updateSunPosition(
                date,
                parseInt(document.getElementById('slider-latitude').value),
                parseFloat(document.getElementById('slider-time').value)
            );
            needsRender = true;
        });

        document.getElementById('slider-latitude').addEventListener('input', function() {
            const latitude = parseInt(this.value);
            document.getElementById('label-latitude').textContent = `${latitude}°`;

            updateSunPath(parseInt(document.getElementById('slider-date').value), latitude);
            updateLatitudeParallel(latitude);
            updateReadouts(parseInt(document.getElementById('slider-date').value), latitude);
            updateSunPosition(
                parseInt(document.getElementById('slider-date').value),
                latitude,
                parseFloat(document.getElementById('slider-time').value)
            );
            needsRender = true;
        });

        document.getElementById('slider-time').addEventListener('input', function() {
            const time = parseFloat(this.value);
            document.getElementById('label-time').textContent = formatTime(time);

            updateSunPosition(
                parseInt(document.getElementById('slider-date').value),
                parseInt(document.getElementById('slider-latitude').value),
                time
            );
            needsRender = true;
        });

        document.getElementById('select-view').addEventListener('change', function() {
            updateView();
        });

        // Checkbox event listeners
        document.getElementById('check-sun-path').addEventListener('change', function() {
            sunPathLine.visible = this.checked;
            needsRender = true;
        });

        document.getElementById('check-constellations-day').addEventListener('change', function() {
            constellations.visible = this.checked;
            needsRender = true;
        });

        document.getElementById('check-stationary-earth').addEventListener('change', function() {
            needsRender = true;
        });

        document.getElementById('check-constellations').addEventListener('change', function() {
            constellations.visible = this.checked;
            needsRender = true;
        });

        document.getElementById('check-day-night-division').addEventListener('change', function() {
            terminator.visible = this.checked;
            needsRender = true;
        });

        document.getElementById('check-day-night-parallel').addEventListener('change', function() {
            dayNightSegment.visible = this.checked;
            needsRender = true;
        });

        // Handle window resize
        window.addEventListener('resize', function() {
            const width = canvas.clientWidth;
            const height = canvas.clientHeight;

            groundCamera.aspect = width / height;
            groundCamera.updateProjectionMatrix();

            spaceCamera.aspect = width / height;
            spaceCamera.updateProjectionMatrix();

            renderer.setSize(width, height);
            needsRender = true;
        });

        // Initial setup
        updateSunPath(79, 45);
        updateLatitudeParallel(45);
        updateReadouts(79, 45);
        updateSunPosition(79, 45, 12);
        updateView();

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);

            controls.update();

            if (needsRender) {
                renderer.render(scene, currentCamera);
                needsRender = false;
            }
        }

        animate();
    </script>
</body>
</html>
