                p.textAlign(p.LEFT, p.TOP);
                p.text("Z", centerX, 20);

                p.pop();
            }

            function drawVectorField() {
                // Calculate the field at grid points to find min/max magnitude
                let gridSize = 25;
                let minMag = Infinity;
                let maxMag = -Infinity;
                let fieldValues = [];

                for (let i = 0; i < gridSize; i++) {
                    fieldValues[i] = [];
                    for (let j = 0; j < gridSize; j++) {
                        let y = p.map(i, 0, gridSize-1, -0.5, 0.5);
                        let z = p.map(j, 0, gridSize-1, -0.5, 0.5);

                        let B = calculateTotalField(y, z);
                        let mag = Math.sqrt(B.y*B.y + B.z*B.z);

                        fieldValues[i][j] = {B, mag};
                        minMag = Math.min(minMag, mag);
                        maxMag = Math.max(maxMag, mag);
                    }
                }

                // Draw streamlines
                p.push();
                p.strokeWeight(1);

                for (let i = 0; i < gridSize; i += 2) {
                    for (let j = 0; j < gridSize; j += 2) {
                        let y = p.map(i, 0, gridSize-1, -0.5, 0.5);
                        let z = p.map(j, 0, gridSize-1, -0.5, 0.5);

