                let z = zStart;

                // Forward direction
                for (let i = 0; i < steps; i++) {
                    if (Math.abs(y) > 0.55 || Math.abs(z) > 0.55) break;

                    let B = calculateTotalField(y, z);
                    let mag = Math.sqrt(B.y*B.y + B.z*B.z);

                    // Normalize direction
                    let dy = B.y / mag;
                    let dz = B.z / mag;

                    // Calculate color based on magnitude
                    let hue = p.map(mag, minMag, maxMag, 60, 270);
                    p.stroke(hue, 100, 100);

                    // Draw line segment
                    let nextY = y + dy * stepSize;
                    let nextZ = z + dz * stepSize;

                    let y1 = p.map(y, -0.5, 0.5, 0, p.width);
                    let z1 = p.map(z, -0.5, 0.5, p.height, 0);
                    let y2 = p.map(nextY, -0.5, 0.5, 0, p.width);
                    let z2 = p.map(nextZ, -0.5, 0.5, p.height, 0);

                    p.line(y1, z1, y2, z2);

                    // Draw arrow occasionally
                    if (i % arrowSteps === 0) {
                        drawArrow(y1, z1, y2, z2);
