                const zp = z - z_offset;

                if (y === 0) {
                    // Special case when y=0 to avoid division by zero
                    return { y: 0, z: 0 };
                }

                const m = (4 * radius * y) / ((radius + y)**2 + zp**2);
                const common_factor_1 = (μ0 * current * turns) / (2 * Math.PI);
                const common_factor_2 = 1 / Math.sqrt((radius + y)**2 + zp**2);

                const K = ellipticK(m);
                const E = ellipticE(m);

                const denominator = (radius - y)**2 + zp**2;

                const B_y = common_factor_1 * (zp / y) * common_factor_2 * 
                           (((radius**2 + y**2 + zp**2) / denominator) * E - K);

                const B_z = common_factor_1 * common_factor_2 * 
                           (K - ((radius**2 - y**2 - zp**2) / denominator) * E);

                return { y: B_y, z: B_z };
            }

            // Complete elliptic integral of the first kind (using AGM method)
            function ellipticK(m) {
                const a = 1;
                const b = Math.sqrt(1 - m);
                let s = 1;
                let sum = 0;

                for (let i = 0; i < 10; i++) {
                    const aNext = (a + b) / 2;
                    const bNext = Math.sqrt(a * b);
                    sum += s * (a * a - b * b);
                    s *= 2;

                    if (Math.abs(a - b) < 1e-10) break;

                    a = aNext;
                    b = bNext;
                }

                return Math.PI / (2 * a);
            }

            // Complete elliptic integral of the second kind (using AGM method)
            function ellipticE(m) {
                const a = 1;
                const b = Math.sqrt(1 - m);
                let s = 1;
                let sum = 1 - m/2;

                for (let i = 0; i < 10; i++) {
                    const aNext = (a + b) / 2;
                    const bNext = Math.sqrt(a * b);
                    sum -= s * (aNext - bNext)**2 / 2;
                    s *= 2;

                    if (Math.abs(a - b) < 1e-10) break;

                    a = aNext;
                    b = bNext;
                }

                return Math.PI * sum / (2 * a);
            }
        };

        // Initialize the p5 sketch
        new p5(sketch);

        // DOM event listeners
        document.getElementById('slider-current').addEventListener('input', function() {
            current = parseFloat(this.value);
            document.getElementById('value-current').textContent = current.toFixed(3);
            redraw();
        });

        document.getElementById('slider-earth-field').addEventListener('input', function() {
            earthField = parseFloat(this.value);
            document.getElementById('value-earth-field').textContent = earthField.toExponential(2);
            redraw();
        });

        document.getElementById('slider-radius').addEventListener('input', function() {
            radius = parseFloat(this.value);
            document.getElementById('value-radius').textContent = radius.toFixed(2);
            redraw();
        });

        document.getElementById('slider-turns').addEventListener('input', function() {
            turns = parseInt(this.value);
            document.getElementById('value-turns').textContent = turns;
            redraw();
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            // Reset sliders to initial values
            document.getElementById('slider-current').value = 0.431;
            document.getElementById('slider-earth-field').value = 0.0000194;
            document.getElementById('slider-radius').value = 0.5;
            document.getElementById('slider-turns').value = 25;

            // Update displayed values
            current = 0.431;
            earthField = 0.0000194;
            radius = 0.5;
            turns = 25;

            document.getElementById('value-current').textContent = '0.431';
            document.getElementById('value-earth-field').textContent = '1.94e-5';
            document.getElementById('value-radius').textContent = '0.50';
            document.getElementById('value-turns').textContent = '25';

            redraw();
        });

        function redraw() {
            if (sketch && sketch.redraw) {
                sketch.redraw();
            }
        }
    </script>
</body>
</html>
