            });

            document.getElementById('image-size-minus-btn').addEventListener('click', () => {
                const slider = document.getElementById('slider-image-size');
                slider.value = Math.max(parseInt(slider.min), parseInt(slider.value) - 1);
                slider.dispatchEvent(new Event('input'));
            });

            document.getElementById('image-size-plus-btn').addEventListener('click', () => {
                const slider = document.getElementById('slider-image-size');
